// src/pages/BrowseEvents.jsx
import React, { useEffect, useMemo, useState } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";

/* === Ïπ¥ÌÖåÍ≥†Î¶¨ Ï†ïÏùò === */
const CATEGORIES = ["Ï†ÑÏ≤¥", "Í≥µÏó∞", "Ï†ÑÏãú", "ÍµêÏú°/Ï≤¥Ìóò", "Í∏∞ÌÉÄ"];

/* === ÏÉÅÏúÑ Ïπ¥ÌÖåÍ≥†Î¶¨ Îß§Ìïë === */
function toHighLevelCategory(codename = "", themecode = "") {
  const c = String(codename);
  if (["ÏΩòÏÑúÌä∏", "ÌÅ¥ÎûòÏãù", "Íµ≠ÏïÖ", "Î¨¥Ïö©", "Ïó∞Í∑π", "ÎÆ§ÏßÄÏª¨/Ïò§ÌéòÎùº", "Ï∂ïÏ†ú-Í∏∞ÌÉÄ"].some(k => c.includes(k))) {
    return "Í≥µÏó∞";
  }
  if (c.includes("Ï†ÑÏãú/ÎØ∏Ïà†")) return "Ï†ÑÏãú";
  if (c.includes("ÍµêÏú°/Ï≤¥Ìóò") || String(themecode).includes("ÍµêÏú°")) return "ÍµêÏú°/Ï≤¥Ìóò";
  return "Í∏∞ÌÉÄ";
}

/* === ÎÇ†Ïßú ÌëúÍ∏∞ Ïú†Ìã∏ === */
function normalizeDateRange(startStr = "", endStr = "") {
  const s = (startStr || "").replaceAll(".", "-");
  const e = (endStr || "").replaceAll(".", "-");
  if (!s && !e) return "ÏùºÏ†ï ÎØ∏Ï†ï";
  if (s && e) return `${s} ~ ${e}`;
  return s || e;
}

/* === XML ÌååÏÑú === */
function parseSeoulXML(text) {
  const doc = new window.DOMParser().parseFromString(text, "text/xml");
  const err = doc.getElementsByTagName("parsererror")[0];
  if (err) throw new Error("XML parse error");

  const root = doc.getElementsByTagName("culturalEventInfo")[0];
  if (!root) return [];

  const rows = root.getElementsByTagName("row");
  const out = [];

  for (let i = 0; i < rows.length; i++) {
    const r = rows[i];

    const get = (tag) => {
      const el = r.getElementsByTagName(tag)[0];
      return el ? el.textContent?.trim() : "";
    };

    const id = get("SVCID") || `evt_${i}`;
    const title = get("TITLE") || get("SVCNM") || "Î¨¥Ï†ú";
    const codename = get("CODENAME");
    const themecode = get("THEMECODE");
    const gu = get("GUNAME");
    const place = get("PLACE");
    const start = get("STRTDATE") || get("DATE");
    const end = get("END_DATE") || get("ENDDATE") || get("END");
    const homepage = get("ORG_LINK") || get("HMPG_ADDR");
    const img = get("MAIN_IMG");
    const fee = get("USE_FEE");
    const category = toHighLevelCategory(codename, themecode);

    out.push({
      id,
      title,
      category,
      date: normalizeDateRange(start, end),
      place: place || gu || "Ïû•ÏÜå ÎØ∏Ï†ï",
      thumb: img || "/images/sample-event.jpg",
      homepage,
      gu,
      fee,
    });
  }
  return out;
}

/* === Îç∞Ïù¥ÌÑ∞ Î°úÎî© ÌõÖ: XML Î≤ÑÏ†Ñ === */
const SEOUL_XML_URL =
  "http://openapi.seoul.go.kr:8088/746754794179656f37364f4c4f4e69/xml/culturalEventInfo/1/50/";

function useSeoulEventsXML() {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState(null);

  useEffect(() => {
    let abort = false;
    (async () => {
      setLoading(true);
      setErr(null);
      try {
        const res = await fetch(SEOUL_XML_URL);
        if (!res.ok) throw new Error(`HTTP ${res.status}`);
        const text = await res.text();
        const items = parseSeoulXML(text);
        if (!abort) setEvents(items);
      } catch (e) {
        if (!abort) setErr(e);
      } finally {
        if (!abort) setLoading(false);
      }
    })();
    return () => { abort = true; };
  }, []);

  return { events, loading, error: err };
}

/* === Ï¶êÍ≤®Ï∞æÍ∏∞ Î°úÏª¨Ïä§ÌÜ†Î¶¨ÏßÄ Ïú†Ìã∏ (Favorites.jsxÏôÄ ÎèôÏùº ÌÇ§ ÏÇ¨Ïö©) === */
const LS_KEY = "sn_favorites";
function loadFavs() {
  try {
    const raw = localStorage.getItem(LS_KEY);
    return raw ? JSON.parse(raw) : [];
  } catch {
    return [];
  }
}
function saveFavs(list) {
  localStorage.setItem(LS_KEY, JSON.stringify(list));
}
function addFavIfNeeded(item) {
  const list = loadFavs();
  if (!list.some((x) => x.id === item.id)) {
    list.push(item);
    saveFavs(list);
  }
}

export default function BrowseEvents() {
  const navigate = useNavigate();
  const [sp, setSp] = useSearchParams();

  // URL ÏøºÎ¶¨ ÎèôÍ∏∞Ìôî
  const initialCategory = decodeURIComponent(sp.get("category") || "Ï†ÑÏ≤¥");
  const initialQuery = sp.get("q") || "";

  const [category, setCategory] = useState(
    CATEGORIES.includes(initialCategory) ? initialCategory : "Ï†ÑÏ≤¥"
  );
  const [input, setInput] = useState(initialQuery);
  const [query, setQuery] = useState(initialQuery);

  // Ïã§Ï†ú Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞ (XML)
  const { events, loading, error } = useSeoulEventsXML();

  useEffect(() => {
    const next = new URLSearchParams(sp);
    if (category && category !== "Ï†ÑÏ≤¥") next.set("category", encodeURIComponent(category));
    else next.delete("category");
    if (query) next.set("q", query);
    else next.delete("q");
    setSp(next, { replace: true });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [category, query]);

  const results = useMemo(() => {
    const pool =
      category === "Ï†ÑÏ≤¥" ? events : events.filter((e) => e.category === category);
    if (!query.trim()) return pool;
    const q = query.trim().toLowerCase();
    return pool.filter(
      (e) =>
        e.title.toLowerCase().includes(q) ||
        (e.place || "").toLowerCase().includes(q)
