// src/pages/BrowseEvents.jsx
import React, { useEffect, useMemo, useState } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import useEvents from "../hooks/useEvents";

// ---- Î∂ÑÎ•ò Îß§Ìïë (Í≥µÏó∞/Ï†ÑÏãú/ÍµêÏú°/Í∏∞ÌÉÄ) ----
function toHighLevelCategory(codename = "", themecode = "") {
  const c = String(codename);
  if (["ÏΩòÏÑúÌä∏", "ÌÅ¥ÎûòÏãù", "Íµ≠ÏïÖ", "Î¨¥Ïö©", "Ïó∞Í∑π", "ÎÆ§ÏßÄÏª¨/Ïò§ÌéòÎùº", "Ï∂ïÏ†ú-Í∏∞ÌÉÄ"].some(k => c.includes(k))) {
    return "Í≥µÏó∞";
  }
  if (c.includes("Ï†ÑÏãú/ÎØ∏Ïà†")) return "Ï†ÑÏãú";
  if (c.includes("ÍµêÏú°/Ï≤¥Ìóò") || String(themecode).includes("ÍµêÏú°")) return "ÍµêÏú°/Ï≤¥Ìóò";
  return "Í∏∞ÌÉÄ";
}

// ---- ÎÇ†Ïßú Ìó¨Ìçº (Î°úÏª¨ ÌÉÄÏûÑÏ°¥ Í∏∞Ï§Ä) ----
function startOfDay(d = new Date()) {
  const x = new Date(d);
  x.setHours(0, 0, 0, 0);
  return x;
}
function endOfDay(d = new Date()) {
  const x = new Date(d);
  x.setHours(23, 59, 59, 999);
  return x;
}
function startOfWeekKR(d = new Date()) {
  // ÏõîÏöîÏùº ÏãúÏûë
  const x = startOfDay(d);
  const day = x.getDay(); // 0=Ïùº,1=Ïõî...
  const diff = (day === 0 ? -6 : 1 - day); // ÏõîÍπåÏßÄ ÎêòÎèåÎ¶º
  x.setDate(x.getDate() + diff);
  return x;
}
function endOfWeekKR(d = new Date()) {
  const s = startOfWeekKR(d);
  const e = new Date(s);
  e.setDate(e.getDate() + 6);
  e.setHours(23, 59, 59, 999);
  return e;
}
function startOfMonth(d = new Date()) {
  const x = startOfDay(d);
  x.setDate(1);
  return x;
}
function endOfMonth(d = new Date()) {
  const x = startOfDay(d);
  x.setMonth(x.getMonth() + 1, 0); // Ïù¥Î≤à Îã¨ ÎßàÏßÄÎßâ ÎÇ†
  x.setHours(23, 59, 59, 999);
  return x;
}

const CATEGORIES = ["Ï†ÑÏ≤¥", "Í≥µÏó∞", "Ï†ÑÏãú", "ÍµêÏú°/Ï≤¥Ìóò", "Í∏∞ÌÉÄ"];
const DATE_RANGES = ["Ï†ÑÏ≤¥", "Ïò§Îäò", "Ïù¥Î≤à Ï£º", "Ïù¥Î≤à Îã¨"];

export default function BrowseEvents() {
  const navigate = useNavigate();
  const [sp, setSp] = useSearchParams();

  // URL ÏøºÎ¶¨ Ï¥àÍ∏∞Í∞í
  const initialCategory = decodeURIComponent(sp.get("category") || "Ï†ÑÏ≤¥");
  const initialQuery = sp.get("q") || "";
  const initialArea = decodeURIComponent(sp.get("area") || "Ï†ÑÏ≤¥");
  const initialDate = decodeURIComponent(sp.get("date") || "Ï†ÑÏ≤¥");

  const [category, setCategory] = useState(CATEGORIES.includes(initialCategory) ? initialCategory : "Ï†ÑÏ≤¥");
  const [area, setArea] = useState(initialArea || "Ï†ÑÏ≤¥");
  const [dateRange, setDateRange] = useState(DATE_RANGES.includes(initialDate) ? initialDate : "Ï†ÑÏ≤¥");
  const [input, setInput] = useState(initialQuery);
  const [query, setQuery] = useState(initialQuery);

  // URL ÎèôÍ∏∞Ìôî
  useEffect(() => {
    const next = new URLSearchParams(sp);
    const setOrDel = (key, val, all = "Ï†ÑÏ≤¥") => {
      if (val && val !== all) next.set(key, encodeURIComponent(val));
      else next.delete(key);
    };
    setOrDel("category", category);
    query ? next.set("q", query) : next.delete("q");
    setOrDel("area", area);
    setOrDel("date", dateRange);
    setSp(next, { replace: true });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [category, query, area, dateRange]);

