  const raw = useEvents();

  // ‚ë° ÎèÑÎ©îÏù∏ Î™®Îç∏Î°ú ÎßµÌïë
  const ALL_EVENTS = useMemo(() => {
    return (raw || []).map((e, idx) => {
      const startMs = Number(e.strtdate) || (e.date ? Date.parse(String(e.date).split("~")[0].trim()) : NaN);
      const endMs =
        Number(e.end_date) ||
        (e.date && String(e.date).includes("~")
          ? Date.parse(String(e.date).split("~")[1].trim())
          : startMs);

      return {
        id: `${e.title || "ev"}-${e.strtdate || e.date || idx}`,
        title: e.title,
        category: toHighLevelCategory(e.codename, e.themecode),
        date: e.date,                         // "YYYY-MM-DD~YYYY-MM-DD"
        place: e.place,
        area: e.guname || "Í∏∞ÌÉÄ",
        thumb: e.main_img || "/images/no-image.png",
        link: e.hmpg_addr || e.org_link || "",
        startMs,
        endMs,
      };
    });
  }, [raw]);

  // ‚ë¢ ÏûêÏπòÍµ¨ Î™©Î°ù (Îç∞Ïù¥ÌÑ∞Î°úÎ∂ÄÌÑ∞ ÎèôÏ†Å ÏÉùÏÑ±)
  const AREA_OPTIONS = useMemo(() => {
    const s = new Set(["Ï†ÑÏ≤¥"]);
    ALL_EVENTS.forEach((e) => e.area && s.add(e.area));
    return Array.from(s);
  }, [ALL_EVENTS]);

  // ‚ë£ ÎÇ†Ïßú ÌïÑÌÑ∞ Ìï®Ïàò
  const inDateRange = (ev, range) => {
    if (!range || range === "Ï†ÑÏ≤¥") return true;
    const now = new Date();
    let R1, R2;
    if (range === "Ïò§Îäò") {
      R1 = startOfDay(now); R2 = endOfDay(now);
    } else if (range === "Ïù¥Î≤à Ï£º") {
      R1 = startOfWeekKR(now); R2 = endOfWeekKR(now);
    } else if (range === "Ïù¥Î≤à Îã¨") {
      R1 = startOfMonth(now); R2 = endOfMonth(now);
    } else {
      return true;
    }
    const a = ev.startMs, b = ev.endMs;
    if (Number.isNaN(a) && Number.isNaN(b)) return true; // ÎÇ†Ïßú ÏóÜÏúºÎ©¥ ÌÜµÍ≥º
    const s = Number.isNaN(a) ? b : a;
    const e = Number.isNaN(b) ? a : b;
    const rangeStart = R1.getTime();
    const rangeEnd = R2.getTime();
    // Í∏∞Í∞Ñ Í≤πÏπ® Ïó¨Î∂Ä
    return !(e < rangeStart || s > rangeEnd);
  };

  // ‚ë§ ÌÜµÌï© ÌïÑÌÑ∞/Í≤ÄÏÉâ
  const results = useMemo(() => {
    let list = ALL_EVENTS;

    // Ïπ¥ÌÖåÍ≥†Î¶¨
    if (category !== "Ï†ÑÏ≤¥") list = list.filter((e) => e.category === category);

    // ÏûêÏπòÍµ¨
    if (area !== "Ï†ÑÏ≤¥") list = list.filter((e) => e.area === area);

    // ÎÇ†Ïßú
    list = list.filter((e) => inDateRange(e, dateRange));

    // Í≤ÄÏÉâ
    if (query.trim()) {
      const q = query.trim().toLowerCase();
      list = list.filter(
        (e) =>
          (e.title && e.title.toLowerCase().includes(q)) ||
          (e.place && e.place.toLowerCase().includes(q)) ||
          (e.area && e.area.toLowerCase().includes(q))
      );
    }
    return list;
  }, [ALL_EVENTS, category, area, dateRange, query]);

  const onSubmit = (e) => {
    e.preventDefault();
    setQuery(input);
  };

  return (
    <div className="min-h-screen bg-white">
      <div className="max-w-6xl mx-auto px-4 md:px-6">
        {/* Ïª®Ìä∏Î°§ Î∞î: Ïπ¥ÌÖåÍ≥†Î¶¨ + ÏûêÏπòÍµ¨ + ÎÇ†Ïßú + Í≤ÄÏÉâ */}
        <div className="flex flex-col md:flex-row md:items-center gap-3 md:gap-4">
          {/* Ïπ¥ÌÖåÍ≥†Î¶¨ */}
          <div className="w-full md:w-48">
            <label htmlFor="category" className="sr-only">Ïπ¥ÌÖåÍ≥†Î¶¨</label>
            <select
              id="category"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              className="w-full border rounded-md px-3 py-2 bg-white"
            >
              {CATEGORIES.map((c) => (
                <option key={c} value={c}>{c}</option>
              ))}
            </select>
          </div>

          {/* ÏûêÏπòÍµ¨ */}
          <div className="w-full md:w-48">
            <label htmlFor="area" className="sr-only">ÏûêÏπòÍµ¨</label>
            <select
              id="area"
              value={area}
              onChange={(e) => setArea(e.target.value)}
              className="w-full border rounded-md px-3 py-2 bg-white"
            >
              {AREA_OPTIONS.map((g) => (
                <option key={g} value={g}>{g}</option>
              ))}
            </select>
          </div>

          {/* ÎÇ†Ïßú Î≤îÏúÑ */}
          <div className="w-full md:w-48">
            <label htmlFor="dateRange" className="sr-only">ÎÇ†Ïßú</label>
            <select
              id="dateRange"
              value={dateRange}
              onChange={(e) => setDateRange(e.target.value)}
              className="w-full border rounded-md px-3 py-2 bg-white"
            >
              {DATE_RANGES.map((d) => (
                <option key={d} value={d}>{d}</option>
              ))}
            </select>
          </div>

          {/* Í≤ÄÏÉâ */}
          <form onSubmit={onSubmit} className="flex-1">
            <div className="relative">
              <input
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="ÌñâÏÇ¨Î™Ö, Ïû•ÏÜå, ÏûêÏπòÍµ¨ Í≤ÄÏÉâ"
                className="w-full border rounded-md pl-3 pr-10 py-2"
              />
              <button
                type="submit"
                aria-label="Í≤ÄÏÉâ"
                className="absolute right-2 top-1/2 -translate-y-1/2 p-1 rounded hover:bg-gray-50"
              >
                <img src="/images/search.png" alt="" className="w-4 h-4" />
              </button>
            </div>
          </form>
        </div>

        {/* Î≥∏Î¨∏ */}
        <div className="mt-6 grid grid-cols-1 lg:grid-cols-12 gap-6">
          {/* Í≤∞Í≥º Î¶¨Ïä§Ìä∏ */}
          <div className="lg:col-span-8">
            <div className="border rounded-lg p-4 min-h-[360px]">
              {results.length === 0 ? (
                <div className="h-56 grid place-items-center text-gray-500">
                  Ï°∞Í±¥Ïóê ÎßûÎäî ÌñâÏÇ¨Í∞Ä ÏóÜÏäµÎãàÎã§.
                </div>
              ) : (
                <div className="grid sm:grid-cols-2 gap-4">
                  {results.map((ev) => (
                    <button
                      key={ev.id}
                      onClick={() => ev.link ? window.open(ev.link, "_blank", "noopener,noreferrer") : null}
                      className="text-left bg-white border rounded-lg overflow-hidden hover:shadow transition"
                    >
                      <img
                        src={ev.thumb}
                        alt={ev.title}
                        className="w-full h-36 object-cover"
                        onError={(e) => (e.currentTarget.src = "/images/no-image.png")}
                      />
                      <div className="p-3">
                        <div className="flex items-center justify-between">
                          <h3 className="font-semibold line-clamp-2">{ev.title}</h3>
                          <span className="text-xs px-2 py-0.5 rounded-full bg-gray-100">
                            {ev.category}
                          </span>
                        </div>
                        <p className="text-sm text-gray-600 mt-1">üìÖ {ev.date}</p>
                        <p className="text-sm text-gray-600">üìç {ev.place} ¬∑ {ev.area}</p>
                      </div>
                    </button>
                  ))}
                </div>
              )}
            </div>
          </div>

          {/* ÏÇ¨Ïù¥Îìú Ï∞∏Í≥† Ïù¥ÎØ∏ÏßÄ (ÏÑ†ÌÉù) */}
          <aside className="lg:col-span-4">
            <div className="border rounded-lg overflow-hidden">
              <img
                src="/images/wireframes/browse_guide.png"
                alt="Browse ÌéòÏù¥ÏßÄ Í∏∞Îä• ÏÑ§Î™Ö Ïù¥ÎØ∏ÏßÄ"
                className="w-full h-auto"
              />
            </div>
            <p className="mt-2 text-xs text-gray-500">* ÌïÑÏöî ÏóÜÏúºÎ©¥ Ï†úÍ±∞Ìï¥ÎèÑ Îê©ÎãàÎã§.</p>
          </aside>
        </div>
      </div>
    </div>
  );
}
