import { useEffect, useState } from "react";

export default function useEvents() {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    fetch("/data/events.json")
      .then((res) => res.json())
      .then((data) => {
        // 데이터 구조 확인 후 배열만 저장
        setEvents(data.DATA);
      });
  }, []);

  return events;
}
