// src/pages/BrowseEvents.jsx
import React, { useEffect, useMemo, useState } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import useEvents from "../hooks/useEvents";

// ÏÉÅÏúÑ Ïπ¥ÌÖåÍ≥†Î¶¨(Í≥µÏó∞/Ï†ÑÏãú/ÍµêÏú°/Í∏∞ÌÉÄ)Î°ú Îß§Ìïë
function toHighLevelCategory(codename = "", themecode = "") {
  const c = String(codename || "");
  if (["ÏΩòÏÑúÌä∏", "ÌÅ¥ÎûòÏãù", "Íµ≠ÏïÖ", "Î¨¥Ïö©", "Ïó∞Í∑π", "ÎÆ§ÏßÄÏª¨/Ïò§ÌéòÎùº", "Ï∂ïÏ†ú-Í∏∞ÌÉÄ"].some(k => c.includes(k))) {
    return "Í≥µÏó∞";
  }
  if (c.includes("Ï†ÑÏãú/ÎØ∏Ïà†")) return "Ï†ÑÏãú";
  if (c.includes("ÍµêÏú°/Ï≤¥Ìóò") || String(themecode || "").includes("ÍµêÏú°")) return "ÍµêÏú°/Ï≤¥Ìóò";
  return "Í∏∞ÌÉÄ";
}

// URL Ï§ëÍ∞ÑÏóê Í≥µÎ∞± ÏÑûÏó¨ Îì§Ïñ¥Ïò® Í≤ΩÏö∞ Î≥¥Ï†ï
const cleanUrl = (u) => (u ? String(u).replace(/\s+/g, "") : "");

const CATEGORIES = ["Ï†ÑÏ≤¥", "Í≥µÏó∞", "Ï†ÑÏãú", "ÍµêÏú°/Ï≤¥Ìóò", "Í∏∞ÌÉÄ"];

export default function BrowseEvents() {
  const navigate = useNavigate();
  const [sp, setSp] = useSearchParams();

  // URL ÏøºÎ¶¨ Ï¥àÍ∏∞Í∞í
  const initialCategory = decodeURIComponent(sp.get("category") || "Ï†ÑÏ≤¥");
  const initialArea = decodeURIComponent(sp.get("area") || "Ï†ÑÏ≤¥");
  const initialQuery = sp.get("q") || "";

  const [category, setCategory] = useState(
    CATEGORIES.includes(initialCategory) ? initialCategory : "Ï†ÑÏ≤¥"
  );
  const [area, setArea] = useState(initialArea || "Ï†ÑÏ≤¥");
  const [input, setInput] = useState(initialQuery);
  const [query, setQuery] = useState(initialQuery);

  // URL ÎèôÍ∏∞Ìôî (Ïπ¥ÌÖåÍ≥†Î¶¨/ÏûêÏπòÍµ¨/Í≤ÄÏÉâ)
  useEffect(() => {
    const next = new URLSearchParams(sp);
    const setOrDel = (key, val, all = "Ï†ÑÏ≤¥") => {
      if (val && val !== all) next.set(key, encodeURIComponent(val));
      else next.delete(key);
    };
    setOrDel("category", category);
    setOrDel("area", area);
    query ? next.set("q", query) : next.delete("q");
    setSp(next, { replace: true });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [category, area, query]);

  // ‚ë† Í≥µÍ≥µÎç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞
  const raw = useEvents();

  // ‚ë° ÌôîÎ©¥ÏóêÏÑú Ïì∞Í∏∞ Ï¢ãÍ≤å Î≥ÄÌôò (ÎåÄ/ÏÜåÎ¨∏Ïûê ÌÇ§ ÌòºÏö© ÎåÄÏùë)
  const ALL_EVENTS = useMemo(() => {
    return (raw || []).map((e, idx) => {
      const title = e.title ?? e.TITLE;
      const place = e.place ?? e.PLACE;
      const img   = e.main_img ?? e.MAIN_IMG;
      const link  = cleanUrl(e.hmpg_addr ?? e.HMPG_ADDR ?? e.ORG_LINK ?? e.org_link);
      const dateText = e.date ?? e.DATE;
      const codeName = e.codename ?? e.CODENAME;
      const theme    = e.themecode ?? e.THEMECODE;
      const gu       = (e.guname ?? e.GUNAME ?? e.area)?.trim();

      return {
        id: `${title || "ev"}-${e.strtdate ?? e.STRTDATE ?? dateText ?? idx}`,
        title,
        category: toHighLevelCategory(codeName, theme),
        date: dateText,
        place,
        area: gu || "Í∏∞ÌÉÄ",
        thumb: img || "/images/no-image.png",
        link,
      };
    });
  }, [raw]);

  // ‚ë¢ ÏûêÏπòÍµ¨ Î™©Î°ù (Îç∞Ïù¥ÌÑ∞ Í∏∞Ï§Ä ÎèôÏ†Å ÏÉùÏÑ±)
  const AREA_OPTIONS = useMemo(() => {
    const s = new Set(["Ï†ÑÏ≤¥"]);
    ALL_EVENTS.forEach((e) => e.area && s.add(e.area));
    return Array.from(s);
  }, [ALL_EVENTS]);

  // ‚ë£ ÌïÑÌÑ∞/Í≤ÄÏÉâ (ÎÇ†Ïßú ÌïÑÌÑ∞ Ï†úÍ±∞)
  const results = useMemo(() => {
    let list = category === "Ï†ÑÏ≤¥" ? ALL_EVENTS : ALL_EVENTS.filter((e) => e.category === category);
    if (area !== "Ï†ÑÏ≤¥") list = list.filter((e) => e.area === area);

    if (query.trim()) {
      const q = query.trim().toLowerCase();
      list = list.filter(
        (e) =>
          (e.title && e.title.toLowerCase().includes(q)) ||
          (e.place && e.place.toLowerCase().includes(q)) ||
          (e.area && e.area.toLowerCase().includes(q))
      );
    }
    return list;
  }, [ALL_EVENTS, category, area, query]);

  const onSubmit = (e) => {
    e.preventDefault();
    setQuery(input);
  };

  return (
    <div className="min-h-screen bg-white">
      <div className="max-w-6xl mx-auto px-4 md:px-6">
        {/* Ïª®Ìä∏Î°§ Î∞î: Ïπ¥ÌÖåÍ≥†Î¶¨ + ÏûêÏπòÍµ¨ + Í≤ÄÏÉâ */}
        <div className="flex flex-col md:flex-row md:items-center gap-3 md:gap-4">
          {/* Ïπ¥ÌÖåÍ≥†Î¶¨ */}
          <div className="w-full md:w-48">
            <label htmlFor="category" className="sr-only">Ïπ¥ÌÖåÍ≥†Î¶¨</label>
            <select
              id="category"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              className="w-full border rounded-md px-3 py-2 bg-white"
            >
              {CATEGORIES.map((c) => (
                <option key={c} value={c}>{c}</option>
              ))}
            </select>
          </div>

          {/* ÏûêÏπòÍµ¨ */}
          <div className="w-full md:w-48">
            <label htmlFor="area" className="sr-only">ÏûêÏπòÍµ¨</label>
            <select
              id="area"
              value={area}
              onChange={(e) => setArea(e.target.value)}
              className="w-full border rounded-md px-3 py-2 bg-white"
            >
              {AREA_OPTIONS.map((g) => (
                <option key={g} value={g}>{g}</option>
              ))}
            </select>
          </div>

