// src/pages/BrowseEvents.jsx
import React, { useEffect, useMemo, useState } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import useEvents from "../hooks/useEvents";

// ÏÉÅÏúÑ Ïπ¥ÌÖåÍ≥†Î¶¨(Í≥µÏó∞/Ï†ÑÏãú/ÍµêÏú°/Í∏∞ÌÉÄ)Î°ú Îß§Ìïë
function toHighLevelCategory(codename = "", themecode = "") {
  const c = String(codename);
  if (["ÏΩòÏÑúÌä∏", "ÌÅ¥ÎûòÏãù", "Íµ≠ÏïÖ", "Î¨¥Ïö©", "Ïó∞Í∑π", "ÎÆ§ÏßÄÏª¨/Ïò§ÌéòÎùº", "Ï∂ïÏ†ú-Í∏∞ÌÉÄ"].some(k => c.includes(k))) {
    return "Í≥µÏó∞";
  }
  if (c.includes("Ï†ÑÏãú/ÎØ∏Ïà†")) return "Ï†ÑÏãú";
  if (c.includes("ÍµêÏú°/Ï≤¥Ìóò") || String(themecode).includes("ÍµêÏú°")) return "ÍµêÏú°/Ï≤¥Ìóò";
  return "Í∏∞ÌÉÄ";
}

const CATEGORIES = ["Ï†ÑÏ≤¥", "Í≥µÏó∞", "Ï†ÑÏãú", "ÍµêÏú°/Ï≤¥Ìóò", "Í∏∞ÌÉÄ"];

export default function BrowseEvents() {
  const navigate = useNavigate();
  const [sp, setSp] = useSearchParams();

  // URL ÏøºÎ¶¨ ÎèôÍ∏∞Ìôî
  const initialCategory = decodeURIComponent(sp.get("category") || "Ï†ÑÏ≤¥");
  const initialQuery = sp.get("q") || "";

  const [category, setCategory] = useState(
    CATEGORIES.includes(initialCategory) ? initialCategory : "Ï†ÑÏ≤¥"
  );
  const [input, setInput] = useState(initialQuery);
  const [query, setQuery] = useState(initialQuery);

  useEffect(() => {
    const next = new URLSearchParams(sp);
    if (category && category !== "Ï†ÑÏ≤¥") next.set("category", encodeURIComponent(category));
    else next.delete("category");
    if (query) next.set("q", query);
    else next.delete("q");
    setSp(next, { replace: true });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [category, query]);

  // ‚ë† Í≥µÍ≥µÎç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞
  const raw = useEvents();

  // ‚ë° ÌôîÎ©¥ÏóêÏÑú Ïì∞Í∏∞ Ï¢ãÍ≤å Î≥ÄÌôò
  const ALL_EVENTS = useMemo(() => {
    return (raw || []).map((e, idx) => ({
      id: `${e.title || "ev"}-${e.strtdate || e.date || idx}`,
      title: e.title,
      category: toHighLevelCategory(e.codename, e.themecode),
      date: e.date,                   // "YYYY-MM-DD~YYYY-MM-DD" ÌòïÌÉú
      place: e.place,
      thumb: e.main_img || "/images/no-image.png",
      link: e.hmpg_addr || e.org_link // ÏÉÅÏÑ∏Î°ú Ïó∞Í≤∞ Í∞ÄÎä•Ìïú URL
    }));
  }, [raw]);

  // ‚ë¢ ÌïÑÌÑ∞/Í≤ÄÏÉâ
  const results = useMemo(() => {
    const byCategory =
      category === "Ï†ÑÏ≤¥" ? ALL_EVENTS : ALL_EVENTS.filter((e) => e.category === category);
    if (!query.trim()) return byCategory;
    const q = query.trim().toLowerCase();
    return byCategory.filter(
      (e) =>
        (e.title && e.title.toLowerCase().includes(q)) ||
        (e.place && e.place.toLowerCase().includes(q))
    );
  }, [ALL_EVENTS, category, query]);

  const onSubmit = (e) => {
    e.preventDefault();
    setQuery(input);
  };

  return (
    <div className="min-h-screen bg-white">
      <div className="max-w-6xl mx-auto px-4 md:px-6">
        {/* Ïª®Ìä∏Î°§ Î∞î: Ïπ¥ÌÖåÍ≥†Î¶¨ + Í≤ÄÏÉâ */}
        <div className="flex flex-col md:flex-row md:items-center gap-3 md:gap-4">
          {/* Ïπ¥ÌÖåÍ≥†Î¶¨ */}
          <div className="w-full md:w-56">
            <label htmlFor="category" className="sr-only">Ïπ¥ÌÖåÍ≥†Î¶¨</label>
            <select
              id="category"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              className="w-full border rounded-md px-3 py-2 bg-white"
            >
              {CATEGORIES.map((c) => (
                <option key={c} value={c}>{c}</option>
              ))}
            </select>
          </div>

          {/* Í≤ÄÏÉâ */}
          <form onSubmit={onSubmit} className="flex-1">
            <div className="relative">
              <input
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="ÌñâÏÇ¨Î™Ö, Ïû•ÏÜåÎ°ú Í≤ÄÏÉâ"
                className="w-full border rounded-md pl-3 pr-10 py-2"
              />
              <button
                type="submit"
                aria-label="Í≤ÄÏÉâ"
                className="absolute right-2 top-1/2 -translate-y-1/2 p-1 rounded hover:bg-gray-50"
              >
                <img src="/images/search.png" alt="" className="w-4 h-4" />
              </button>
            </div>
          </form>
        </div>

        {/* Î≥∏Î¨∏: Í≤∞Í≥º + Ï∞∏Í≥† Ïù¥ÎØ∏ÏßÄ */}
        <div className="mt-6 grid grid-cols-1 lg:grid-cols-12 gap-6">
          {/* Í≤∞Í≥º Î¶¨Ïä§Ìä∏ */}
          <div className="lg:col-span-8">
            <div className="border rounded-lg p-4 min-h-[360px]">
              {results.length === 0 ? (
                <div className="h-56 grid place-items-center text-gray-500">
                  Ï°∞Í±¥Ïóê ÎßûÎäî ÌñâÏÇ¨Í∞Ä ÏóÜÏäµÎãàÎã§.
                </div>
              ) : (
                <div className="grid sm:grid-cols-2 gap-4">
                  {results.map((ev) => (
                    <button
                      key={ev.id}
                      onClick={() => {
                        if (ev.link) window.open(ev.link, "_blank", "noopener,noreferrer");
                        else navigate(`/detail/${encodeURIComponent(ev.id)}`);
                      }}
                      className="text-left bg-white border rounded-lg overflow-hidden hover:shadow transition"
                    >
                      <img
                        src={ev.thumb}
                        alt={ev.title}
                        className="w-full h-36 object-cover"
                        onError={(e) => (e.currentTarget.src = "/images/no-image.png")}
                      />
                      <div className="p-3">
                        <div className="flex items-center justify-between">
                          <h3 className="font-semibold line-clamp-2">{ev.title}</h3>
                          <span className="text-xs px-2 py-0.5 rounded-full bg-gray-100">
                            {ev.category}
                          </span>
                        </div>
                        <p className="text-sm text-gray-600 mt-1">üìÖ {ev.date}</p>
                        <p className="text-sm text-gray-600">üìç {ev.place}</p>
                      </div>
                    </button>
                  ))}
                </div>
              )}
            </div>
          </div>

          {/* Ï∞∏Í≥† Ïù¥ÎØ∏ÏßÄ */}
          <aside className="lg:col-span-4">
            <div className="border rounded-lg overflow-hidden">
              <img
                src="/images/wireframes/browse_guide.png"
                alt="Browse ÌéòÏù¥ÏßÄ Í∏∞Îä• ÏÑ§Î™Ö Ïù¥ÎØ∏ÏßÄ"
                className="w-full h-auto"
              />
            </div>
            <p className="mt-2 text-xs text-gray-500">* Ï∞∏Í≥† Ïù¥ÎØ∏ÏßÄÎäî ÌïÑÏöî ÏóÜÏúºÎ©¥ Ï†úÍ±∞Ìï¥ÎèÑ Îê©ÎãàÎã§.</p>
          </aside>
        </div>
      </div>
    </div>
  );
}
