// src/pages/Map.jsx
import React, { useEffect, useMemo, useRef, useState } from "react";

/* === ÌôòÍ≤ΩÎ≥ÄÏàò === */
const SEOUL_KEY = (process.env.REACT_APP_SEOUL_KEY || "").trim();
const KAKAO_KEY = (process.env.REACT_APP_KAKAO_MAP_KEY || "").trim();

/* === ÌïÑÌÑ∞ ÏòµÏÖò === */
const CATEGORIES = ["Ï†ÑÏ≤¥", "Í≥µÏó∞", "Ï†ÑÏãú", "ÍµêÏú°/Ï≤¥Ìóò", "Í∏∞ÌÉÄ"];
const AREAS = [
  "Ï†ÑÏ≤¥", "Ï¢ÖÎ°úÍµ¨", "Ï§ëÍµ¨", "Ïö©ÏÇ∞Íµ¨", "ÏÑ±ÎèôÍµ¨", "Í¥ëÏßÑÍµ¨",
  "ÎèôÎåÄÎ¨∏Íµ¨", "Ï§ëÎûëÍµ¨", "ÏÑ±Î∂ÅÍµ¨", "Í∞ïÎ∂ÅÍµ¨", "ÎèÑÎ¥âÍµ¨",
  "ÎÖ∏ÏõêÍµ¨", "ÏùÄÌèâÍµ¨", "ÏÑúÎåÄÎ¨∏Íµ¨", "ÎßàÌè¨Íµ¨", "ÏñëÏ≤úÍµ¨",
  "Í∞ïÏÑúÍµ¨", "Íµ¨Î°úÍµ¨", "Í∏àÏ≤úÍµ¨", "ÏòÅÎì±Ìè¨Íµ¨", "ÎèôÏûëÍµ¨",
  "Í¥ÄÏïÖÍµ¨", "ÏÑúÏ¥àÍµ¨", "Í∞ïÎÇ®Íµ¨", "ÏÜ°ÌååÍµ¨", "Í∞ïÎèôÍµ¨"
];
const QUICK_RANGES = ["Ï†ÑÏ≤¥", "Ïò§Îäò", "Ïù¥Î≤à Ï£º", "Ïù¥Î≤à Îã¨"];

/* === ÏÑúÏö∏Ïãú ÌñâÏ†ïÍµ¨ Ï§ëÏã¨ Ï¢åÌëú(ÎåÄÎûµ) === */
const GU_CENTER = {
  Ï¢ÖÎ°úÍµ¨: [37.5730, 126.9794],
  Ï§ëÍµ¨: [37.5636, 126.9976],
  Ïö©ÏÇ∞Íµ¨: [37.5326, 126.9905],
  ÏÑ±ÎèôÍµ¨: [37.5636, 127.0364],
  Í¥ëÏßÑÍµ¨: [37.5386, 127.0822],
  ÎèôÎåÄÎ¨∏Íµ¨: [37.5744, 127.0396],
  Ï§ëÎûëÍµ¨: [37.6060, 127.0929],
  ÏÑ±Î∂ÅÍµ¨: [37.5894, 127.0167],
  Í∞ïÎ∂ÅÍµ¨: [37.6396, 127.0257],
  ÎèÑÎ¥âÍµ¨: [37.6688, 127.0471],
  ÎÖ∏ÏõêÍµ¨: [37.6542, 127.0568],
  ÏùÄÌèâÍµ¨: [37.6176, 126.9227],
  ÏÑúÎåÄÎ¨∏Íµ¨: [37.5791, 126.9368],
  ÎßàÌè¨Íµ¨: [37.5665, 126.9018],
  ÏñëÏ≤úÍµ¨: [37.5169, 126.8665],
  Í∞ïÏÑúÍµ¨: [37.5510, 126.8495],
  Íµ¨Î°úÍµ¨: [37.4954, 126.8874],
  Í∏àÏ≤úÍµ¨: [37.4599, 126.9001],
  ÏòÅÎì±Ìè¨Íµ¨: [37.5263, 126.8963],
  ÎèôÏûëÍµ¨: [37.5124, 126.9393],
  Í¥ÄÏïÖÍµ¨: [37.4784, 126.9516],
  ÏÑúÏ¥àÍµ¨: [37.4836, 127.0326],
  Í∞ïÎÇ®Íµ¨: [37.5173, 127.0473],
  ÏÜ°ÌååÍµ¨: [37.5112, 127.0980],
  Í∞ïÎèôÍµ¨: [37.5301, 127.1238]
};

/* === Ï¶êÍ≤®Ï∞æÍ∏∞ Ïú†Ìã∏(localStorage) === */
const LS_FAV = "sn_favorites";
const loadFavs = () => { try { return JSON.parse(localStorage.getItem(LS_FAV) || "[]"); } catch { return []; } };
const saveFavs = (list) => localStorage.setItem(LS_FAV, JSON.stringify(list));
const isFav = (id) => loadFavs().some((x) => x.id === id);
const toggleFav = (item) => {
  const cur = loadFavs();
  const exists = cur.some((x) => x.id === item.id);
  const next = exists ? cur.filter((x) => x.id !== item.id) : [...cur, item];
  saveFavs(next);
  return next;
};

/* === Kakao SDK Î°úÎçî (.env ÌÇ§ ÏÇ¨Ïö©) === */
const loadKakao = () =>
  new Promise((resolve, reject) => {
    if (window.kakao && window.kakao.maps) { resolve(window.kakao); return; }

    const key = KAKAO_KEY; // ÌïÑÏàò: .envÏùò REACT_APP_KAKAO_MAP_KEY
    if (!key) return reject(new Error("REACT_APP_KAKAO_MAP_KEYÍ∞Ä ÏÑ§Ï†ïÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§ (.env ÌôïÏù∏)."));

    const ID = "kakao-maps-sdk";
    const exist = document.getElementById(ID);

    const onLoaded = () => {
      try { window.kakao.maps.load(() => resolve(window.kakao)); }
      catch (e) { reject(e); }
    };

    if (exist) {
      exist.addEventListener("load", onLoaded, { once: true });
      exist.addEventListener("error", reject, { once: true });
      return;
    }

    const s = document.createElement("script");
    s.id = ID;
    s.async = true;
    s.src = `https://dapi.kakao.com/v2/maps/sdk.js?appkey=${encodeURIComponent(key)}&libraries=services,clusterer,drawing&autoload=false`;
    s.onload = onLoaded;
    s.onerror = reject;
    document.head.appendChild(s);
  });

/* === ÏÑúÏö∏Ïãú Î¨∏ÌôîÌñâÏÇ¨ OpenAPI(JSON) ===
   ÌîÑÎ°ùÏãúÎ•º Ïì∞ÏßÄ ÏïäÍ≥† .env ÌÇ§Î°ú ÏßÅÏ†ë Ìò∏Ï∂ú
*/
const SEOUL_API_URL = SEOUL_KEY
  ? `http://openapi.seoul.go.kr:8088/${encodeURIComponent(SEOUL_KEY)}/json/culturalEventInfo/1/200/`
  : null;

/* === ÏÉÅÏúÑ Ïπ¥ÌÖåÍ≥†Î¶¨ Îß§Ìïë === */
function toHighLevelCategory(codename = "", themecode = "") {
  const c = String(codename);
  if (["ÏΩòÏÑúÌä∏", "ÌÅ¥ÎûòÏãù", "Íµ≠ÏïÖ", "Î¨¥Ïö©", "Ïó∞Í∑π", "ÎÆ§ÏßÄÏª¨/Ïò§ÌéòÎùº", "Ï∂ïÏ†ú-Í∏∞ÌÉÄ"].some(k => c.includes(k))) return "Í≥µÏó∞";
  if (c.includes("Ï†ÑÏãú/ÎØ∏Ïà†")) return "Ï†ÑÏãú";
  if (c.includes("ÍµêÏú°/Ï≤¥Ìóò") || String(themecode).includes("ÍµêÏú°")) return "ÍµêÏú°/Ï≤¥Ìóò";
  return "Í∏∞ÌÉÄ";
}

/* === ÎÇ†Ïßú Ïú†Ìã∏ === */
const ymd = (d) => {
  const y = d.getFullYear();
  const m = String(d.getMonth() + 1).padStart(2, "0");
  const day = String(d.getDate()).padStart(2, "0");
  return `${y}-${m}-${day}`;
};
const parseToDate = (s = "") => {
  if (!s) return null;
  const raw = String(s).trim();
  if (/^\d{8}$/.test(raw)) {
    const y = raw.slice(0, 4), m = raw.slice(4, 6), d = raw.slice(6, 8);
    const dt = new Date(`${y}-${m}-${d}T00:00:00`);
    return isNaN(dt) ? null : dt;
  }
  const normalized = raw.replaceAll(".", "-");
  const dt = new Date(normalized);
  return isNaN(dt) ? null : dt;
};
const normalizeRangeLabel = (s = "", e = "") => {
  const S = (s || "").replaceAll(".", "-");
  const E = (e || "").replaceAll(".", "-");
  if (!S && !E) return "ÏùºÏ†ï ÎØ∏Ï†ï";
  if (S && E) return `${S} ~ ${E}`;
  return S || E;
};

/* === API ‚Üí Ïï±Ïö© Ïù¥Î≤§Ìä∏ Ï†ïÍ∑úÌôî(Ï¢åÌëúÎäî ÎÇòÏ§ëÏóê Ï±ÑÏõÄ) === */
function normalizeEvents(json) {
  const rows = json?.culturalEventInfo?.row || [];
  return rows.map((r, idx) => {
    const startStr = r.STRTDATE || r.DATE;
    const endStr = r.END_DATE || r.ENDDATE || r.END;
    const start = parseToDate(startStr);
    const end = parseToDate(endStr) || start;
    const category = toHighLevelCategory(r.CODENAME, r.THEMECODE);

    return {
      id: r.SVCID || `evt_${idx}`,
      title: r.TITLE || r.SVCNM || "Î¨¥Ï†ú",
      category,
      place: r.PLACE || "",
      gu: r.GUNAME || "",
      dateStart: start ? ymd(start) : "",
      dateEnd: end ? ymd(end) : "",
      dateLabel: normalizeRangeLabel(startStr, endStr),
      homepage: r.ORG_LINK || r.HMPG_ADDR || "",
      fee: r.USE_FEE || "",
      lat: null,
      lng: null,
    };
  });
}

/* === Ï¢åÌëú Ï∫êÏãú(localStorage) === */
const LS_GEO = "sn_geo_cache_v1";
const loadGeoCache = () => { try { return JSON.parse(localStorage.getItem(LS_GEO) || "{}"); } catch { return {}; } };
const saveGeoCache = (obj) => localStorage.setItem(LS_GEO, JSON.stringify(obj));

/* === ÎÇ†Ïßú ÌïÑÌÑ∞ === */
const inRange = (ev, startISO, endISO) => {
  if (!startISO && !endISO) return true;
  const s = ev.dateStart ? new Date(ev.dateStart + "T00:00:00") : null;
  const e = ev.dateEnd ? new Date(ev.dateEnd + "T23:59:59") : s;
  const S = startISO ? new Date(startISO + "T00:00:00") : null;
  const E = endISO ? new Date(endISO + "T23:59:59") : null;
  if (!s || !e) return false;
  const leftOK = !E || s <= E;
  const rightOK = !S || e >= S;
  return leftOK && rightOK;
};

// üåü ÏÑúÏö∏Ïãú ÎåÄÎûµÏ†ÅÏù∏ Í≤ΩÍ≥Ñ (ÏßÄÏò§ÏΩîÎî© Í≤∞Í≥º ÌïÑÌÑ∞ÎßÅÏö©)
// ÏúÑÎèÑ: 37.4 ~ 37.7, Í≤ΩÎèÑ: 126.7 ~ 127.2
const isWithinSeoulBoundary = (lat, lng) => {
  return lat >= 37.4 && lat <= 37.7 && lng >= 126.7 && lng <= 127.2;
};

export default function MapPage() {
  /* ÌïÑÌÑ∞ ÏÉÅÌÉú */
  const [category, setCategory] = useState("Ï†ÑÏ≤¥");
  const [area, setArea] = useState("Ï†ÑÏ≤¥");
  const [quick, setQuick] = useState("Ï†ÑÏ≤¥");
  const [startDate, setStartDate] = useState(""); // YYYY-MM-DD
  const [endDate, setEndDate] = useState("");

  /* Îç∞Ïù¥ÌÑ∞/ÏßÄÎèÑ ÏÉÅÌÉú */
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState(null);
  const [selected, setSelected] = useState(null);

  const mapEl = useRef(null);
  const mapRef = useRef(null);
  const markersRef = useRef([]);
  const infoRef = useRef([]);
  const clusterRef = useRef(null);
  const kakaoRef = useRef(null);

  /* Kakao + Îç∞Ïù¥ÌÑ∞ Î°úÎìú */
  useEffect(() => {
    let disposed = false;
    (async () => {
      try {
        const kakao = await loadKakao();
        if (disposed) return;
        kakaoRef.current = kakao;

        const center = new kakao.maps.LatLng(37.5665, 126.9780);
        if (mapEl.current) {
          const map = new kakao.maps.Map(mapEl.current, { center, level: 7 });
          mapRef.current = map;
        }

        setLoading(true);
        if (!SEOUL_API_URL) throw new Error("REACT_APP_SEOUL_KEYÍ∞Ä ÏÑ§Ï†ïÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§ (.env ÌôïÏù∏).");
        const res = await fetch(SEOUL_API_URL, { cache: "no-store" });
        if (!res.ok) throw new Error(`HTTP ${res.status}`);
        const json = await res.json();
        const items = normalizeEvents(json);
        if (!disposed) setEvents(items);
      } catch (e) {
        if (!disposed) setErr(e);
      } finally {
        if (!disposed) setLoading(false);
      }
    })();
    return () => { disposed = true; };
  }, []);

  /* Îπ†Î•∏ Í∏∞Í∞Ñ ‚Üí ÎÇ†Ïßú Î∞òÏòÅ */
  useEffect(() => {
    const today = new Date();
    if (quick === "Ï†ÑÏ≤¥") { setStartDate(""); setEndDate(""); return; }
    if (quick === "Ïò§Îäò") {
      const k = ymd(today); setStartDate(k); setEndDate(k); return;
    }
    if (quick === "Ïù¥Î≤à Ï£º") {
      const day = today.getDay(); // 0:Ïùº
      const diffToMon = day === 0 ? -6 : 1 - day;
      const mon = new Date(today); mon.setDate(today.getDate() + diffToMon);
      const sun = new Date(mon);   sun.setDate(mon.getDate() + 6);
      setStartDate(ymd(mon)); setEndDate(ymd(sun)); return;
    }
    if (quick === "Ïù¥Î≤à Îã¨") {
      const y = today.getFullYear(), m = today.getMonth();
      const s = new Date(y, m, 1);
      const e = new Date(y, m + 1, 0);
      setStartDate(ymd(s)); setEndDate(ymd(e)); return;
    }
  }, [quick]);

  /* ÏßÄÏó≠ Î≥ÄÍ≤Ω ‚Üí ÏßÄÎèÑ Ï§ëÏã¨ Ïù¥Îèô */
  useEffect(() => {
    const kakao = kakaoRef.current;
    const map = mapRef.current;
    if (!kakao || !map) return;
    if (area === "Ï†ÑÏ≤¥") {
      map.setLevel(7);
      map.setCenter(new kakao.maps.LatLng(37.5665, 126.9780));
    } else if (GU_CENTER[area]) {
      const [lat, lng] = GU_CENTER[area];
      map.setLevel(5);
      map.setCenter(new kakao.maps.LatLng(lat, lng));
    } else {
      const ps = new kakao.maps.services.Places();
      ps.keywordSearch(area, (data, status) => {
        if (status === kakao.maps.services.Status.OK && data[0]) {
          map.setLevel(5);
          map.setCenter(new kakao.maps.LatLng(data[0].y, data[0].x));
        }
      });
    }
  }, [area]);

  /* ÌïÑÌÑ∞ÎßÅ */
  const filtered = useMemo(() => {
    return events.filter((e) => {
      const byCat = category === "Ï†ÑÏ≤¥" ? true : e.category === category;
      const byArea = area === "Ï†ÑÏ≤¥" ? true : (e.gu?.includes(area) || e.place?.includes(area));
      const byDate = inRange(e, startDate, endDate);
      return byCat && byArea && byDate;
    });
  }, [events, category, area, startDate, endDate]);

  /* Ïû•ÏÜå ‚Üí Ï¢åÌëú(ÏßÄÏò§ÏΩîÎî©) + Ï∫êÏãú + ÏÑúÏö∏/Íµ¨ Í≤ÄÏ¶ù Í∞ïÌôî */
  const [geoReadyEvents, setGeoReadyEvents] = useState([]);
  useEffect(() => {
    const kakao = kakaoRef.current;
    if (!kakao) return;

    const cache = loadGeoCache();
    const ps = new kakao.maps.services.Places();
    const geocoder = new kakao.maps.services.Geocoder();

    let cancelled = false;

    const SEOUL_CENTER = new kakao.maps.LatLng(37.5665, 126.9780);
    const isBadPlace = (txt = "") => {
      const s = String(txt);
      return !s.trim() || /Ïò®ÎùºÏù∏|ÎπÑÎåÄÎ©¥|Î¨¥Í¥Ä|ÎØ∏Ï†ï|ÏóÜÏùå/i.test(s);
    };
    const pickSeoulHit = (data, gu) => {
      const inSeoul = data.filter(d => (d.address_name || "").startsWith("ÏÑúÏö∏"));
      const inGu = gu ? inSeoul.filter(d => d.address_name.includes(gu)) : inSeoul;
      return (inGu[0] || inSeoul[0] || null);
    };

    const fillCoords = async () => {
      const targets = filtered.slice(0, 300);
      const out = [];
      for (const ev of targets) {
        const key = `${ev.gu || ""}|${ev.place || ""}|${ev.title}`;
        if (cache[key]) { out.push({ ...ev, ...cache[key] }); continue; }

        let coords = null;

        // 0) PLACEÍ∞Ä ÏßÄÏò§ÏΩîÎî© Î∂ÄÏ†ÅÌï©Ïù¥Î©¥ Î∞îÎ°ú Íµ¨ Ï§ëÏã¨
        if (isBadPlace(ev.place) && GU_CENTER[ev.gu]) {
          const [lat, lng] = GU_CENTER[ev.gu];
          coords = { lat, lng };
        }

        // 1) ÌÇ§ÏõåÎìú Í≤ÄÏÉâ (Íµ¨Î™Ö + Ïû•ÏÜå)
        if (!coords && ev.place) {
          const query = `${ev.gu ? ev.gu + " " : ""}${ev.place}`.trim();
          coords = await new Promise(resolve => {
            ps.keywordSearch(
              query,
              (data, status) => {
                if (status === kakao.maps.services.Status.OK && data.length) {
                  const best = pickSeoulHit(data, ev.gu);
                  if (best) return resolve({ lat: Number(best.y), lng: Number(best.x) });
                }
                resolve(null);
              },
              { location: SEOUL_CENTER, radius: 60000 }
            );
          });
        }

        // 2) Ï£ºÏÜå ÏßÄÏò§ÏΩîÎî© (ÏÑúÏö∏/Íµ¨ Í≤ÄÏ¶ù)
        if (!coords && ev.place) {
          coords = await new Promise(resolve => {
            geocoder.addressSearch(ev.place, (result, status) => {
              if (status === kakao.maps.services.Status.OK && result[0]) {
                const r = result.find(r => r.address_name.startsWith("ÏÑúÏö∏") && (!ev.gu || r.address_name.includes(ev.gu))) || result[0];
                if (r.address_name.startsWith("ÏÑúÏö∏")) {
                  return resolve({ lat: Number(r.y), lng: Number(r.x) });
                }
              }
              resolve(null);
            });
          });
        }

        // 3) ÏµúÏ¢Ö Î≥¥Í∞ï: Íµ¨ Ï§ëÏã¨
        if (!coords && GU_CENTER[ev.gu]) {
          const [lat, lng] = GU_CENTER[ev.gu];
          coords = { lat, lng };
        }
        
        // ÏÑúÏö∏ Í≤ΩÍ≥Ñ Í≤ÄÏ¶ù
        if (coords) {
          if (!isWithinSeoulBoundary(coords.lat, coords.lng)) {
            console.warn(`[Î≤îÏúÑ Ïò§Î•ò] ÏÑúÏö∏ Î∞ñ Ï¢åÌëú Í∞êÏßÄ: ${ev.title}`, coords);
            if (GU_CENTER[ev.gu]) {
              const [lat, lng] = GU_CENTER[ev.gu];
              coords = { lat, lng };
            } else {
              coords = null;
            }
          }
        }

        if (coords) {
          cache[key] = coords;
          out.push({ ...ev, ...coords });
        }

        await new Promise(r => setTimeout(r, 25));
        if (cancelled) return;
      }
      saveGeoCache(cache);
      if (!cancelled) setGeoReadyEvents(out);
    };

    fillCoords();
    return () => { cancelled = true; };
  }, [filtered]);

  /* ÎßàÏª§/ÌÅ¥Îü¨Ïä§ÌÑ∞ Í∞±Ïã† */
  useEffect(() => {
    const kakao = kakaoRef.current;
    const map = mapRef.current;
    if (!kakao || !map) return;

    markersRef.current.forEach((m) => m.setMap(null));
    infoRef.current.forEach((i) => i.close());
    markersRef.current = [];
    infoRef.current = [];
    if (clusterRef.current) clusterRef.current.clear();

    if (geoReadyEvents.length === 0) return;

    const markers = [];
    const infos = [];
    const bounds = new kakao.maps.LatLngBounds();

    geoReadyEvents.forEach((ev) => {
      if (ev.lat == null || ev.lng == null) return;
      const pos = new kakao.maps.LatLng(ev.lat, ev.lng);
      bounds.extend(pos);

      const marker = new kakao.maps.Marker({ position: pos, title: ev.title });
      const iwHtml = `
        <div style="padding:8px 10px; font-size:12px; max-width:240px;">
          <div style="font-weight:600; margin-bottom:4px;">${ev.title}</div>
          <div style="color:#666;">${ev.place || ev.gu || ""}</div>
          <div style="color:#888; margin-top:2px;">${ev.dateLabel}</div>
        </div>
      `;
      const iw = new kakao.maps.InfoWindow({ content: iwHtml });

      kakao.maps.event.addListener(marker, "click", () => {
        infos.forEach((i) => i.close());
        iw.open(map, marker);
        setSelected(ev);
      });

      markers.push(marker);
      infos.push(iw);
    });

    markersRef.current = markers;
    infoRef.current = infos;

    const clusterer = new kakao.maps.MarkerClusterer({
      map,
      markers,
      averageCenter: true,
      minLevel: 6,
      disableClickZoom: false
    });
    clusterRef.current = clusterer;

    if (area === "Ï†ÑÏ≤¥" && markers.length > 0) {
      map.setBounds(bounds, 40, 40, 40, 40);
    }
    if (selected && !geoReadyEvents.some((e) => e.id === selected.id)) {
      setSelected(null);
    }
  }, [geoReadyEvents, area, selected]);

  /* Ï¶êÍ≤®Ï∞æÍ∏∞ ÌÜ†Í∏Ä (ÌéòÏù¥ÏßÄ Ïù¥Îèô ÏóÜÏùå) */
  const [, forceFav] = useState(0);
  const onToggleFav = (ev) => {
    toggleFav({
      id: ev.id,
      title: ev.title,
      category: ev.category,
      date: ev.dateLabel,
      place: ev.place || ev.gu || "",
      homepage: ev.homepage || "",
    });
    forceFav((v) => v + 1);
  };

  return (
    <div className="min-h-screen bg-white px-6 py-8 max-w-7xl mx-auto">
      <div className="grid grid-cols-1 lg:grid-cols-12 gap-6">
        {/* ÏôºÏ™Ω Ï†úÏñ¥ Ìå®ÎÑê */}
        <aside className="lg:col-span-3">
          <h2 className="font-semibold mb-2">Ïπ¥ÌÖåÍ≥†Î¶¨</h2>
          <select
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            className="border rounded px-2 py-2 w-full mb-4"
          >
            {CATEGORIES.map((c) => <option key={c} value={c}>{c}</option>)}
          </select>

          <h2 className="font-semibold mb-2">ÏßÄÏó≠(ÌñâÏ†ïÍµ¨)</h2>
          <select
            value={area}
            onChange={(e) => setArea(e.target.value)}
            className="border rounded px-2 py-2 w-full mb-4"
          >
            {AREAS.map((a) => <option key={a} value={a}>{a}</option>)}
          </select>

          <h2 className="font-semibold mb-2">ÎÇ†Ïßú</h2>
          <div className="grid grid-cols-3 gap-2 mb-2">
            {QUICK_RANGES.map((r) => (
              <button
                key={r}
                onClick={() => setQuick(r)}
                className={
                  "text-xs border rounded px-2 py-1 " +
                  (quick === r ? "bg-black text-white" : "bg-white hover:bg-gray-50")
                }
              >
                {r}
              </button>
            ))}
          </div>
          <div className="flex items-center gap-2">
            <input
              type="date"
              value={startDate}
              onChange={(e) => { setStartDate(e.target.value); setQuick("Ï†ÑÏ≤¥"); }}
              className="border rounded px-2 py-1 text-sm w-full"
            />
            <span className="text-sm text-gray-500">~</span>
            <input
              type="date"
              value={endDate}
              onChange={(e) => { setEndDate(e.target.value); setQuick("Ï†ÑÏ≤¥"); }}
              className="border rounded px-2 py-1 text-sm w-full"
            />
          </div>

          <div className="mt-4 text-xs text-gray-600">
            {loading ? "üîÑ ÏÑúÏö∏Ïãú ÌñâÏÇ¨ Î∂àÎü¨Ïò§Îäî Ï§ë‚Ä¶" : `ÌëúÏãú ÌõÑÎ≥¥: ${filtered.length}Í±¥`}
            {err && <div className="text-red-600 mt-1">Îç∞Ïù¥ÌÑ∞ Î°úÎìú Ïã§Ìå®: {String(err.message || err)}</div>}
          </div>
        </aside>

        {/* ÏßÄÎèÑ */}
        <section className="lg:col-span-6">
          <div
            ref={mapEl}
            className="border rounded-lg w-full"
            style={{ height: 600 }}
            aria-label="Ïπ¥Ïπ¥Ïò§ ÏßÄÎèÑ"
            role="region"
          />
        </section>

        {/* ÏÉÅÏÑ∏ Ìå®ÎÑê */}
        <aside className="lg:col-span-3 border rounded-lg p-4 bg-gray-50">
          {!selected ? (
            <div className="text-gray-500 text-sm text-center mt-20">
              ÏßÄÎèÑÏùò ÎßàÏª§Î•º ÌÅ¥Î¶≠ÌïòÎ©¥ ÏÉÅÏÑ∏ Ï†ïÎ≥¥Í∞Ä ÌëúÏãúÎê©ÎãàÎã§.
            </div>
          ) : (
            <div>
              <div className="text-xs inline-flex items-center rounded-full bg-gray-200 px-2 py-0.5 mb-2">
                {selected.category} {selected.gu ? `¬∑ ${selected.gu}` : ""}
              </div>
              <h3 className="font-bold text-lg mb-1">{selected.title}</h3>
              <p className="text-sm text-gray-700">{selected.place || selected.gu || ""}</p>
              <p className="text-sm text-gray-700 mt-1">üìÖ {selected.dateLabel}</p>
              {selected.fee && <p className="text-xs text-gray-500 mt-1">ÏöîÍ∏à: {selected.fee}</p>}

              <div className="mt-3 flex items-center gap-3">
                <button
                  onClick={() => onToggleFav(selected)}
                  className="text-xl"
                  title={isFav(selected.id) ? "Ï¶êÍ≤®Ï∞æÍ∏∞ Ìï¥Ï†ú" : "Ï¶êÍ≤®Ï∞æÍ∏∞ Ï∂îÍ∞Ä"}
                  aria-label={isFav(selected.id) ? "Ï¶êÍ≤®Ï∞æÍ∏∞ Ìï¥Ï†ú" : "Ï¶êÍ≤®Ï∞æÍ∏∞ Ï∂îÍ∞Ä"}
                >
                  {isFav(selected.id) ? "‚ù§Ô∏è" : "ü§ç"}
                </button>
                {selected.homepage && (
                  <a
                    href={selected.homepage}
                    target="_blank"
                    rel="noreferrer"
                    className="text-sm underline"
                    title="ÏÉÅÏÑ∏Î≥¥Í∏∞"
                  >
                    ÏÉÅÏÑ∏Î≥¥Í∏∞
                  </a>
                )}
              </div>

              <button
                className="mt-4 text-xs underline text-gray-600"
                onClick={() => setSelected(null)}
              >
                ÏÑ†ÌÉù Ìï¥Ï†ú
              </button>
            </div>
          )}
        </aside>
      </div>
    </div>
  );
}
