// Favorites.jsx
import React, { useEffect, useMemo, useState } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";

// Ïπ¥ÌÖåÍ≥†Î¶¨ Í≥†Ï†ï ÎùºÎ≤®
const CATEGORIES = ["Í≥µÏó∞", "Ï†ÑÏãú", "ÍµêÏú°/Ï≤¥Ìóò", "Í∏∞ÌÉÄ"];

// Î°úÏª¨Ïä§ÌÜ†Î¶¨ÏßÄ ÌÇ§
const LS_KEY = "sn_favorites";

// Ï†ÄÏû•/Î∂àÎü¨Ïò§Í∏∞ Ïú†Ìã∏
function loadFavs() {
  try {
    const raw = localStorage.getItem(LS_KEY);
    return raw ? JSON.parse(raw) : [];
  } catch {
    return [];
  }
}
function saveFavs(list) {
  localStorage.setItem(LS_KEY, JSON.stringify(list));
}
function toggleFav(item) {
  const list = loadFavs();
  const exists = list.some((x) => x.id === item.id);
  const next = exists ? list.filter((x) => x.id !== item.id) : [...list, item];
  saveFavs(next);
  return next;
}
function isFav(id) {
  return loadFavs().some((x) => x.id === id);
}

// ÏûÑÏãú Îç∞Ïù¥ÌÑ∞(ÏãúÏó∞Ïö©)
const SEED = [
  { id: "ev001", title: "ÏÑúÏö∏Ïû¨Ï¶àÌéòÏä§Ìã∞Î≤å 2025", category: "Í≥µÏó∞", date: "2025-06-02 ~ 06-05", place: "Ïò¨Î¶ºÌîΩÍ≥µÏõê" },
  { id: "ev002", title: "ÏãúÎ¶ΩÎØ∏Ïà†Í¥Ä Ïó¨Î¶Ñ Í∏∞ÌöçÏ†Ñ", category: "Ï†ÑÏãú", date: "2025-06-10 ~ 08-31", place: "ÏÑúÏö∏ÏãúÎ¶ΩÎØ∏Ïà†Í¥Ä" },
  { id: "ev003", title: "ÌïúÍ∞ï ÎèóÏûêÎ¶¨ Ï≤¥Ìóò ÌÅ¥ÎûòÏä§", category: "ÍµêÏú°/Ï≤¥Ìóò", date: "2025-06-15", place: "Ïó¨ÏùòÎèÑ ÌïúÍ∞ïÍ≥µÏõê" },
  { id: "ev004", title: "Ï≤≠ÎÖÑ Î¨∏ÌôîÎßàÏºì", category: "Í∏∞ÌÉÄ", date: "2025-06-22", place: "ÏÑ±ÏàòÎèô" },
];

export default function Favorites() {
  const navigate = useNavigate();
  const [sp, setSp] = useSearchParams();
  const category = sp.get("category"); // ÏóÜÏúºÎ©¥ Ïπ¥ÌÖåÍ≥†Î¶¨ Í∑∏Î¶¨Îìú, ÏûàÏúºÎ©¥ Î™©Î°ù ÌôîÎ©¥

  // Í≤ÄÏÉâÏñ¥
  const [q, setQ] = useState("");
  // Î°úÏª¨Ïä§ÌÜ†Î¶¨ÏßÄ Ï¶êÍ≤®Ï∞æÍ∏∞ Î™©Î°ù
  const [favs, setFavs] = useState(() => loadFavs());

  // Îç∞Î™® Ìé∏Ïùò: Ï¶êÍ≤®Ï∞æÍ∏∞ ÎπÑÏñ¥ ÏûàÏúºÎ©¥ SEED 2Í∞ú ÎÑ£Ïñ¥Ï§å(ÏµúÏ¥à 1Ìöå)
  useEffect(() => {
    if (favs.length === 0) {
      const seeded = [SEED[0], SEED[1]];
      saveFavs(seeded);
      setFavs(seeded);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ Î™©Î°ù + Í≤ÄÏÉâ
  const list = useMemo(() => {
    const base = category ? favs.filter((x) => x.category === category) : favs;
    if (!q.trim()) return base;
    const k = q.trim().toLowerCase();
    return base.filter(
      (x) => x.title.toLowerCase().includes(k) || x.place.toLowerCase().includes(k)
    );
  }, [favs, category, q]);

  const goGrid = () => {
    sp.delete("category");
    setSp(sp, { replace: true });
    setQ("");
  };
  const goList = (cat) => {
    sp.set("category", cat);
    setSp(sp, { replace: true });
    setQ("");
  };

  const onToggle = (item) => {
    const next = toggleFav(item);
    setFavs(next);
  };

  return (
    <div className="min-h-screen bg-white px-6 py-8 max-w-6xl mx-auto">
      {/* ÌôîÎ©¥ 1: Ïπ¥ÌÖåÍ≥†Î¶¨ Í∑∏Î¶¨Îìú */}
      {!category && (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-10">
          {CATEGORIES.map((cat) => (
            <div key={cat}>
              <div className="mb-2 text-gray-800">{cat}</div>
              <button
                onClick={() => goList(cat)}
                className="w-full aspect-[16/9] bg-gray-200 border rounded-lg hover:bg-gray-300 transition grid place-items-center"
                title={`${cat} Ï¶êÍ≤®Ï∞æÍ∏∞ Î≥¥Í∏∞`}
                aria-label={`${cat} Ï¶êÍ≤®Ï∞æÍ∏∞ Î™©Î°ùÏúºÎ°ú Ïù¥Îèô`}
              >
                {/* ÌöåÏÉâ Î∞ïÏä§ ÎÇ¥Î∂Ä ÌëúÍ∏∞ (ÏÑ§Î™Ö Î¨∏Íµ¨Îäî ÌôîÎ©¥ ÌïòÎã®ÏóêÏÑú Ï†úÍ±∞) */}
                <div className="text-gray-600 text-sm text-center leading-snug px-2">
                  {cat} ÎåÄÌëú Ïù¥ÎØ∏ÏßÄ ÏûêÎ¶¨
                  <br />
                  (ÌÅ¥Î¶≠ÌïòÏó¨ Î™©Î°ù Î≥¥Í∏∞)
                </div>
              </button>
            </div>
          ))}
        </div>
      )}

      {/* ÌôîÎ©¥ 2: ÏÑ†ÌÉù Ïπ¥ÌÖåÍ≥†Î¶¨ Î™©Î°ù */}
      {category && (
        <div className="mt-1">
          {/* ÏÉÅÎã® Î∞î: Ïπ¥ÌÖåÍ≥†Î¶¨Î™Ö + Í≤ÄÏÉâ + Îí§Î°úÍ∞ÄÍ∏∞ */}
          <div className="flex items-center gap-3 mb-4">
            <button
              onClick={goGrid}
              className="px-2 py-1 border rounded text-sm hover:bg-gray-50"
              title="Ïπ¥ÌÖåÍ≥†Î¶¨Î°ú ÎèåÏïÑÍ∞ÄÍ∏∞"
              aria-label="Ïπ¥ÌÖåÍ≥†Î¶¨ Í∑∏Î¶¨ÎìúÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞"
            >
              ‚Üê
            </button>
            <h2 className="text-lg font-semibold">{category}</h2>

            <div className="ml-auto relative">
              <input
                value={q}
                onChange={(e) => setQ(e.target.value)}
                placeholder="Í≤ÄÏÉâ"
                className="border rounded pl-3 pr-8 py-1 text-sm"
                aria-label="Ï¶êÍ≤®Ï∞æÍ∏∞ Í≤ÄÏÉâ"
              />
              <span className="absolute right-2 top-1/2 -translate-y-1/2 select-none">üîç</span>
            </div>
          </div>

          {/* Î™©Î°ù */}
          {list.length === 0 ? (
            <div className="text-gray-500 text-sm h-40 grid place-items-center border rounded">
              {q ? "Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§." : "Ïù¥ Ïπ¥ÌÖåÍ≥†Î¶¨Ïóê Ï¶êÍ≤®Ï∞æÍ∏∞Ìïú ÌñâÏÇ¨Í∞Ä ÏóÜÏäµÎãàÎã§."}
            </div>
          ) : (
            <ul className="space-y-3">
              {list.map((it) => {
                const fav = isFav(it.id);
                return (
                  <li
                    key={it.id}
                    className="flex items-center justify-between bg-gray-200 rounded px-4 py-3"
                  >
                    <div className="min-w-0">
                      <div className="font-medium truncate">{it.title}</div>
                      <div className="text-xs text-gray-700 mt-0.5">
                        üìÖ {it.date} ¬∑ üìç {it.place}
                      </div>
                    </div>
                    <button
                      onClick={() => onToggle(it)}
                      className="ml-3 text-xl"
                      title={fav ? "Ï¶êÍ≤®Ï∞æÍ∏∞ Ìï¥Ï†ú" : "Ï¶êÍ≤®Ï∞æÍ∏∞ Ï∂îÍ∞Ä"}
                      aria-label={fav ? "Ï¶êÍ≤®Ï∞æÍ∏∞ Ìï¥Ï†ú" : "Ï¶êÍ≤®Ï∞æÍ∏∞ Ï∂îÍ∞Ä"}
                    >
                      {fav ? "‚ù§Ô∏è" : "ü§ç"}
                    </button>
                  </li>
                );
              })}
            </ul>
          )}
        </div>
      )}
    </div>
  );
}
