import React, { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";

// Î°úÏª¨Ïä§ÌÜ†Î¶¨ÏßÄ ÌÇ§ (Favorites.jsxÏôÄ ÎèôÏùº ÌÇ§ ÏÇ¨Ïö©)
const LS_FAV = "sn_favorites";
const LS_RECENT = "sn_recent"; // ÏµúÍ∑º Î≥∏ Ìï≠Î™© ÏµúÎåÄ 10Í∞ú

function loadLS(key, fallback = []) {
  try {
    const raw = localStorage.getItem(key);
    return raw ? JSON.parse(raw) : fallback;
  } catch {
    return fallback;
  }
}
function saveLS(key, value) {
  localStorage.setItem(key, JSON.stringify(value));
}

/** Îã§Î•∏ ÌéòÏù¥ÏßÄ(ÏÉÅÏÑ∏ Îì±)ÏóêÏÑú Ìò∏Ï∂úÌï¥ÏÑú ÏµúÍ∑º Î≥∏ Ìï≠Î™©ÏùÑ Í∏∞Î°ùÌï† Îïå Ïì∞Î©¥ Îê®
 *  ÏÇ¨Ïö© Ïòà:
 *    addRecent({ id, title, place, date, category })
 */
export function addRecent(item) {
  const arr = loadLS(LS_RECENT, []);
  const withoutDup = arr.filter((x) => x.id !== item.id);
  const next = [item, ...withoutDup].slice(0, 10); // ÏµúÎåÄ 10Í∞ú
  saveLS(LS_RECENT, next);
  return next;
}

export default function MyPage() {
  const navigate = useNavigate();
  const [recent, setRecent] = useState(() => loadLS(LS_RECENT, []));
  const [favs, setFavs] = useState(() => loadLS(LS_FAV, []));

  // Îç∞Î™® Ìé∏Ïùò: Îëò Îã§ ÎπÑÏñ¥ ÏûàÏúºÎ©¥ ÏÉòÌîå 2Í∞úÏî© Ï±ÑÏö∞Í∏∞ (ÏµúÏ¥à 1Ìöå)
  useEffect(() => {
    if (recent.length === 0) {
      const seed = [
        { id: "ev001", title: "ÏÑúÏö∏Ïû¨Ï¶àÌéòÏä§Ìã∞Î≤å 2025", place: "Ïò¨Î¶ºÌîΩÍ≥µÏõê", date: "2025-06-02 ~ 06-05", category: "Í≥µÏó∞" },
        { id: "ev002", title: "ÏãúÎ¶ΩÎØ∏Ïà†Í¥Ä Ïó¨Î¶Ñ Í∏∞ÌöçÏ†Ñ", place: "ÏÑúÏö∏ÏãúÎ¶ΩÎØ∏Ïà†Í¥Ä", date: "2025-06-10 ~ 08-31", category: "Ï†ÑÏãú" },
      ];
      setRecent(seed);
      saveLS(LS_RECENT, seed);
    }
    if (favs.length === 0) {
      const seedFav = [
        { id: "ev003", title: "ÌïúÍ∞ï ÎèóÏûêÎ¶¨ Ï≤¥Ìóò ÌÅ¥ÎûòÏä§", place: "Ïó¨ÏùòÎèÑ ÌïúÍ∞ïÍ≥µÏõê", date: "2025-06-15", category: "ÍµêÏú°/Ï≤¥Ìóò" },
        { id: "ev004", title: "Ï≤≠ÎÖÑ Î¨∏ÌôîÎßàÏºì", place: "ÏÑ±ÏàòÎèô", date: "2025-06-22", category: "Í∏∞ÌÉÄ" },
      ];
      setFavs(seedFav);
      saveLS(LS_FAV, seedFav);
    }
  }, []); // eslint-disable-line

  // Í∞ÄÏßú ÏßÄÎèÑ ÎßàÏª§ Ï¢åÌëú(Ïª®ÌÖåÏù¥ÎÑàÏùò width/heightÎ•º 0~1Î°ú Ï†ïÍ∑úÌôî)
  const markerSlots = useMemo(
    () => [
      { x: 0.32, y: 0.35 },
      { x: 0.58, y: 0.42 },
      { x: 0.45, y: 0.55 },
      { x: 0.70, y: 0.30 },
      { x: 0.25, y: 0.60 },
      { x: 0.60, y: 0.70 },
      { x: 0.40, y: 0.28 },
      { x: 0.15, y: 0.45 },
    ],
    []
  );

  return (
    <div className="min-h-screen bg-white px-6 py-8 max-w-7xl mx-auto">
      

      <div className="grid grid-cols-1 lg:grid-cols-12 gap-8">
        {/* ‚ë† ÏµúÍ∑º Î≥∏ Ìï≠Î™© */}
        <section className="lg:col-span-6">
          <h2 className="text-lg font-semibold mb-3">ÏµúÍ∑º Î≥∏ Ìï≠Î™©</h2>

          {recent.length === 0 ? (
            <div className="h-[420px] grid place-items-center bg-gray-100 border rounded text-gray-500">
              ÏµúÍ∑º Î≥∏ Ìï≠Î™©Ïù¥ ÏóÜÏäµÎãàÎã§.
            </div>
          ) : (
            <div className="border rounded p-3 h-[420px] overflow-auto">
              <ul className="space-y-2">
                {recent.map((it) => (
                  <li key={it.id}>
                    <button
                      onClick={() => navigate(`/detail/${it.id}`)}
                      className="w-full text-left px-3 py-2 rounded hover:bg-gray-50 border"
                      title="ÏÉÅÏÑ∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô"
                    >
                      <div className="flex items-center justify-between gap-2">
                        <div className="min-w-0">
                          <div className="font-medium truncate">{it.title}</div>
                          <div className="text-xs text-gray-600 truncate">
                            üìÖ {it.date} ¬∑ üìç {it.place} ¬∑ {it.category}
                          </div>
                        </div>
                        <span className="text-xs text-gray-500">ÏûêÏÑ∏Ìûà ‚Üí</span>
                      </div>
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </section>

        {/* ‚ë° My Favorite (Í∞ÄÏßú ÏßÄÎèÑ + ÎßàÏª§) */}
        <section className="lg:col-span-6">
          <h2 className="text-lg font-semibold mb-3">My Favorite</h2>

          <div className="relative border rounded-lg bg-gray-200 h-[420px] overflow-hidden">
            {/* ÏßÄÎèÑ ÌîåÎ†àÏù¥Ïä§ÌôÄÎçî Î†àÏù¥Î∏î */}
            <div className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 text-gray-500 text-sm pointer-events-none">
              ÏßÄÎèÑ ÏòÅÏó≠ (Ï¶êÍ≤®Ï∞æÍ∏∞ ÎßàÏª§ ÌëúÏãú ¬∑ API ÎØ∏Ïó∞Í≤∞)
            </div>

            {/* ÎßàÏª§ */}
            {favs.map((f, i) => {
              const pos = markerSlots[i % markerSlots.length];
              return (
                <div
                  key={f.id}
                  title={`${f.title} ¬∑ ${f.place}`}
                  className="absolute -translate-x-1/2 -translate-y-full"
                  style={{
                    left: `${pos.x * 100}%`,
                    top: `${pos.y * 100}%`,
                  }}
                >
                  {/* Îπ®Í∞Ñ ÎßàÏª§ */}
                  <div className="relative group">
                    <div className="w-3 h-3 bg-red-500 rounded-full shadow" />
                    {/* Ìà¥ÌåÅ */}
                    <div className="absolute left-1/2 -translate-x-1/2 mt-2 hidden group-hover:block whitespace-nowrap bg-black text-white text-xs rounded px-2 py-1">
                      {f.title}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>

          {/* Ï¶êÍ≤®Ï∞æÍ∏∞ Í∞ÑÎã® Î¶¨Ïä§Ìä∏ (ÏßÄÎèÑÏùò ÏïÑÎûò) */}
          <ul className="mt-3 text-sm text-gray-700 space-y-1">
            {favs.map((f) => (
              <li key={f.id} className="flex items-center gap-2">
                <span>‚ù§Ô∏è</span>
                <span className="truncate">
                  <b>{f.title}</b> ¬∑ {f.date} ¬∑ {f.place} ({f.category})
                </span>
              </li>
            ))}
            {favs.length === 0 && (
              <li className="text-gray-500">Ï¶êÍ≤®Ï∞æÍ∏∞Ìïú ÌñâÏÇ¨Í∞Ä ÏóÜÏäµÎãàÎã§.</li>
            )}
          </ul>
        </section>
      </div>
    </div>
  );
}
