// src/pages/Calendar.jsx
import React, { useEffect, useMemo, useState } from "react";

/* === ÌôòÍ≤ΩÎ≥ÄÏàò === */
const SEOUL_KEY = (process.env.REACT_APP_SEOUL_KEY || "").trim();

/* === Ïπ¥ÌÖåÍ≥†Î¶¨(Î∏åÎùºÏö∞Ï¶àÏôÄ ÎèôÏùº ÎùºÎ≤®) === */
const CATEGORIES = ["Ï†ÑÏ≤¥", "Í≥µÏó∞", "Ï†ÑÏãú", "ÍµêÏú°/Ï≤¥Ìóò", "Í∏∞ÌÉÄ"];

/* === Ïπ¥ÌÖåÍ≥†Î¶¨ ÏÉâ(Ï†ê ÌëúÏãúÏö©) === */
const CAT_COLOR = {
  Í≥µÏó∞: "bg-indigo-500",
  Ï†ÑÏãú: "bg-emerald-500",
  "ÍµêÏú°/Ï≤¥Ìóò": "bg-amber-500",
  Í∏∞ÌÉÄ: "bg-rose-500",
};

/* === ÏÉÅÏúÑ Ïπ¥ÌÖåÍ≥†Î¶¨ Îß§Ìïë === */
function toHighLevelCategory(codename = "", themecode = "") {
  const c = String(codename);
  if (["ÏΩòÏÑúÌä∏", "ÌÅ¥ÎûòÏãù", "Íµ≠ÏïÖ", "Î¨¥Ïö©", "Ïó∞Í∑π", "ÎÆ§ÏßÄÏª¨/Ïò§ÌéòÎùº", "Ï∂ïÏ†ú-Í∏∞ÌÉÄ"].some(k => c.includes(k))) {
    return "Í≥µÏó∞";
  }
  if (c.includes("Ï†ÑÏãú/ÎØ∏Ïà†")) return "Ï†ÑÏãú";
  if (c.includes("ÍµêÏú°/Ï≤¥Ìóò") || String(themecode).includes("ÍµêÏú°")) return "ÍµêÏú°/Ï≤¥Ìóò";
  return "Í∏∞ÌÉÄ";
}

/* === ÎÇ†Ïßú Ïú†Ìã∏ === */
function ymd(date) {
  const y = date.getFullYear();
  const m = String(date.getMonth() + 1).padStart(2, "0");
  const d = String(date.getDate()).padStart(2, "0");
  return `${y}-${m}-${d}`;
}
function parseToDate(s = "") {
  if (!s) return null;
  const raw = String(s).trim();
  if (/^\d{8}$/.test(raw)) {
    const y = raw.slice(0, 4);
    const m = raw.slice(4, 6);
    const d = raw.slice(6, 8);
    const dt = new Date(`${y}-${m}-${d}T00:00:00`);
    return isNaN(dt) ? null : dt;
  }
  const normalized = raw.replaceAll(".", "-").split(" ")[0];
  const dt = new Date(`${normalized}T00:00:00`);
  return isNaN(dt) ? null : dt;
}
function normalizeRangeLabel(startStr = "", endStr = "") {
  const s = (startStr || "").replaceAll(".", "-");
  const e = (endStr || "").replaceAll(".", "-");
  if (!s && !e) return "ÏùºÏ†ï ÎØ∏Ï†ï";
  if (s && e) return `${s} ~ ${e}`;
  return s || e;
}
function monthMatrix(year, monthIdx, weekStartsOn = 0) {
  // weekStartsOn: 0=Sun, 1=Mon
  const first = new Date(year, monthIdx, 1);
  const last = new Date(year, monthIdx + 1, 0);
  const days = last.getDate();
  const firstDay = (first.getDay() - weekStartsOn + 7) % 7;
  const total = Math.ceil((firstDay + days) / 7) * 7;

  const cells = [];
  for (let i = 0; i < total; i++) {
    const dayOffset = i - firstDay + 1;
    const d = new Date(year, monthIdx, dayOffset);
    const inMonth = dayOffset >= 1 && dayOffset <= days;
    cells.push({ date: d, inMonth });
  }
  return cells;
}

/* === ÏÑúÏö∏Ïãú Î¨∏ÌôîÌñâÏÇ¨ API (JSON) === */
const SEOUL_JSON_URL = SEOUL_KEY
  ? `http://openapi.seoul.go.kr:8088/${encodeURIComponent(SEOUL_KEY)}/json/culturalEventInfo/1/200/`
  : null;

/* API ‚Üí ÌîÑÎ°†Ìä∏Ïö© Ïù¥Î≤§Ìä∏Î°ú Ï†ïÍ∑úÌôî (ÎÇ†ÏßúÍµ¨Í∞ÑÏùÑ Ïùº Îã®ÏúÑÎ°ú ÌôïÏû•) */
function normalizeFromAPI(json) {
  const rows = json?.culturalEventInfo?.row || [];
  return rows.map((r, i) => {
    const startStr = r.STRTDATE || r.DATE;
    const endStr = r.END_DATE || r.ENDDATE || r.END;

    const start = parseToDate(startStr);
    const end = parseToDate(endStr) || start; // Ï¢ÖÎ£åÏùº ÏóÜÏúºÎ©¥ ÏãúÏûëÏùº 1Ïùº ÌñâÏÇ¨Î°ú
    const cat = toHighLevelCategory(r.CODENAME, r.THEMECODE);

    // ÏùºÏûê Î™©Î°ù(ÏµúÎåÄ 31Ïùº ÏïàÏ†Ñ Ï†úÌïú)
    const allDates = [];
    if (start) {
      const until = end && !isNaN(end) ? end : start;
      const cursor = new Date(start);
      let steps = 0;
      while (cursor <= until && steps < 31) {
        allDates.push(ymd(cursor));
        cursor.setDate(cursor.getDate() + 1);
        steps++;
      }
    }

    return {
      id: r.SVCID || `evt_${i}`,
      title: r.TITLE || r.SVCNM || "Î¨¥Ï†ú",
      category: cat,
      place: r.PLACE || r.GUNAME || "Ïû•ÏÜå ÎØ∏Ï†ï",
      dateLabel: normalizeRangeLabel(startStr, endStr),
      allDates, // 'YYYY-MM-DD' Î∞∞Ïó¥
      homepage: r.ORG_LINK || r.HMPG_ADDR,
      fee: r.USE_FEE,
    };
  });
}

/* Îç∞Ïù¥ÌÑ∞ Î°úÎî© ÌõÖ */
function useSeoulCalendarEvents() {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState(null);

  useEffect(() => {
    let abort = false;
    (async () => {
      setLoading(true);
      setErr(null);
      try {
        if (!SEOUL_JSON_URL) throw new Error("REACT_APP_SEOUL_KEYÍ∞Ä ÏÑ§Ï†ïÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§ (.env ÌôïÏù∏).");
        const res = await fetch(SEOUL_JSON_URL);
        if (!res.ok) throw new Error(`HTTP ${res.status}`);
        const json = await res.json();
        const items = normalizeFromAPI(json);
        if (!abort) setEvents(items);
      } catch (e) {
        if (!abort) setErr(e);
      } finally {
        if (!abort) setLoading(false);
      }
    })();
    return () => {
      abort = true;
    };
  }, []);

  return { events, loading, error: err };
}

export default function CalendarPage() {
  // ‚ë† Í∏∞Î≥∏ÏùÄ ÌòÑÏû¨ ÎÖÑ/Ïõî
  const today = new Date();
  const todayKey = ymd(today);
  const [cursor, setCursor] = useState(new Date(today.getFullYear(), today.getMonth(), 1));
  const [category, setCategory] = useState("Ï†ÑÏ≤¥");
  const [activeDay, setActiveDay] = useState(null); // 'YYYY-MM-DD'
  const [showAll, setShowAll] = useState(false);    // Î™©Î°ù ÌéºÏπòÍ∏∞ ÏÉÅÌÉú

  const y = cursor.getFullYear();
  const m = cursor.getMonth();

  // API Îç∞Ïù¥ÌÑ∞ Î°úÎìú
  const { events, loading, error } = useSeoulCalendarEvents();

  // ‚ë¢ Îã¨Î†• Îß§Ìä∏Î¶≠Ïä§
  const matrix = useMemo(() => monthMatrix(y, m, 0), [y, m]);

  // Ïù¥Î≤à Îã¨Ïóê Ìï¥ÎãπÌïòÎäî ÎÇ†ÏßúÎì§Îßå Í≥®Îùº ÎÇ†Ïßú‚ÜíÏù¥Î≤§Ìä∏ Îß§Ìïë
  const byDay = useMemo(() => {
    const map = {};
    const ymPrefix = `${y}-${String(m + 1).padStart(2, "0")}-`;
    const pool = category === "Ï†ÑÏ≤¥" ? events : events.filter(e => e.category === category);

    for (const e of pool) {
      for (const d of e.allDates || []) {
        if (d.startsWith(ymPrefix)) {
          (map[d] ||= []).push(e);
        }
      }
    }
    return map;
  }, [events, category, y, m]);

  // ÌôúÏÑ± ÎÇ†ÏßúÍ∞Ä Î∞îÎÄåÎ©¥ ÌéºÏπ® ÏÉÅÌÉú Ï¥àÍ∏∞Ìôî
  useEffect(() => {
    setShowAll(false);
  }, [activeDay]);

  const selectedEventsAll = activeDay ? (byDay[activeDay] || []) : [];
  const MAX_PREVIEW = 4;
  const selectedEvents = showAll ? selectedEventsAll : selectedEventsAll.slice(0, MAX_PREVIEW);
  const remaining = Math.max(0, selectedEventsAll.length - selectedEvents.length);

  const ymLabel = `${y}.${String(m + 1).padStart(2, "0")}`;

  return (
    <div className="min-h-screen bg-white px-6 py-8 max-w-6xl mx-auto">
      <h2 className="text-2xl font-semibold mb-4">Calendar</h2>

      {/* Ïª®Ìä∏Î°§: ÎÖÑÏõî/Ïπ¥ÌÖåÍ≥†Î¶¨ */}
      <div className="flex flex-col sm:flex-row gap-3 sm:items-center mb-4">
        {/* ÎÖÑÏõî ÏÑ†ÌÉù */}
        <div className="flex items-stretch">
          <div className="border rounded-l px-3 py-2 min-w-[110px] grid place-items-center">
            {ymLabel}
          </div>
          <button
            className="border-y border-r px-3 py-2"
            onClick={() => {
              const d = new Date(y, m - 1, 1);
              setCursor(d);
              setActiveDay(null);
            }}
            aria-label="Ïù¥Ï†Ñ Îã¨"
            title="Ïù¥Ï†Ñ Îã¨"
          >
            ‚óÄ
          </button>
          <button
            className="border rounded-r border-l-0 px-3 py-2"
            onClick={() => {
              const d = new Date(y, m + 1, 1);
              setCursor(d);
              setActiveDay(null);
            }}
            aria-label="Îã§Ïùå Îã¨"
            title="Îã§Ïùå Îã¨"
          >
            ‚ñ∂
          </button>
        </div>

        {/* Ïπ¥ÌÖåÍ≥†Î¶¨ ÏÑ†ÌÉù */}
        <select
          value={category}
          onChange={(e) => {
            setCategory(e.target.value);
            setActiveDay(null);
          }}
          className="border rounded px-3 py-2 w-56"
        >
          {CATEGORIES.map((c) => (
            <option key={c} value={c}>
              {c}
            </option>
          ))}
        </select>
      </div>

      {/* Î°úÎî©/ÏóêÎü¨ */}
      {loading && <div className="mb-4 text-sm text-gray-500">Ïã§ÏãúÍ∞Ñ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë‚Ä¶</div>}
      {error && (
        <div className="mb-4 text-sm text-red-600">
          Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§ÏßÄ Î™ªÌñàÏñ¥Ïöî. {String(error.message || error)}
        </div>
      )}

      {/* Î≥∏Î¨∏: Îã¨Î†• / ÏÉÅÏÑ∏ */}
      <div className="grid grid-cols-1 lg:grid-cols-12 gap-6">
        {/* Ï∫òÎ¶∞Îçî */}
        <div className="lg:col-span-6">
          <div className="border rounded-lg p-4">
            {/* ÏöîÏùº Ìó§Îçî */}
            <div className="text-sm text-gray-600 mb-2">
              {new Date(y, m, 1).toLocaleString("en-US", { month: "long", year: "numeric" })}
            </div>
            <div className="grid grid-cols-7 text-center text-xs text-gray-600 mb-1">
              {["Su", "Mo", "Tu", "We", "Th", "Fr", "Sa"].map((w) => (
                <div key={w} className="py-1">
                  {w}
                </div>
              ))}
            </div>

            <div className="grid grid-cols-7 gap-1">
              {matrix.map(({ date, inMonth }, idx) => {
                const key = ymd(date);
                const day = date.getDate();
                const dayEvents = inMonth ? (byDay[key] || []) : [];
                const isToday = key === todayKey;
                const isPast = inMonth && key < todayKey; // ÏßÄÎÇú ÎÇ†Ïßú ÌöåÏÉâ/ÎπÑÌôúÏÑ±
                const isActive = activeDay === key;

                return (
                  <button
                    key={idx}
                    disabled={!inMonth || isPast}
                    onClick={() => setActiveDay(key)}
                    className={[
                      "aspect-square rounded-md border p-1 text-left",
                      !inMonth
                        ? "bg-gray-50 text-gray-400 cursor-default"
                        : isPast
                        ? "bg-gray-50 text-gray-400 cursor-not-allowed"
                        : "bg-white hover:bg-gray-50",
                      isActive ? "ring-2 ring-black" : "",
                    ].join(" ")}
                    title={inMonth && dayEvents.length ? `${dayEvents.length}Í∞ú ÌñâÏÇ¨` : undefined}
                  >
                    <div className="flex items-center justify-between text-xs">
                      <span className={isToday ? "font-bold underline" : ""}>{day}</span>
                      <span className="flex gap-0.5">
                        {dayEvents.slice(0, 3).map((e, i) => (
                          <i
                            key={i}
                            className={[
                              "inline-block w-1.5 h-1.5 rounded-full",
                              CAT_COLOR[e.category] || "bg-gray-400",
                              isPast ? "opacity-50" : "",
                            ].join(" ")}
                          />
                        ))}
                      </span>
                    </div>
                    <div className="mt-3" />
                  </button>
                );
              })}
            </div>

            {/* Î≤îÎ°Ä */}
            <div className="mt-3 flex flex-wrap gap-3 text-xs">
              {Object.entries(CAT_COLOR).map(([k, v]) => (
                <span key={k} className="inline-flex items-center gap-1">
                  <i className={`inline-block w-2 h-2 rounded-full ${v}`} />
                  {k}
                </span>
              ))}
            </div>
          </div>
        </div>

        {/* ÏÉÅÏÑ∏ */}
        <div className="lg:col-span-6">
          <div className="border rounded-lg p-4 min-h-[280px] bg-gray-50">
            {!activeDay ? (
              <div className="text-sm text-gray-500 h-40 grid place-items-center">
                ÎÇ†ÏßúÎ•º ÏÑ†ÌÉùÌïòÎ©¥ Ìï¥Îãπ ÎÇ†ÏßúÏùò ÌñâÏÇ¨Í∞Ä ÌëúÏãúÎê©ÎãàÎã§.
              </div>
            ) : selectedEventsAll.length === 0 ? (
              <div className="text-sm text-gray-500 h-40 grid place-items-center">
                {activeDay} ÏùºÏ†ïÏù¥ ÏóÜÏäµÎãàÎã§.
              </div>
            ) : (
              <>
                <ul className="space-y-3">
                  {selectedEvents.map((e) => (
                    <li key={e.id} className="bg-white border rounded p-3">
                      <div className="flex items-center justify-between">
                        <h4 className="font-semibold">{e.title}</h4>
                        <span className="text-xs px-2 py-0.5 rounded-full bg-gray-100">{e.category}</span>
                      </div>
                      <p className="text-sm text-gray-600 mt-1">üìÖ {e.dateLabel}</p>
                      <p className="text-sm text-gray-600">üìç {e.place}</p>
                      {e.fee && <p className="text-xs text-gray-500 mt-1">ÏöîÍ∏à: {e.fee}</p>}
                      {e.homepage && (
                        <div className="mt-2 text-right">
                          <a
                            className="text-xs underline underline-offset-4"
                            href={e.homepage}
                            target="_blank"
                            rel="noreferrer"
                          >
                            ÏÉÅÏÑ∏Î≥¥Í∏∞
                          </a>
                        </div>
                      )}
                    </li>
                  ))}
                </ul>

                {/* ÎçîÎ≥¥Í∏∞ / Ï†ëÍ∏∞ */}
                {remaining > 0 ? (
                  <div className="mt-4 text-center">
                    <button
                      className="text-sm underline underline-offset-4"
                      onClick={() => setShowAll(true)}
                    >
                      + ÎçîÎ≥¥Í∏∞ ({remaining}Í∞ú)
                    </button>
                  </div>
                ) : selectedEventsAll.length > MAX_PREVIEW ? (
                  <div className="mt-4 text-center">
                    <button
                      className="text-sm underline underline-offset-4"
                      onClick={() => setShowAll(false)}
                    >
                      Ï†ëÍ∏∞
                    </button>
                  </div>
                ) : null}
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
