// src/pages/MyPage.jsx
import React, { useEffect, useRef, useState } from "react";

/* ===== LocalStorage ===== */
const LS_FAV = "sn_favorites"; // [{id,title,place,date,category,homepage?,gu?,lat?,lng?}]
const LS_RECENT = "sn_recent";

const loadLS = (k, fb = []) => {
  try {
    return JSON.parse(localStorage.getItem(k) || JSON.stringify(fb));
  } catch {
    return fb;
  }
};
const saveLS = (k, v) => localStorage.setItem(k, JSON.stringify(v));

/* ÏµúÍ∑º Î≥∏ Ìï≠Î™© Ï†ÄÏû• */
export function addRecent(item) {
  const now = new Date().toISOString();
  const base = {
    id: item.id ?? "",
    title: item.title ?? "",
    date: item.date ?? "",
    place: item.place ?? "",
    category: item.category ?? "",
    thumb: item.thumb ?? "",
    homepage: item.homepage ?? "",
    gu: item.gu ?? "",
    lat: item.lat ?? null,
    lng: item.lng ?? null,
    seenAt: now,
  };

  const arr = loadLS(LS_RECENT, []);
  const withoutDup = arr.filter((x) => x.id !== base.id);
  const next = [base, ...withoutDup].slice(0, 10);

  saveLS(LS_RECENT, next);
  try {
    window.dispatchEvent(new Event("sn:recent-updated"));
  } catch {}
  return next;
}

/* ===== Kakao Maps loader ===== */
const FALLBACK_APPKEY = "2ee5022c1da6fc178bd51ad4042556fb";
const loadKakao = () =>
  new Promise((resolve, reject) => {
    if (window.kakao && window.kakao.maps) {
      resolve(window.kakao);
      return;
    }
    const key = process.env.REACT_APP_KAKAO_MAP_KEY || FALLBACK_APPKEY;
    const ID = "kakao-maps-sdk";

    const exist = document.getElementById(ID);
    const onLoaded = () => {
      try {
        window.kakao.maps.load(() => resolve(window.kakao));
      } catch (e) {
        reject(e);
      }
    };

    if (exist) {
      exist.addEventListener("load", onLoaded, { once: true });
      exist.addEventListener("error", reject, { once: true });
      return;
    }

    const s = document.createElement("script");
    s.id = ID;
    s.async = true;
    s.src = `https://dapi.kakao.com/v2/maps/sdk.js?appkey=${key}&libraries=services,clusterer&autoload=false`;
    s.onload = onLoaded;
    s.onerror = reject;
    document.head.appendChild(s);
  });
  /* ===== Ï¢åÌëú Ï∫êÏãú ===== */
const LS_GEO = "sn_geo_cache_v1";
const loadGeoCache = () => {
  try {
    return JSON.parse(localStorage.getItem(LS_GEO) || "{}");
  } catch {
    return {};
  }
};
const saveGeoCache = (obj) =>
  localStorage.setItem(LS_GEO, JSON.stringify(obj));

/* ===== Íµ¨ Ï§ëÏã¨(Î∞±ÏóÖ) ===== */
const GU_CENTER = {
  Ï¢ÖÎ°úÍµ¨: [37.573, 126.9794],
  Ï§ëÍµ¨: [37.5636, 126.9976],
  Ïö©ÏÇ∞Íµ¨: [37.5326, 126.9905],
  ÏÑ±ÎèôÍµ¨: [37.5636, 127.0364],
  Í¥ëÏßÑÍµ¨: [37.5386, 127.0822],
  ÎèôÎåÄÎ¨∏Íµ¨: [37.5744, 127.0396],
  Ï§ëÎûëÍµ¨: [37.606, 127.0929],
  ÏÑ±Î∂ÅÍµ¨: [37.5894, 127.0167],
  Í∞ïÎ∂ÅÍµ¨: [37.6396, 127.0257],
  ÎèÑÎ¥âÍµ¨: [37.6688, 127.0471],
  ÎÖ∏ÏõêÍµ¨: [37.6542, 127.0568],
  ÏùÄÌèâÍµ¨: [37.6176, 126.9227],
  ÏÑúÎåÄÎ¨∏Íµ¨: [37.5791, 126.9368],
  ÎßàÌè¨Íµ¨: [37.5665, 126.9018],
  ÏñëÏ≤úÍµ¨: [37.5169, 126.8665],
  Í∞ïÏÑúÍµ¨: [37.551, 126.8495],
  Íµ¨Î°úÍµ¨: [37.4954, 126.8874],
  Í∏àÏ≤úÍµ¨: [37.4599, 126.9001],
  ÏòÅÎì±Ìè¨Íµ¨: [37.5263, 126.8963],
  ÎèôÏûëÍµ¨: [37.5124, 126.9393],
  Í¥ÄÏïÖÍµ¨: [37.4784, 126.9516],
  ÏÑúÏ¥àÍµ¨: [37.4836, 127.0326],
  Í∞ïÎÇ®Íµ¨: [37.5173, 127.0473],
  ÏÜ°ÌååÍµ¨: [37.5112, 127.098],
  Í∞ïÎèôÍµ¨: [37.5301, 127.1238],
};

/* ===== Íµ¨Î™Ö Ï∂îÏ∂ú ===== */
const GU_REGEX =
  /(Ï¢ÖÎ°úÍµ¨|Ï§ëÍµ¨|Ïö©ÏÇ∞Íµ¨|ÏÑ±ÎèôÍµ¨|Í¥ëÏßÑÍµ¨|ÎèôÎåÄÎ¨∏Íµ¨|Ï§ëÎûëÍµ¨|ÏÑ±Î∂ÅÍµ¨|Í∞ïÎ∂ÅÍµ¨|ÎèÑÎ¥âÍµ¨|ÎÖ∏ÏõêÍµ¨|ÏùÄÌèâÍµ¨|ÏÑúÎåÄÎ¨∏Íµ¨|ÎßàÌè¨Íµ¨|ÏñëÏ≤úÍµ¨|Í∞ïÏÑúÍµ¨|Íµ¨Î°úÍµ¨|Í∏àÏ≤úÍµ¨|ÏòÅÎì±Ìè¨Íµ¨|ÎèôÏûëÍµ¨|Í¥ÄÏïÖÍµ¨|ÏÑúÏ¥àÍµ¨|Í∞ïÎÇ®Íµ¨|ÏÜ°ÌååÍµ¨|Í∞ïÎèôÍµ¨)/;


export default function MyPage() {
  const [recent, setRecent] = useState(() =>
    normalizeRecent(loadLS(LS_RECENT, []))
  );
  const [favs, setFavs] = useState(() => loadLS(LS_FAV, []));
  const [geoFavs, setGeoFavs] = useState([]);
  const [kakaoReady, setKakaoReady] = useState(false);

  // map refs
  const mapEl = useRef(null);
  const mapRef = useRef(null);
  const kakaoRef = useRef(null);
  const clusterRef = useRef(null);
  const markersRef = useRef([]);
  const infoRef = useRef([]);

  /* ÏßÄÎèÑ Ï¥àÍ∏∞Ìôî */
  useEffect(() => {
    let off = false;

    (async () => {
      const kakao = await loadKakao();
      if (off) return;

      kakaoRef.current = kakao;
      mapRef.current = new kakao.maps.Map(mapEl.current, {
        center: new kakao.maps.LatLng(37.5665, 126.978),
        level: 7,
      });

      setKakaoReady(true);
    })();

    return () => {
      off = true;
    };
  }, []);

  /* ====== Ï¶êÍ≤®Ï∞æÍ∏∞ ‚Üí Ï¢åÌëú Ï±ÑÏö∞Í∏∞ ======
     1ÏàúÏúÑ: LAT/LOT (API Ï†úÍ≥µ)
     2ÏàúÏúÑ: PLACE (Ï†ïÌôïÌïú Ïû•ÏÜåÎ™Ö)
     3ÏàúÏúÑ: TITLE (Î∂ÄÎìúÎü¨Ïö¥ fallback)
     4ÏàúÏúÑ: GU Ï§ëÏã¨
     5ÏàúÏúÑ: ÏÑúÏö∏ÏãúÏ≤≠
  */
  useEffect(() => {
    const kakao = kakaoRef.current;
    if (!kakaoReady || !kakao) return;
    if (favs.length === 0) {
      setGeoFavs([]);
      return;
    }

    const ps = new kakao.maps.services.Places();
    const geocoder = new kakao.maps.services.Geocoder();
    const cache = loadGeoCache();
    const SEOUL_CENTER = new kakao.maps.LatLng(37.5665, 126.978);

    let cancelled = false;

    const pickSeoul = (data, gu) => {
      const seoulMatches = data.filter((d) =>
        (d.address_name || "").startsWith("ÏÑúÏö∏")
      );
      if (gu) {
        const inGu = seoulMatches.filter((d) =>
          d.address_name.includes(gu)
        );
        return inGu[0] || seoulMatches[0] || null;
      }
      return seoulMatches[0] || null;
    };

    const kwSearch = (keyword, gu) =>
      new Promise((res) => {
        if (!keyword) return res(null);
        ps.keywordSearch(
          keyword,
          (data, status) => {
            if (status === kakao.maps.services.Status.OK && data.length) {
              const best = pickSeoul(data, gu);
              if (best)
                return res({
                  lat: Number(best.y),
                  lng: Number(best.x),
                });
            }
            res(null);
          },
          { location: SEOUL_CENTER, radius: 60000 }
        );
      });

    const addrSearch = (addr) =>
      new Promise((res) => {
        if (!addr) return res(null);
        geocoder.addressSearch(addr, (result, status) => {
          if (status === kakao.maps.services.Status.OK && result[0]) {
            const match =
              result.find((r) => r.address_name.startsWith("ÏÑúÏö∏")) || result[0];
            return res({
              lat: Number(match.y),
              lng: Number(match.x),
            });
          }
          res(null);
        });
      });

    (async () => {
      const out = [];

      for (const f of favs) {
        // 1) Ïù¥ÎØ∏ LAT/LNG ÏûàÏúºÎ©¥ Í∑∏ÎåÄÎ°ú
        if (typeof f.lat === "number" && typeof f.lng === "number") {
          out.push(f);
          continue;
        }

        // 2) Ï∫êÏãú ÌôïÏù∏
        const key = `${f.place}|${f.title}`;
        if (cache[key]) {
          out.push({ ...f, ...cache[key] });
          continue;
        }

        // Íµ¨ Ï∂îÏ∂ú
        let gu = f.gu;
        if (!gu && f.place) {
          const m = f.place.match(GU_REGEX);
          if (m) gu = m[1];
        }

        // 3) PLACE Î®ºÏ†Ä Í≤ÄÏÉâ (Ï†ïÌôïÎèÑ ÎÜíÏùå)
        let coords = null;
        if (f.place) {
          coords = await kwSearch(f.place, gu);
          if (!coords) coords = await addrSearch(f.place);
        }

        // 4) TITLE Í≤ÄÏÉâ
        if (!coords && f.title) {
          coords = await kwSearch(f.title, gu);
        }

        // 5) GU Ï§ëÏã¨
        if (!coords && gu && GU_CENTER[gu]) {
          coords = { lat: GU_CENTER[gu][0], lng: GU_CENTER[gu][1] };
        }

        // 6) ÏÑúÏö∏ÏãúÏ≤≠ fallback
        if (!coords) coords = { lat: 37.5665, lng: 126.978 };

        cache[key] = coords;
        out.push({ ...f, ...coords });

        if (cancelled) return;
        await new Promise((r) => setTimeout(r, 30));
      }

      saveGeoCache(cache);
      if (!cancelled) setGeoFavs(out);
    })();

    return () => {
      cancelled = true;
    };
  }, [favs, kakaoReady]);

  /* ===== ÏßÄÎèÑ ÎßàÏª§ ÏÉùÏÑ± ===== */
  useEffect(() => {
    const kakao = kakaoRef.current;
    const map = mapRef.current;
    if (!kakao || !map) return;

    // cleanup
    markersRef.current.forEach((m) => m.setMap(null));
    infoRef.current.forEach((i) => i.close());
    markersRef.current = [];
    infoRef.current = [];
    if (clusterRef.current) {
      clusterRef.current.clear();
      clusterRef.current = null;
    }

    const list = geoFavs.filter((f) => f.lat && f.lng);
    if (list.length === 0) return;

    const markers = [];
    const infos = [];
    const bounds = new kakao.maps.LatLngBounds();

    list.forEach((f) => {
      const pos = new kakao.maps.LatLng(f.lat, f.lng);
      bounds.extend(pos);

      const marker = new kakao.maps.Marker({
        position: pos,
        title: f.title,
      });

      kakao.maps.event.addListener(marker, "click", () => {
        if (f.homepage) {
          addRecent(f);
          openHomepage(f.homepage);
        }
      });

      const html = `
        <div style="padding:8px;font-size:12px;">
          <b>${f.title}</b><br/>
          ${f.place || ""}<br/>
          ${f.date || ""} ¬∑ ${f.category || ""}
        </div>`;

      const iw = new kakao.maps.InfoWindow({ content: html });

      kakao.maps.event.addListener(marker, "mouseover", () => {
        infos.forEach((i) => i.close());
        iw.open(map, marker);
      });

      kakao.maps.event.addListener(marker, "mouseout", () => iw.close());

      markers.push(marker);
      infos.push(iw);
    });

    markersRef.current = markers;
    infoRef.current = infos;

    const clusterer = new kakao.maps.MarkerClusterer({
      map,
      averageCenter: true,
      minLevel: 6,
    });

    clusterer.addMarkers(markers);
    clusterRef.current = clusterer;

    map.setBounds(bounds);
  }, [geoFavs]);
  /* ===== Storage & Custom Events ===== */
  useEffect(() => {
    const onStorage = (e) => {
      if (e.key === LS_RECENT)
        setRecent(normalizeRecent(loadLS(LS_RECENT, [])));

      if (e.key === LS_FAV) setFavs(loadLS(LS_FAV, []));
    };

    const onRecentEvent = () =>
      setRecent(normalizeRecent(loadLS(LS_RECENT, [])));

    window.addEventListener("storage", onStorage);
    window.addEventListener("sn:recent-updated", onRecentEvent);

    return () => {
      window.removeEventListener("storage", onStorage);
      window.removeEventListener("sn:recent-updated", onRecentEvent);
    };
  }, []);

  /* ===== Ï¶êÍ≤®Ï∞æÍ∏∞ ÌÜ†Í∏Ä ===== */
  const toggleFav = (item) => {
    const list = loadLS(LS_FAV, []);
    const exists = list.some((x) => x.id === item.id);

    const next = exists
      ? list.filter((x) => x.id !== item.id)
      : [...list, item];

    saveLS(LS_FAV, next);
    setFavs(next);
  };

  /* ===== ÏµúÍ∑º Î≥∏ Ìï≠Î™© ÏàòÎèô ÏÉàÎ°úÍ≥†Ïπ® ===== */
  const refreshRecent = () =>
    setRecent(normalizeRecent(loadLS(LS_RECENT, [])));

  return (
    <div className="min-h-screen bg-white px-6 py-8 max-w-7xl mx-auto">
      <div className="grid grid-cols-1 lg:grid-cols-12 gap-8">

        {/* ====================== ÏµúÍ∑º Î≥∏ Ìï≠Î™© ====================== */}
        <section className="lg:col-span-6">
          <div className="flex items-center justify-between mb-3">
            <h2 className="text-lg font-semibold">ÏµúÍ∑º Î≥∏ Ìï≠Î™©</h2>

            <div className="flex items-center gap-3">
              <button
                className="text-xs underline text-gray-500"
                onClick={refreshRecent}
              >
                ÏÉàÎ°úÍ≥†Ïπ®
              </button>

              {recent.length > 0 && (
                <button
                  className="text-xs underline text-gray-500"
                  onClick={() => {
                    saveLS(LS_RECENT, []);
                    setRecent([]);
                  }}
                >
                  ÎπÑÏö∞Í∏∞
                </button>
              )}
            </div>
          </div>

          {recent.length === 0 ? (
            <div className="h-[420px] grid place-items-center bg-gray-100 border rounded text-gray-500">
              ÏµúÍ∑º Î≥∏ Ìï≠Î™©Ïù¥ ÏóÜÏäµÎãàÎã§.
            </div>
          ) : (
            <div className="border rounded p-3 h-[420px] overflow-auto">
              <ul className="space-y-2">
                {recent.map((it) => {
                  const isFav = favs.some((f) => f.id === it.id);

                  return (
                    <li key={it.id} className="flex items-start gap-2">
                      {/* ‚ù§Ô∏è Ï¶êÍ≤®Ï∞æÍ∏∞ ÌÜ†Í∏Ä */}
                      <button
                        onClick={() => toggleFav(it)}
                        className={`text-xl leading-none ${
                          isFav ? "text-red-500" : "text-gray-400"
                        }`}
                        title="Ï¶êÍ≤®Ï∞æÍ∏∞"
                      >
                        {isFav ? "‚ù§Ô∏è" : "ü§ç"}
                      </button>

                      {/* Ìï≠Î™© ÎÇ¥Ïö© */}
                      {it.homepage ? (
                        <a
                          href={it.homepage}
                          target="_blank"
                          rel="noreferrer"
                          onClick={() => addRecent(it)}
                          className="w-full block text-left px-3 py-2 rounded hover:bg-gray-50 border"
                        >
                          <div className="font-medium truncate">{it.title}</div>

                          <div className="text-xs text-gray-600 truncate">
                            üìÖ {it.date || "ÏùºÏ†ï ÎØ∏Ï†ï"} ¬∑ üìç{" "}
                            {it.place || "Ïû•ÏÜå ÎØ∏Ï†ï"} ¬∑ {it.category}
                          </div>
                        </a>
                      ) : (
                        <div className="w-full text-left px-3 py-2 rounded border text-xs text-gray-500">
                          ÎßÅÌÅ¨ Ï†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.
                        </div>
                      )}
                    </li>
                  );
                })}
              </ul>
            </div>
          )}
        </section>

        {/* ======================== Ï¶êÍ≤®Ï∞æÍ∏∞ & ÏßÄÎèÑ ======================== */}
        <section className="lg:col-span-6">
          <div className="flex items-center justify-between mb-3">
            <h2 className="text-lg font-semibold">My Favorite</h2>

            {favs.length > 0 && (
              <button
                className="text-xs underline text-gray-500"
                onClick={() => {
                  saveLS(LS_FAV, []);
                  setFavs([]);
                }}
              >
                Ï†ÑÏ≤¥ Ìï¥Ï†ú
              </button>
            )}
          </div>

          {/* ÏßÄÎèÑ */}
          <div
            ref={mapEl}
            className="relative border rounded-lg bg-gray-100 h-[420px] overflow-hidden"
          >
            {!kakaoReady && (
              <div className="absolute inset-0 grid place-items-center text-gray-500 text-sm">
                ÏßÄÎèÑÎ•º Î∂àÎü¨Ïò§Îäî Ï§ë‚Ä¶
              </div>
            )}
          </div>

          {/* Ï¶êÍ≤®Ï∞æÍ∏∞ Î¶¨Ïä§Ìä∏ */}
          <ul className="mt-3 text-sm text-gray-700 space-y-1">
            {favs.map((f) => (
              <li key={f.id} className="flex items-center gap-2 px-2 py-1 rounded">
                {/* ‚ù§Ô∏è Ï¶êÍ≤®Ï∞æÍ∏∞ */}
                <button
                  onClick={() => toggleFav(f)}
                  title="Ï¶êÍ≤®Ï∞æÍ∏∞ Ìï¥Ï†ú"
                  className="leading-none text-red-500"
                >
                  ‚ù§Ô∏è
                </button>

                {f.homepage ? (
                  <a
                    href={f.homepage}
                    target="_blank"
                    rel="noreferrer"
                    onClick={() => addRecent(f)}
                    className="truncate hover:underline"
                  >
                    <b>{f.title}</b> ¬∑{" "}
                    {f.date || "ÏùºÏ†ï ÎØ∏Ï†ï"} ¬∑{" "}
                    {f.place || "Ïû•ÏÜå ÎØ∏Ï†ï"} ({f.category})
                  </a>
                ) : (
                  <span className="truncate text-gray-500">
                    <b>{f.title}</b> ¬∑{" "}
                    {f.date || "ÏùºÏ†ï ÎØ∏Ï†ï"} ¬∑{" "}
                    {f.place || "Ïû•ÏÜå ÎØ∏Ï†ï"} ({f.category}) ‚Äî ÎßÅÌÅ¨ ÏóÜÏùå
                  </span>
                )}
              </li>
            ))}

            {favs.length === 0 && (
              <li className="text-gray-500">
                Ï¶êÍ≤®Ï∞æÍ∏∞Ìïú ÌñâÏÇ¨Í∞Ä ÏóÜÏäµÎãàÎã§.
              </li>
            )}
          </ul>
        </section>
      </div>
    </div>
  );
}
