// src/pages/MyPage.jsx
import React, { useEffect, useRef, useState } from "react";

/* ===== LocalStorage ===== */
const LS_FAV = "sn_favorites"; // [{id,title,place,date,category,homepage?,gu?,lat?,lng?}]
const LS_RECENT = "sn_recent";

const loadLS = (k, fb = []) => {
  try {
    return JSON.parse(localStorage.getItem(k) || JSON.stringify(fb));
  } catch {
    return fb;
  }
};
const saveLS = (k, v) => localStorage.setItem(k, JSON.stringify(v));

/* ÏµúÍ∑º Î≥∏ Ìï≠Î™© Ï†ÄÏû•(ÌÉÄÏûÑÏä§ÌÉ¨ÌîÑ Ìè¨Ìï®, Ï§ëÎ≥µ Ï†úÍ±∞) + Í∞ôÏùÄ ÌÉ≠ Ïã§ÏãúÍ∞Ñ Í∞±Ïã† Ïù¥Î≤§Ìä∏ */
export function addRecent(item) {
  const now = new Date().toISOString();
  const base = {
    id: item.id ?? String(item.id ?? ""),
    title: item.title ?? "",
    date: item.date ?? "",
    place: item.place ?? "",
    category: item.category ?? "",
    thumb: item.thumb ?? "",
    homepage: item.homepage ?? "",
    gu: item.gu ?? "",
    seenAt: now,
  };
  const arr = loadLS(LS_RECENT, []);
  const withoutDup = arr.filter((x) => x.id !== base.id);
  const next = [base, ...withoutDup].slice(0, 10);
  saveLS(LS_RECENT, next);
  try {
    window.dispatchEvent(new Event("sn:recent-updated"));
  } catch {}
  return next;
}

/* ===== Kakao Maps loader ===== */
const FALLBACK_APPKEY = "2ee5022c1da6fc178bd51ad4042556fb";
const loadKakao = () =>
  new Promise((resolve, reject) => {
    if (window.kakao && window.kakao.maps) {
      resolve(window.kakao);
      return;
    }
    const key = process.env.REACT_APP_KAKAO_MAP_KEY || FALLBACK_APPKEY;
    const ID = "kakao-maps-sdk";
    const exist = document.getElementById(ID);
    const onLoaded = () => {
      try {
        window.kakao.maps.load(() => resolve(window.kakao));
      } catch (e) {
        reject(e);
      }
    };
    if (exist) {
      exist.addEventListener("load", onLoaded, { once: true });
      exist.addEventListener("error", reject, { once: true });
      return;
    }
    const s = document.createElement("script");
    s.id = ID;
    s.async = true;
    s.src = `https://dapi.kakao.com/v2/maps/sdk.js?appkey=${key}&libraries=clusterer&autoload=false`;
    s.onload = onLoaded;
    s.onerror = reject;
    document.head.appendChild(s);
  });

/* ===== Ï¢åÌëú Ï∫êÏãú ===== */
const LS_GEO = "sn_geo_cache_v1";
const loadGeoCache = () => {
  try {
    return JSON.parse(localStorage.getItem(LS_GEO) || "{}");
  } catch {
    return {};
  }
};
const saveGeoCache = (obj) =>
  localStorage.setItem(LS_GEO, JSON.stringify(obj));

/* ===== Íµ¨ Ï§ëÏã¨(Î∞±ÏóÖ) ===== */
const GU_CENTER = {
  Ï¢ÖÎ°úÍµ¨: [37.573, 126.9794],
  Ï§ëÍµ¨: [37.5636, 126.9976],
  Ïö©ÏÇ∞Íµ¨: [37.5326, 126.9905],
  ÏÑ±ÎèôÍµ¨: [37.5636, 127.0364],
  Í¥ëÏßÑÍµ¨: [37.5386, 127.0822],
  ÎèôÎåÄÎ¨∏Íµ¨: [37.5744, 127.0396],
  Ï§ëÎûëÍµ¨: [37.606, 127.0929],
  ÏÑ±Î∂ÅÍµ¨: [37.5894, 127.0167],
  Í∞ïÎ∂ÅÍµ¨: [37.6396, 127.0257],
  ÎèÑÎ¥âÍµ¨: [37.6688, 127.0471],
  ÎÖ∏ÏõêÍµ¨: [37.6542, 127.0568],
  ÏùÄÌèâÍµ¨: [37.6176, 126.9227],
  ÏÑúÎåÄÎ¨∏Íµ¨: [37.5791, 126.9368],
  ÎßàÌè¨Íµ¨: [37.5665, 126.9018],
  ÏñëÏ≤úÍµ¨: [37.5169, 126.8665],
  Í∞ïÏÑúÍµ¨: [37.551, 126.8495],
  Íµ¨Î°úÍµ¨: [37.4954, 126.8874],
  Í∏àÏ≤úÍµ¨: [37.4599, 126.9001],
  ÏòÅÎì±Ìè¨Íµ¨: [37.5263, 126.8963],
  ÎèôÏûëÍµ¨: [37.5124, 126.9393],
  Í¥ÄÏïÖÍµ¨: [37.4784, 126.9516],
  ÏÑúÏ¥àÍµ¨: [37.4836, 127.0326],
  Í∞ïÎÇ®Íµ¨: [37.5173, 127.0473],
  ÏÜ°ÌååÍµ¨: [37.5112, 127.098],
  Í∞ïÎèôÍµ¨: [37.5301, 127.1238],
};

/* ===== ÏµúÍ∑º Î≥∏ Îç∞Ïù¥ÌÑ∞ Ï†ïÎ¶¨ ===== */
function normalizeRecent(rawArr) {
  const arr = Array.isArray(rawArr) ? rawArr : [];
  const fixed = arr.map((x) => ({
    id: String(x.id ?? ""),
    title: x.title ?? "",
    date: x.date ?? "",
    place: x.place ?? "",
    category: x.category ?? "",
    thumb: x.thumb ?? "",
    homepage: x.homepage ?? "",
    gu: x.gu ?? "",
    seenAt: x.seenAt ?? new Date().toISOString(),
  }));
  const valid = fixed.filter((x) => x.id && x.title);
  const dedup = [];
  const vis = new Set();
  for (const it of valid) {
    if (vis.has(it.id)) continue;
    vis.add(it.id);
    dedup.push(it);
  }
  dedup.sort((a, b) => new Date(b.seenAt) - new Date(a.seenAt));
  return dedup.slice(0, 50);
}

/* ==== Ïô∏Î∂Ä ÏÉÅÏÑ∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô ==== */
function openHomepage(url) {
  if (!url) return;
  window.open(url, "_blank", "noopener,noreferrer");
}

/* ===== Íµ¨ Ïù¥Î¶Ñ Ï∂îÏ∂úÏö© Ï†ïÍ∑úÏãù ===== */
const GU_REGEX =
  /(Ï¢ÖÎ°úÍµ¨|Ï§ëÍµ¨|Ïö©ÏÇ∞Íµ¨|ÏÑ±ÎèôÍµ¨|Í¥ëÏßÑÍµ¨|ÎèôÎåÄÎ¨∏Íµ¨|Ï§ëÎûëÍµ¨|ÏÑ±Î∂ÅÍµ¨|Í∞ïÎ∂ÅÍµ¨|ÎèÑÎ¥âÍµ¨|ÎÖ∏ÏõêÍµ¨|ÏùÄÌèâÍµ¨|ÏÑúÎåÄÎ¨∏Íµ¨|ÎßàÌè¨Íµ¨|ÏñëÏ≤úÍµ¨|Í∞ïÏÑúÍµ¨|Íµ¨Î°úÍµ¨|Í∏àÏ≤úÍµ¨|ÏòÅÎì±Ìè¨Íµ¨|ÎèôÏûëÍµ¨|Í¥ÄÏïÖÍµ¨|ÏÑúÏ¥àÍµ¨|Í∞ïÎÇ®Íµ¨|ÏÜ°ÌååÍµ¨|Í∞ïÎèôÍµ¨)/;

export default function MyPage() {
  const [recent, setRecent] = useState(() =>
    normalizeRecent(loadLS(LS_RECENT, []))
  );
  const [favs, setFavs] = useState(() => loadLS(LS_FAV, []));
  const [geoFavs, setGeoFavs] = useState([]);
  const [kakaoReady, setKakaoReady] = useState(false);

  // Kakao map refs
  const mapEl = useRef(null);
  const mapRef = useRef(null);
  const kakaoRef = useRef(null);
  const clusterRef = useRef(null);
  const markersRef = useRef([]);
  const infoRef = useRef([]);

  /* ÏßÄÎèÑ Ï¥àÍ∏∞Ìôî */
  useEffect(() => {
    let off = false;
    (async () => {
      const kakao = await loadKakao();
      if (off) return;
      kakaoRef.current = kakao;
      const center = new kakao.maps.LatLng(37.5665, 126.978);
      mapRef.current = new kakao.maps.Map(mapEl.current, {
        center,
        level: 7,
      });
      setKakaoReady(true);
    })();
    return () => {
      off = true;
    };
  }, []);

  /* Ï¶êÍ≤®Ï∞æÍ∏∞ ‚Üí Ï¢åÌëú Ï±ÑÏö∞Í∏∞ (Íµ¨ Îã®ÏúÑÎ°ú ÌôïÏã§ÌïòÍ≤å Îß§Ìïë) */
  useEffect(() => {
    const kakao = kakaoRef.current;
    if (!kakaoReady || !kakao) return;

    if (!favs || favs.length === 0) {
      setGeoFavs([]);
      return;
    }

    const cache = loadGeoCache();
    let cancelled = false;

    (async () => {
      const out = [];
      for (const f of favs) {
        // Ïù¥ÎØ∏ lat/lng ÏûàÏúºÎ©¥ Í∑∏ÎåÄÎ°ú ÏÇ¨Ïö©
        if (typeof f.lat === "number" && typeof f.lng === "number") {
          out.push(f);
          continue;
        }

        // Ï∫êÏãú ÌôïÏù∏
        const cacheKey = `${f.gu || ""}|${f.place || ""}`;
        if (cache[cacheKey]) {
          out.push({ ...f, ...cache[cacheKey] });
          continue;
        }

        let gu = f.gu;
        if (!gu && f.place) {
          const m = f.place.match(GU_REGEX);
          if (m) gu = m[1];
        }

        let coords = null;
        if (gu && GU_CENTER[gu]) {
          const [lat, lng] = GU_CENTER[gu];
          coords = { lat, lng };
        }

        // Íµ¨ Ï†ïÎ≥¥Í∞Ä Ï†ÑÌòÄ ÏóÜÏúºÎ©¥ ÏÑúÏö∏ÏãúÏ≤≠ Í∏∞Ï§Ä
        if (!coords) {
          coords = { lat: 37.5665, lng: 126.978 };
        }

        cache[cacheKey] = coords;
        out.push({ ...f, ...coords });

        if (cancelled) return;
      }
      saveGeoCache(cache);
      if (!cancelled) setGeoFavs(out);
    })();

    return () => {
      cancelled = true;
    };
  }, [favs, kakaoReady]);

  /* ÎßàÏª§ + ÌÅ¥Îü¨Ïä§ÌÑ∞ + Ìà¥ÌåÅ + ÌÅ¥Î¶≠ ‚Üí Ïô∏Î∂Ä ÎßÅÌÅ¨ + ÏµúÍ∑º Î≥∏ Ìï≠Î™© Í∏∞Î°ù */
  useEffect(() => {
    const kakao = kakaoRef.current;
    const map = mapRef.current;
    if (!kakao || !map) return;

    // cleanup
    markersRef.current.forEach((m) => m.setMap(null));
    infoRef.current.forEach((i) => i.close());
    markersRef.current = [];
    infoRef.current = [];
    if (clusterRef.current) {
      clusterRef.current.clear();
      clusterRef.current = null;
    }

    const list = geoFavs.filter(
      (f) => f.lat != null && f.lng != null
    );
    if (list.length === 0) return;

    const markers = [];
    const infos = [];
    const bounds = new kakao.maps.LatLngBounds();

    list.forEach((f) => {
      const pos = new kakao.maps.LatLng(f.lat, f.lng);
      bounds.extend(pos);

      const marker = new kakao.maps.Marker({
        position: pos,
        title: f.title,
      });

      kakao.maps.event.addListener(marker, "click", () => {
        if (f.homepage) {
          addRecent(f);
          openHomepage(f.homepage);
        }
      });

      const iwHtml = `
        <div style="padding:8px 10px; font-size:12px; max-width:220px;">
          <div style="font-weight:600; margin-bottom:4px;">${f.title}</div>
          <div style="color:#666;">${f.place || ""}</div>
          <div style="color:#888; margin-top:2px;">${f.date || ""} ¬∑ ${
        f.category || ""
      }</div>
        </div>`;
      const iw = new kakao.maps.InfoWindow({ content: iwHtml });
      kakao.maps.event.addListener(marker, "mouseover", () => {
        infos.forEach((i) => i.close());
        iw.open(map, marker);
      });
      kakao.maps.event.addListener(marker, "mouseout", () =>
        iw.close()
      );

      markers.push(marker);
      infos.push(iw);
    });

    markersRef.current = markers;
    infoRef.current = infos;

    const clusterer = new kakao.maps.MarkerClusterer({
      map,
      averageCenter: true,
      minLevel: 6,
      disableClickZoom: false,
    });
    clusterer.addMarkers(markers);
    clusterRef.current = clusterer;

    map.setBounds(bounds, 40, 40, 40, 40);
  }, [geoFavs]);

  // Ïä§ÌÜ†Î¶¨ÏßÄ/Ïª§Ïä§ÌÖÄ Ïù¥Î≤§Ìä∏ Î∞òÏòÅ
  useEffect(() => {
    const onStorage = (e) => {
      if (e.key === LS_RECENT)
        setRecent(normalizeRecent(loadLS(LS_RECENT, [])));
      if (e.key === LS_FAV) setFavs(loadLS(LS_FAV, []));
    };
    const onRecentEvent = () =>
      setRecent(normalizeRecent(loadLS(LS_RECENT, [])));
    window.addEventListener("storage", onStorage);
    window.addEventListener("sn:recent-updated", onRecentEvent);
    return () => {
      window.removeEventListener("storage", onStorage);
      window.removeEventListener(
        "sn:recent-updated",
        onRecentEvent
      );
    };
  }, []);

  /* Ï¶êÍ≤®Ï∞æÍ∏∞ ÌÜ†Í∏Ä */
  const toggleFav = (item) => {
    const list = loadLS(LS_FAV, []);
    const exists = list.some((x) => x.id === item.id);
    const next = exists
      ? list.filter((x) => x.id !== item.id)
      : [...list, item];
    saveLS(LS_FAV, next);
    setFavs(next);
  };

  /* ÏµúÍ∑º Î™©Î°ù ÏàòÎèô ÏÉàÎ°úÍ≥†Ïπ® */
  const refreshRecent = () =>
    setRecent(normalizeRecent(loadLS(LS_RECENT, [])));

  return (
    <div className="min-h-screen bg-white px-6 py-8 max-w-7xl mx-auto">
      <div className="grid grid-cols-1 lg:grid-cols-12 gap-8">
        {/* ÏµúÍ∑º Î≥∏ Ìï≠Î™© */}
        <section className="lg:col-span-6">
          <div className="flex items-center justify-between mb-3">
            <h2 className="text-lg font-semibold">ÏµúÍ∑º Î≥∏ Ìï≠Î™©</h2>
            <div className="flex items-center gap-3">
              <button
                className="text-xs underline text-gray-500"
                onClick={refreshRecent}
                title="ÏÉàÎ°úÍ≥†Ïπ®"
              >
                ÏÉàÎ°úÍ≥†Ïπ®
              </button>
              {recent.length > 0 && (
                <button
                  className="text-xs underline text-gray-500"
                  onClick={() => {
                    saveLS(LS_RECENT, []);
                    setRecent([]);
                  }}
                >
                  ÎπÑÏö∞Í∏∞
                </button>
              )}
            </div>
          </div>
          {recent.length === 0 ? (
            <div className="h-[420px] grid place-items-center bg-gray-100 border rounded text-gray-500">
              ÏµúÍ∑º Î≥∏ Ìï≠Î™©Ïù¥ ÏóÜÏäµÎãàÎã§.
            </div>
          ) : (
            <div className="border rounded p-3 h-[420px] overflow-auto">
              <ul className="space-y-2">
                {recent.map((it) => (
                  <li key={it.id}>
                    {it.homepage ? (
                      <a
                        href={it.homepage}
                        target="_blank"
                        rel="noreferrer"
                        onClick={() => addRecent(it)}
                        className="w-full block text-left px-3 py-2 rounded hover:bg-gray-50 border"
                      >
                        <div className="font-medium truncate">
                          {it.title}
                        </div>
                        <div className="text-xs text-gray-600 truncate">
                          üìÖ {it.date || "ÏùºÏ†ï ÎØ∏Ï†ï"} ¬∑ üìç{" "}
                          {it.place || "Ïû•ÏÜå ÎØ∏Ï†ï"} ¬∑{" "}
                          {it.category || ""}
                        </div>
                      </a>
                    ) : (
                      <div className="w-full text-left px-3 py-2 rounded border text-xs text-gray-500">
                        ÎßÅÌÅ¨ Ï†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.
                      </div>
                    )}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </section>

        {/* My Favorite (ÏßÄÎèÑ) */}
        <section className="lg:col-span-6">
          <div className="flex items-center justify-between mb-3">
            <h2 className="text-lg font-semibold">My Favorite</h2>
            {favs.length > 0 && (
              <button
                className="text-xs underline text-gray-500"
                onClick={() => {
                  saveLS(LS_FAV, []);
                  setFavs([]);
                }}
              >
                Ï†ÑÏ≤¥ Ìï¥Ï†ú
              </button>
            )}
          </div>

          <div
            ref={mapEl}
            className="relative border rounded-lg bg-gray-100 h-[420px] overflow-hidden"
            aria-label="Ï¶êÍ≤®Ï∞æÍ∏∞ ÏßÄÎèÑ"
            role="region"
          >
            {!kakaoReady && (
              <div className="absolute inset-0 grid place-items-center text-gray-500 text-sm">
                ÏßÄÎèÑÎ•º Î∂àÎü¨Ïò§Îäî Ï§ë‚Ä¶
              </div>
            )}
          </div>

          {/* Ï¶êÍ≤®Ï∞æÍ∏∞ Í∞ÑÎã® Î¶¨Ïä§Ìä∏ */}
          <ul className="mt-3 text-sm text-gray-700 space-y-1">
            {favs.map((f) => (
              <li
                key={f.id}
                className="flex items-center gap-2 px-2 py-1 rounded"
              >
                <button
                  onClick={() => toggleFav(f)}
                  title="Ï¶êÍ≤®Ï∞æÍ∏∞ Ìï¥Ï†ú"
                  aria-label="Ï¶êÍ≤®Ï∞æÍ∏∞ Ìï¥Ï†ú"
                  className="leading-none"
                >
                  ‚ù§Ô∏è
                </button>

                {f.homepage ? (
                  <a
                    href={f.homepage}
                    target="_blank"
                    rel="noreferrer"
                    onClick={() => addRecent(f)}
                    className="truncate text-left hover:underline"
                  >
                    <b>{f.title}</b> ¬∑{" "}
                    {f.date || "ÏùºÏ†ï ÎØ∏Ï†ï"} ¬∑{" "}
                    {f.place || "Ïû•ÏÜå ÎØ∏Ï†ï"} (
                    {f.category || ""})
                  </a>
                ) : (
                  <span className="truncate text-gray-500">
                    <b>{f.title}</b> ¬∑{" "}
                    {f.date || "ÏùºÏ†ï ÎØ∏Ï†ï"} ¬∑{" "}
                    {f.place || "Ïû•ÏÜå ÎØ∏Ï†ï"} (
                    {f.category || ""}) ‚Äî ÎßÅÌÅ¨ ÏóÜÏùå
                  </span>
                )}
              </li>
            ))}
            {favs.length === 0 && (
              <li className="text-gray-500">
                Ï¶êÍ≤®Ï∞æÍ∏∞Ìïú ÌñâÏÇ¨Í∞Ä ÏóÜÏäµÎãàÎã§.
              </li>
            )}
          </ul>
        </section>
      </div>
    </div>
  );
}
