// src/pages/Favorites.jsx
import React, { useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";

const LS_FAV = "sn_favorites";
const loadFavs = () => {
  try {
    return JSON.parse(localStorage.getItem(LS_FAV) || "[]");
  } catch {
    return [];
  }
};

const CATEGORIES = ["Í≥µÏó∞", "Ï†ÑÏãú", "ÍµêÏú°/Ï≤¥Ìóò", "Í∏∞ÌÉÄ"];

export default function Favorites() {
  const [favs, setFavs] = useState(loadFavs());
  const [category, setCategory] = useState("");
  const [q, setQ] = useState("");
  const navigate = useNavigate();

  const list = useMemo(() => {
    let arr = favs;
    if (category) arr = arr.filter((f) => f.category === category);
    if (q) {
      const k = q.trim().toLowerCase();
      arr = arr.filter(
        (f) =>
          String(f.title || "").toLowerCase().includes(k) ||
          String(f.place || "").toLowerCase().includes(k)
      );
    }
    return arr;
  }, [favs, category, q]);

  return (
    <div className="min-h-screen bg-white px-6 py-8 max-w-6xl mx-auto">
      <h2 className="text-2xl font-semibold mb-4">Favorites</h2>

      {/* Ïπ¥ÌÖåÍ≥†Î¶¨ ÏÑ†ÌÉù ÌôîÎ©¥ */}
      {!category && (
        <div>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-3">
            {CATEGORIES.map((c) => (
              <button
                key={c}
                onClick={() => setCategory(c)}
                className="rounded-xl border p-6 hover:shadow text-center"
              >
                {c}
              </button>
            ))}
          </div>
        </div>
      )}

      {/* ÏÑ†ÌÉùÎêú Ïπ¥ÌÖåÍ≥†Î¶¨ ÌôîÎ©¥ */}
      {category && (
        <div className="mt-4">
          <div className="flex items-center gap-2">
            <button
              onClick={() => setCategory("")}
              className="px-3 py-1.5 rounded-lg border hover:bg-gray-50"
            >
              ‚Üê Îí§Î°ú
            </button>
            <div className="font-semibold">{category}</div>
            <input
              value={q}
              onChange={(e) => setQ(e.target.value)}
              placeholder="Í≤ÄÏÉâÏñ¥"
              className="px-3 py-2 rounded-lg border ml-auto"
            />
          </div>

          <ul className="divide-y border rounded-lg mt-3">
            {list.length === 0 && (
              <li className="p-4 text-sm text-gray-500">
                Ìï¥Îãπ Ïπ¥ÌÖåÍ≥†Î¶¨Ïóê Ï¶êÍ≤®Ï∞æÍ∏∞Í∞Ä ÏóÜÏñ¥Ïöî.
              </li>
            )}
            {list.map((it) => (
              <li
                key={it.id}
                className="p-4 hover:bg-gray-50 cursor-pointer"
                onClick={() => navigate(`/events/${it.id}`, { state: { event: it } })}
              >
                <div className="font-medium">{it.title}</div>
                <div className="text-xs text-gray-600">
                  üìÖ {it.date || "-"} ¬∑ üìç {it.place || "-"}
                </div>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
