// src/pages/MyPage.jsx
import React, { useEffect, useMemo, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";

/* ===== LocalStorage Keys ===== */
const LS_RECENTS = "sn_recents";
const LS_FAV = "sn_favorites";

/* ===== Recent Helpers (export for other pages) ===== */
export function loadRecents() {
  try {
    return JSON.parse(localStorage.getItem(LS_RECENTS) || "[]");
  } catch {
    return [];
  }
}

export function addRecent(item) {
  // item: {id,title,date,place,category,thumb,homepage,gu,lat,lng}
  if (!item || !item.id) return;
  const now = Date.now();
  let arr = loadRecents().filter((x) => x.id !== item.id);
  arr.unshift({ ...item, _ts: now });
  if (arr.length > 10) arr = arr.slice(0, 10);
  localStorage.setItem(LS_RECENTS, JSON.stringify(arr));
}

export function clearRecents() {
  localStorage.removeItem(LS_RECENTS);
}

/* ===== Favorites Helpers ===== */
export function loadFavs() {
  try {
    return JSON.parse(localStorage.getItem(LS_FAV) || "[]");
  } catch {
    return [];
  }
}

export default function MyPage() {
  const [recent, setRecent] = useState(loadRecents());
  const [favs, setFavs] = useState(loadFavs());
  const mapEl = useRef(null);
  const kakaoRef = useRef(null);
  const mapRef = useRef(null);
  const favMarkersRef = useRef([]);
  const navigate = useNavigate();

  /* ===== Kakao Map Loader ===== */
  useEffect(() => {
    if (window.kakao && window.kakao.maps) {
      kakaoRef.current = window.kakao;
      return;
    }
    const script = document.createElement("script");
    // TODO: Î≥∏Ïù∏ Ïï±ÌÇ§ ÏÇΩÏûÖ
    script.src =
      "https://dapi.kakao.com/v2/maps/sdk.js?appkey=YOUR_APP_KEY&autoload=false";
    script.async = true;
    script.onload = () => {
      window.kakao.maps.load(() => {
        kakaoRef.current = window.kakao;
      });
    };
    document.body.appendChild(script);
  }, []);

  /* ===== Map Init ===== */
  useEffect(() => {
    const kakao = kakaoRef.current;
    if (!kakao || !mapEl.current || mapRef.current) return;

    mapRef.current = new kakao.maps.Map(mapEl.current, {
      center: new kakao.maps.LatLng(37.5665, 126.9780),
      level: 8,
    });
  }, [kakaoRef.current]);

  /* ===== Favorites markers on map ===== */
  useEffect(() => {
    const kakao = kakaoRef.current;
    const map = mapRef.current;
    if (!kakao || !map) return;

    // clear
    favMarkersRef.current.forEach((m) => m.setMap(null));
    favMarkersRef.current = [];

    const coords = favs
      .map((f) => {
        const lat = typeof f.lat === "number" ? f.lat : null;
        const lng = typeof f.lng === "number" ? f.lng : null;
        if (!lat || !lng) return null;
        return { ...f, lat, lng };
      })
      .filter(Boolean);

    if (!coords.length) return;

    const bounds = new kakao.maps.LatLngBounds();

    coords.forEach((f) => {
      const pos = new kakao.maps.LatLng(f.lat, f.lng);
      const marker = new kakao.maps.Marker({ position: pos, map });
      const iw = new kakao.maps.InfoWindow({
        content: `<div style="padding:6px 8px;font-size:12px;">${f.title || "Ï¶êÍ≤®Ï∞æÍ∏∞"}</div>`,
      });

      kakao.maps.event.addListener(marker, "click", () => {
        navigate(`/events/${f.id}`, { state: { event: f } });
      });
      kakao.maps.event.addListener(marker, "mouseover", () => iw.open(map, marker));
      kakao.maps.event.addListener(marker, "mouseout", () => iw.close());

      favMarkersRef.current.push(marker);
      bounds.extend(pos);
    });

    map.setBounds(bounds);
  }, [favs, navigate]);

  /* ===== actions ===== */
  const onClearRecents = () => {
    clearRecents();
    setRecent([]);
  };

  return (
    <div className="min-h-screen bg-white px-6 py-8 max-w-6xl mx-auto">
      <h2 className="text-2xl font-semibold mb-4">My Page</h2>

      {/* ÏµúÍ∑º Î≥∏ Ìï≠Î™© */}
      <section className="mb-8">
        <div className="flex items-center justify-between mb-2">
          <h3 className="text-lg font-semibold">ÏµúÍ∑º Î≥∏ Ìï≠Î™©</h3>
          <button
            onClick={onClearRecents}
            className="px-3 py-1.5 text-sm rounded-lg border hover:bg-gray-50"
          >
            ÎπÑÏö∞Í∏∞
          </button>
        </div>
        <ul className="divide-y border rounded-lg">
          {recent.length === 0 && (
            <li className="p-4 text-sm text-gray-500">ÏïÑÏßÅ Î≥∏ Ìï≠Î™©Ïù¥ ÏóÜÏñ¥Ïöî.</li>
          )}
          {recent.map((it) => (
            <li
              key={it.id}
              className="p-4 hover:bg-gray-50 cursor-pointer"
              onClick={() => navigate(`/events/${it.id}`, { state: { event: it } })}
            >
              <div className="font-medium">{it.title}</div>
              <div className="text-xs text-gray-600">
                üìÖ {it.date || "-"} ¬∑ üìç {it.place || "-"}
              </div>
            </li>
          ))}
        </ul>
      </section>

      {/* Ï¶êÍ≤®Ï∞æÍ∏∞ */}
      <section className="mb-8">
        <div className="flex items-center justify-between mb-2">
          <h3 className="text-lg font-semibold">Ï¶êÍ≤®Ï∞æÍ∏∞</h3>
        </div>
        <ul className="divide-y border rounded-lg">
          {favs.length === 0 && (
            <li className="p-4 text-sm text-gray-500">Ï¶êÍ≤®Ï∞æÍ∏∞Í∞Ä ÎπÑÏñ¥ ÏûàÏñ¥Ïöî.</li>
          )}
          {favs.map((it) => (
            <li
              key={it.id}
              className="p-4 hover:bg-gray-50 cursor-pointer"
              onClick={() => navigate(`/events/${it.id}`, { state: { event: it } })}
            >
              <div className="font-medium">{it.title}</div>
              <div className="text-xs text-gray-600">
                üìÖ {it.date || "-"} ¬∑ üìç {it.place || "-"}
              </div>
            </li>
          ))}
        </ul>
      </section>

      {/* Ï¶êÍ≤®Ï∞æÍ∏∞ ÏßÄÎèÑ */}
      <section>
        <h3 className="text-lg font-semibold mb-2">Ï¶êÍ≤®Ï∞æÍ∏∞ ÏßÄÎèÑ</h3>
        <div ref={mapEl} className="w-full h-[420px] rounded-lg border" />
      </section>
    </div>
  );
}
