// src/pages/Map.jsx
import React, { useEffect, useMemo, useRef, useState } from "react";

/* === 필터 옵션(예시) === */
const CATEGORIES = ["전체", "공연", "전시", "교육/체험", "기타"];
const AREAS = [
  "전체", "종로구", "중구", "용산구", "성동구", "광진구",
  "동대문구", "중랑구", "성북구", "강북구", "도봉구",
  "노원구", "은평구", "서대문구", "마포구", "양천구",
  "강서구", "구로구", "금천구", "영등포구", "동작구",
  "관악구", "서초구", "강남구", "송파구", "강동구"
];
const QUICK_RANGES = ["전체", "오늘", "이번 주", "이번 달"];

/* === LocalStorage Keys === */
const LS_FAV = "sn_favorites";
const loadFavs = () => {
  try {
    return JSON.parse(localStorage.getItem(LS_FAV) || "[]");
  } catch {
    return [];
  }
};

export default function MapPage() {
  const [category, setCategory] = useState("전체");
  const [area, setArea] = useState("전체");
  const [range, setRange] = useState("전체");
  const [query, setQuery] = useState("");
  const [events, setEvents] = useState([]); // 실제 API 연동시 교체
  const [showFavsOnly, setShowFavsOnly] = useState(false);

  const mapEl = useRef(null);
  const kakaoRef = useRef(null);
  const mapRef = useRef(null);
  const markersRef = useRef([]);
  const favMarkersRef = useRef([]);

  /* ===== Kakao Loader ===== */
  useEffect(() => {
    if (window.kakao && window.kakao.maps) {
      kakaoRef.current = window.kakao;
      return;
    }
    const script = document.createElement("script");
    // TODO: 본인 앱키 삽입
    script.src =
      "https://dapi.kakao.com/v2/maps/sdk.js?appkey=YOUR_APP_KEY&autoload=false";
    script.async = true;
    script.onload = () => {
      window.kakao.maps.load(() => {
        kakaoRef.current = window.kakao;
      });
    };
    document.body.appendChild(script);
  }, []);

  /* ===== Map Init ===== */
  useEffect(() => {
    const kakao = kakaoRef.current;
    if (!kakao || !mapEl.current || mapRef.current) return;
    mapRef.current = new kakao.maps.Map(mapEl.current, {
      center: new kakao.maps.LatLng(37.5665, 126.9780),
      level: 8,
    });
  }, [kakaoRef.current]);

  /* ===== 기본 이벤트 마커(즐겨찾기 오버레이와 별개) ===== */
  useEffect(() => {
    const kakao = kakaoRef.current;
    const map = mapRef.current;
    if (!kakao || !map) return;

    // 기존 마커 제거
    markersRef.current.forEach((m) => m.setMap(null));
    markersRef.current = [];

    // 필터링 (데모)
    const filtered = events
      .filter((ev) => (category === "전체" ? true : ev.category === category))
      .filter((ev) => (area === "전체" ? true : ev.gu === area))
      .filter((ev) => {
        if (!query) return true;
        const k = query.trim().toLowerCase();
        return (
          String(ev.title || "").toLowerCase().includes(k) ||
          String(ev.place || "").toLowerCase().includes(k)
        );
      });

    if (filtered.length === 0) return;

    const bounds = new kakao.maps.LatLngBounds();

    filtered.forEach((ev) => {
      if (typeof ev.lat !== "number" || typeof ev.lng !== "number") return;
      const pos = new kakao.maps.LatLng(ev.lat, ev.lng);
      const marker = new kakao.maps.Marker({ position: pos, map });
      const iw = new kakao.maps.InfoWindow({
        content: `<div style="padding:6px 8px;font-size:12px;">${ev.title || "행사"}</div>`,
      });
      kakao.maps.event.addListener(marker, "mouseover", () => iw.open(map, marker));
      kakao.maps.event.addListener(marker, "mouseout", () => iw.close());
      markersRef.current.push(marker);
      bounds.extend(pos);
    });

    if (!bounds.isEmpty()) {
      map.setBounds(bounds);
    }
  }, [events, category, area, query, range]);

  /* ===== 즐겨찾기 마커 오버레이 (토글) ===== */
  useEffect(() => {
    const kakao = kakaoRef.current;
    const map = mapRef.current;
    if (!kakao || !map) return;

    // 이전 오버레이 제거
    favMarkersRef.current.forEach((m) => m.setMap(null));
    favMarkersRef.current = [];

    if (!showFavsOnly) return;

    const favs = loadFavs();
    const coords = favs
      .map((f) => {
        const lat = typeof f.lat === "number" ? f.lat : null;
        const lng = typeof f.lng === "number" ? f.lng : null;
        if (!lat || !lng) return null;
        return { ...f, lat, lng };
      })
      .filter(Boolean);

    if (!coords.length) return;

    const bounds = new kakao.maps.LatLngBounds();

    coords.forEach((f) => {
      const pos = new kakao.maps.LatLng(f.lat, f.lng);
      const marker = new kakao.maps.Marker({ position: pos, map });
      const iw = new kakao.maps.InfoWindow({
        content: `<div style="padding:6px 8px;font-size:12px;">${f.title || "즐겨찾기"}</div>`,
      });

      // 상세 페이지로 이동 (SPA 라우팅 반영)
      kakao.maps.event.addListener(marker, "click", () => {
        // 라우터 navigate를 직접 쓰려면 컴포넌트 위로 올려야 하므로, 여기서는 URL 변경 + popstate로 처리
        window.history.pushState({}, "", `/events/${f.id}`);
        window.dispatchEvent(new PopStateEvent("popstate"));
      });

      kakao.maps.event.addListener(marker, "mouseover", () => iw.open(map, marker));
      kakao.maps.event.addListener(marker, "mouseout", () => iw.close());
      favMarkersRef.current.push(marker);
      bounds.extend(pos);
    });

    if (!bounds.isEmpty()) {
      map.setBounds(bounds);
    }
  }, [showFavsOnly]);

  return (
    <div className="min-h-screen bg-white px-6 py-8 max-w-6xl mx-auto">
      <h2 className="text-2xl font-semibold mb-4">Map</h2>

      {/* 상단 컨트롤바 */}
      <div className="flex flex-wrap items-center gap-2 mb-3">
        <select
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          className="px-3 py-2 rounded-lg border"
        >
          {CATEGORIES.map((c) => (
            <option key={c}>{c}</option>
          ))}
        </select>
        <select
          value={area}
          onChange={(e) => setArea(e.target.value)}
          className="px-3 py-2 rounded-lg border"
        >
          {AREAS.map((a) => (
            <option key={a}>{a}</option>
          ))}
        </select>
        <select
          value={range}
          onChange={(e) => setRange(e.target.value)}
          className="px-3 py-2 rounded-lg border"
        >
          {QUICK_RANGES.map((r) => (
            <option key={r}>{r}</option>
          ))}
        </select>

        <input
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="검색어(제목/장소)"
          className="px-3 py-2 rounded-lg border w-64"
        />

        <label className="ml-auto inline-flex items-center gap-2 text-sm select-none">
          <input
            type="checkbox"
            checked={showFavsOnly}
            onChange={(e) => setShowFavsOnly(e.target.checked)}
          />
          즐겨찾기만 보기
        </label>
      </div>

      <div ref={mapEl} className="w-full h-[560px] rounded-lg border" />
    </div>
  );
}
