              onChange={(e) => setCategory(e.target.value)}
              className="w-full border rounded-md px-3 py-2 bg-white"
            >
              {CATEGORIES.map((c) => (
                <option key={c} value={c}>{c}</option>
              ))}
            </select>
          </div>

          {/* ê²€ìƒ‰ */}
          <form onSubmit={onSubmit} className="flex-1">
            <div className="relative">
              <input
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="í–‰ì‚¬ëª…, ì¥ì†Œë¡œ ê²€ìƒ‰"
                className="w-full border rounded-md pl-3 pr-10 py-2"
              />
              <button
                type="submit"
                aria-label="ê²€ìƒ‰"
                className="absolute right-2 top-1/2 -translate-y-1/2 p-1 rounded hover:bg-gray-50"
              >
                <img src="/images/search.png" alt="" className="w-4 h-4" />
              </button>
            </div>
          </form>
        </div>

        {/* ë³¸ë¬¸: ê²°ê³¼ (ê°€ìƒ ìŠ¤í¬ë¡¤ ì»¨í…Œì´ë„ˆ) */}
        <div className="mt-6">
          <div className="border rounded-lg">
            {/* ìƒë‹¨ ìƒíƒœë°” */}
            <div className="px-4 py-2 text-sm text-gray-600 border-b flex items-center justify-between">
              <div>
                {loading ? "ë¶ˆëŸ¬ì˜¤ëŠ” ì¤‘â€¦" : `ì´ ${results.length.toLocaleString()}ê°œ`}
