              </div>
              {error && (
                <div className="text-red-600">
                  ë¡œë”© ì‹¤íŒ¨: {String(error.message || error)}
                </div>
              )}
            </div>

            {/* Virtuoso ë¦¬ìŠ¤íŠ¸ */}
            <Virtuoso
              style={{ height: 720 }}
              data={results}
              overscan={300}
              itemContent={(index, ev) => {
                const isFav = favSet.has(ev.id);
                return (
                  <div className="p-4">
                    <div className="relative text-left bg-white border rounded-lg overflow-hidden hover:shadow transition">
                      {/* í•˜íŠ¸ ë²„íŠ¼ */}
                      <button
