  // Ï¶êÍ≤®Ï∞æÍ∏∞ ÏÉÅÌÉú(Îπ†Î•∏ Ï°∞ÌöåÎ•º ÏúÑÌï¥ SetÏúºÎ°ú Î≥¥Í¥Ä)
  const [favSet, setFavSet] = useState(() => new Set(loadFavs().map((x) => x.id)));

  useEffect(() => {
    const next = new URLSearchParams(sp);
    if (category && category !== "Ï†ÑÏ≤¥") next.set("category", encodeURIComponent(category));
    else next.delete("category");
    if (query) next.set("q", query);
    else next.delete("q");
    setSp(next, { replace: true });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [category, query]);

  const results = useMemo(() => {
    const pool =
      category === "Ï†ÑÏ≤¥" ? events : events.filter((e) => e.category === category);
    if (!query.trim()) return pool;
    const q = query.trim().toLowerCase();
    return pool.filter(
      (e) =>
        e.title.toLowerCase().includes(q) ||
        (e.place || "").toLowerCase().includes(q)
    );
  }, [events, category, query]);

  const onSubmit = (e) => {
    e.preventDefault();
    setQuery(input);
  };

  // ÌïòÌä∏ ÌÅ¥Î¶≠ ‚Üí Î°úÏª¨Ïä§ÌÜ†Î¶¨ÏßÄÏóê ÌÜ†Í∏Ä Ï†ÄÏû•Îßå (Ïù¥Îèô ÏóÜÏùå)
  const onHeartToggle = (ev) => {
    const list = loadFavs();
    const exists = list.some((x) => x.id === ev.id);
    let next;
    if (exists) {
      next = list.filter((x) => x.id !== ev.id);
    } else {
      // Favorites.jsxÍ∞Ä Ïì∞Îäî ÌïÑÎìú Íµ¨Ï°∞Î°ú Ï†ÄÏû•
      next = [
        ...list,
        {
          id: ev.id,
          title: ev.title,
          category: ev.category,
          date: ev.date,
          place: ev.place,
          thumb: ev.thumb,
          homepage: ev.homepage,
        },
      ];
    }
    saveFavs(next);
    setFavSet(new Set(next.map((x) => x.id))); // UI ÎèôÍ∏∞Ìôî
  };

  return (
    <div className="min-h-screen bg-white">
      <div className="max-w-6xl mx-auto px-4 md:px-6">
        {/* Ïª®Ìä∏Î°§ Î∞î: Ïπ¥ÌÖåÍ≥†Î¶¨ + Í≤ÄÏÉâ */}
        <div className="flex flex-col md:flex-row md:items-center gap-3 md:gap-4">
          {/* Ïπ¥ÌÖåÍ≥†Î¶¨ */}
          <div className="w-full md:w-56">
            <label htmlFor="category" className="sr-only">
              Ïπ¥ÌÖåÍ≥†Î¶¨
            </label>
            <select
              id="category"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              className="w-full border rounded-md px-3 py-2 bg-white"
            >
              {CATEGORIES.map((c) => (
                <option key={c} value={c}>
                  {c}
                </option>
              ))}
            </select>
          </div>

          {/* Í≤ÄÏÉâ */}
          <form onSubmit={onSubmit} className="flex-1">
            <div className="relative">
              <input
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="ÌñâÏÇ¨Î™Ö, Ïû•ÏÜåÎ°ú Í≤ÄÏÉâ"
                className="w-full border rounded-md pl-3 pr-10 py-2"
              />
              <button
                type="submit"
                aria-label="Í≤ÄÏÉâ"
                className="absolute right-2 top-1/2 -translate-y-1/2 p-1 rounded hover:bg-gray-50"
              >
                <img src="/images/search.png" alt="" className="w-4 h-4" />
              </button>
            </div>
          </form>
        </div>

        {/* Î≥∏Î¨∏: Í≤∞Í≥º */}
        <div className="mt-6">
          <div className="border rounded-lg p-4">
            {loading && (
              <div className="h-56 grid place-items-center text-gray-500">Î∂àÎü¨Ïò§Îäî Ï§ë‚Ä¶</div>
            )}
            {error && (
              <div className="h-56 grid place-items-center text-red-600">
                Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§ÏßÄ Î™ªÌñàÏñ¥Ïöî. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥ Ï£ºÏÑ∏Ïöî.
              </div>
            )}
            {!loading && !error && results.length === 0 && (
              <div className="h-56 grid place-items-center text-gray-500">
                Ï°∞Í±¥Ïóê ÎßûÎäî ÌñâÏÇ¨Í∞Ä ÏóÜÏäµÎãàÎã§.
              </div>
            )}
            {!loading && !error && results.length > 0 && (
              <div className="grid sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
                {results.map((ev) => {
                  const isFav = favSet.has(ev.id);
                  return (
                    <div
                      key={ev.id}
                      className="relative text-left bg-white border rounded-lg overflow-hidden hover:shadow transition"
                    >
                      {/* ÌïòÌä∏ Î≤ÑÌäº (Ïò§Î≤ÑÎ†àÏù¥) */}
                      <button
                        onClick={(e) => { e.preventDefault(); onHeartToggle(ev); }}
                        className="absolute right-2 top-2 z-10 text-2xl leading-none select-none"
                        title={isFav ? "Ï¶êÍ≤®Ï∞æÍ∏∞ Ìï¥Ï†ú" : "Ï¶êÍ≤®Ï∞æÍ∏∞ Ï∂îÍ∞Ä"}
                        aria-label={isFav ? "Ï¶êÍ≤®Ï∞æÍ∏∞ Ìï¥Ï†ú" : "Ï¶êÍ≤®Ï∞æÍ∏∞ Ï∂îÍ∞Ä"}
                      >
                        {isFav ? "‚ù§Ô∏è" : "ü§ç"}
                      </button>

                      {/* ÎÇ¥Ïö©: ÎßÅÌÅ¨ ÏòÅÏó≠ */}
                      <a
                        href={ev.homepage || "#"}
                        target={ev.homepage ? "_blank" : "_self"}
                        rel="noreferrer"
                        className="block"
                        title={ev.homepage ? "ÏÉÅÏÑ∏Î≥¥Í∏∞(ÏÉàÏ∞Ω)" : undefined}
                      >
                        <img
                          src={ev.thumb}
                          alt={ev.title}
                          className="w-full h-40 object-cover"
                        />
                        <div className="p-3">
                          <div className="flex items-center justify-between gap-2">
                            <h3 className="font-semibold line-clamp-2">{ev.title}</h3>
                            <span className="text-xs px-2 py-0.5 rounded-full bg-gray-100 whitespace-nowrap">
