                          {ev.fee && (
                            <p className="text-xs text-gray-500 mt-1">ÏöîÍ∏à: {ev.fee}</p>
                          )}
                        </div>
                      </a>
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
