// src/pages/BrowseEvents.jsx
import React, { useEffect, useMemo, useState } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import { addRecent } from "./MyPage";

/* === Ïπ¥ÌÖåÍ≥†Î¶¨ Ï†ïÏùò === */
const CATEGORIES = ["Ï†ÑÏ≤¥", "Í≥µÏó∞", "Ï†ÑÏãú", "ÍµêÏú°/Ï≤¥Ìóò", "Í∏∞ÌÉÄ"];

/* === ÏÉÅÏúÑ Ïπ¥ÌÖåÍ≥†Î¶¨ Îß§Ìïë === */
function toHighLevelCategory(codename = "", themecode = "") {
  const c = String(codename);
  if (
    ["ÏΩòÏÑúÌä∏", "ÌÅ¥ÎûòÏãù", "Íµ≠ÏïÖ", "Î¨¥Ïö©", "Ïó∞Í∑π", "ÎÆ§ÏßÄÏª¨/Ïò§ÌéòÎùº", "Ï∂ïÏ†ú-Í∏∞ÌÉÄ"].some((k) =>
      c.includes(k)
    )
  ) {
    return "Í≥µÏó∞";
  }
  if (c.includes("Ï†ÑÏãú/ÎØ∏Ïà†")) return "Ï†ÑÏãú";
  if (c.includes("ÍµêÏú°/Ï≤¥Ìóò") || String(themecode).includes("ÍµêÏú°")) return "ÍµêÏú°/Ï≤¥Ìóò";
  return "Í∏∞ÌÉÄ";
}

/* === Î°úÏª¨ Ï†ÄÏû• ÌÇ§ === */
const LS_FAV = "sn_favorites";

/* === Ï¶êÍ≤®Ï∞æÍ∏∞ Ìó¨Ìçº === */
const loadFavs = () => {
  try {
    return JSON.parse(localStorage.getItem(LS_FAV) || "[]");
  } catch {
    return [];
  }
};

const saveFavs = (list) => {
  localStorage.setItem(LS_FAV, JSON.stringify(list));
};

export default function BrowseEvents() {
  const [searchParams, setSearchParams] = useSearchParams();
  const [all, setAll] = useState([]); // Ï†ÑÏ≤¥ Ïù¥Î≤§Ìä∏ (API ÎòêÎäî ÏÉÅÏúÑÏóêÏÑú Ï£ºÏûÖ)
  const [q, setQ] = useState(searchParams.get("q") || "");
  const [cat, setCat] = useState(searchParams.get("cat") || "Ï†ÑÏ≤¥");
  const [favs, setFavs] = useState(loadFavs());
  const navigate = useNavigate();

  /* ======= Îç∞Î™®Ïö©: all Ï±ÑÏö∞Í∏∞ (Ïã§ÏÑúÎπÑÏä§ÏóêÏÑúÎäî props/hook/APIÎ°ú ÍµêÏ≤¥) ======= */
  useEffect(() => {
    // Ïã§Ï†ú Ïó∞ÎèôÎ∂ÄÏóêÏÑú ÎèôÏùºÌïú ÌïÑÎìú(id/title/date/place/category/gu/lat/lng/thumb/homepage)Î•º ÎßûÏ∂∞Ï£ºÏÑ∏Ïöî.
    setAll((prev) => prev || []);
  }, []);

  /* ======= ÌååÎùºÎØ∏ÌÑ∞ ÎèôÍ∏∞Ìôî ======= */
  useEffect(() => {
    const sp = new URLSearchParams();
    if (q) sp.set("q", q);
    if (cat && cat !== "Ï†ÑÏ≤¥") sp.set("cat", cat);
    setSearchParams(sp);
  }, [q, cat, setSearchParams]);

  /* ======= Í≤ÄÏÉâ/ÌïÑÌÑ∞ ======= */
  const list = useMemo(() => {
    let arr = all;
    if (cat && cat !== "Ï†ÑÏ≤¥") {
      arr = arr.filter((ev) => (ev.category || toHighLevelCategory(ev.codename, ev.themecode)) === cat);
    }
    if (q) {
      const k = q.trim().toLowerCase();
      arr = arr.filter(
        (ev) =>
          String(ev.title || "").toLowerCase().includes(k) ||
          String(ev.place || "").toLowerCase().includes(k) ||
          String(ev.gu || "").toLowerCase().includes(k)
      );
    }
    return arr;
  }, [all, q, cat]);

  /* ======= Ï¶êÍ≤®Ï∞æÍ∏∞ ÌÜ†Í∏Ä ======= */
  const toggleFav = (ev) => {
    const has = favs.some((f) => f.id === ev.id);
    const next = has ? favs.filter((f) => f.id !== ev.id) : [{ ...ev }, ...favs];
    setFavs(next);
    saveFavs(next);
  };

  const isFav = (id) => favs.some((f) => f.id === id);

  return (
    <div className="min-h-screen bg-white px-6 py-8 max-w-6xl mx-auto">
      <h2 className="text-2xl font-semibold mb-4">Browse Events</h2>

      {/* Í≤ÄÏÉâ/ÌïÑÌÑ∞ Î∞î */}
      <div className="flex flex-wrap gap-2 items-center mb-4">
        <input
          value={q}
          onChange={(e) => setQ(e.target.value)}
          placeholder="Í≤ÄÏÉâÏñ¥(Ï†úÎ™©/Ïû•ÏÜå/Íµ¨)"
          className="px-3 py-2 rounded-lg border w-64"
        />
        <select
          value={cat}
          onChange={(e) => setCat(e.target.value)}
          className="px-3 py-2 rounded-lg border"
        >
          {CATEGORIES.map((c) => (
            <option key={c} value={c}>
              {c}
            </option>
          ))}
        </select>
      </div>

      {/* Ïπ¥Îìú Í∑∏Î¶¨Îìú */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {list.map((ev) => (
          <div
            key={ev.id}
            className="rounded-xl border p-3 hover:shadow transition cursor-pointer"
            onClick={() => {
              // ÏÉÅÏÑ∏Î°ú Ïù¥Îèô Ï†ÑÏóê 'ÏµúÍ∑º Î≥∏'Ïóê Í∏∞Î°ù
              addRecent({
                id: ev.id,
                title: ev.title,
                date: ev.date,
                place: ev.place,
                category: ev.category || toHighLevelCategory(ev.codename, ev.themecode),
                thumb: ev.thumb,
                homepage: ev.homepage,
                gu: ev.gu,
                lat: ev.lat,
                lng: ev.lng,
              });
              navigate(`/events/${ev.id}`, { state: { event: ev } });
            }}
          >
            {ev.thumb ? (
              <img
                src={ev.thumb}
                alt={ev.title}
                className="w-full h-44 object-cover rounded-lg mb-3"
              />
            ) : (
              <div className="w-full h-44 bg-gray-100 rounded-lg mb-3" />
            )}
            <div className="flex items-start justify-between gap-3">
              <div className="flex-1">
                <div className="font-semibold line-clamp-2">{ev.title}</div>
                <div className="text-xs text-gray-600 mt-1">
                  üìÖ {ev.date || "-"} ¬∑ üìç {ev.place || "-"}
                </div>
                <div className="text-[11px] text-gray-500 mt-0.5">
                  #{ev.category || toHighLevelCategory(ev.codename, ev.themecode)}
                  {ev.gu ? ` ¬∑ #${ev.gu}` : ""}
                </div>
              </div>
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  toggleFav(ev);
                }}
                className="shrink-0 text-rose-500"
                aria-label="Ï¶êÍ≤®Ï∞æÍ∏∞"
                title="Ï¶êÍ≤®Ï∞æÍ∏∞"
              >
                {isFav(ev.id) ? "‚ô•" : "‚ô°"}
              </button>
            </div>
          </div>
        ))}
      </div>

      {list.length === 0 && (
        <div className="text-sm text-gray-500 py-16 text-center">Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§.</div>
      )}
    </div>
  );
}
