function ReviewEditor({ mode = "create", initialValue, onSubmit, onCancel }) {
  const [rating, setRating] = useState(initialValue?.rating ?? 0);
  const [body, setBody] = useState(initialValue?.body ?? "");
  const [saving, setSaving] = useState(false);
