  const handleSubmit = async (e) => {
    e.preventDefault();
    if (rating < 1) return alert("Î≥ÑÏ†êÏùÑ ÏÑ†ÌÉùÌï¥ Ï£ºÏÑ∏Ïöî (1~5)");
    if (body.trim().length < 5) return alert("Î¶¨Î∑∞Î•º 5Ïûê Ïù¥ÏÉÅ ÏûÖÎ†•Ìï¥ Ï£ºÏÑ∏Ïöî");

