export default function BrowseEvents() {
  const [sp, setSp] = useSearchParams();

  // URL ì¿¼ë¦¬ ë™ê¸°í™”
  const initialCategory = decodeURIComponent(sp.get("category") || "ì „ì²´");
  const initialQuery = sp.get("q") || "";

  const [category, setCategory] = useState(
    CATEGORIES.includes(initialCategory) ? initialCategory : "ì „ì²´"
  );
  const [input, setInput] = useState(initialQuery);
  const [query, setQuery] = useState(initialQuery);

  const [page, setPage] = useState(1);

  // ê²€ìƒ‰ ì…ë ¥ ë””ë°”ìš´ìŠ¤
  useEffect(() => {
    const t = setTimeout(() => setQuery(input), 300);
    return () => clearTimeout(t);
  }, [input]);

  // ì¹´í…Œê³ ë¦¬/ê²€ìƒ‰ ë°”ë€Œë©´ 1í˜ì´ì§€ë¡œ ì´ë™
  useEffect(() => {
    setPage(1);
  }, [category, query]);

  // í˜„ì¬ í˜ì´ì§€ ë°ì´í„°ë§Œ APIë¡œ ë¡œë”©
  const { events: pageEvents, loading, error, totalCount, totalPages } =
    useSeoulEventsPage(page);

  // ì¦ê²¨ì°¾ê¸°
  const [favSet, setFavSet] = useState(
    () => new Set(loadFavs().map((x) => x.id))
  );


  // URL ìœ ì§€
  useEffect(() => {
    const next = new URLSearchParams(sp);
    if (category && category !== "ì „ì²´")
      next.set("category", encodeURIComponent(category));
    else next.delete("category");
    if (query) next.set("q", query);
    else next.delete("q");
    setSp(next, { replace: true });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [category, query]);

  // í•„í„° + ê²€ìƒ‰ (í˜„ì¬ í˜ì´ì§€ ë°ì´í„° ì•ˆì—ì„œë§Œ)
  const deferredQuery = useDeferredValue(query);
  const filtered = useMemo(() => {
    const pool =
      category === "ì „ì²´"
        ? pageEvents
        : pageEvents.filter((e) => e.category === category);
    const q = (deferredQuery || "").trim().toLowerCase();
    if (!q) return pool;
    return pool.filter(
      (e) =>
        e.title.toLowerCase().includes(q) ||
        (e.place || "").toLowerCase().includes(q)
    );
  }, [pageEvents, category, deferredQuery]);

  const onSubmit = (e) => {
    e.preventDefault();
    setQuery(input);
  };

  const onHeartToggle = (ev) => {
    const list = loadFavs();
    const exists = list.some((x) => x.id === ev.id);
    let next;
    if (exists) {
      next = list.filter((x) => x.id !== ev.id);
    } else {
      next = [
        ...list,
        {
          id: ev.id,
          title: ev.title,
          category: ev.category,
          date: ev.date,
          place: ev.place,
          thumb: ev.thumb,
          homepage: ev.homepage,
        },
      ];
    }
    saveFavs(next);
    setFavSet(new Set(next.map((x) => x.id)));
  };

  return (
    <div className="min-h-screen bg-white">
      <div className="max-w-6xl mx-auto px-4 md:px-6">
        {/* ì»¨íŠ¸ë¡¤ ë°” */}
        <div className="flex flex-col md:flex-row md:items-center gap-3 md:gap-4">
          <div className="w-full md:w-56">
            <label htmlFor="category" className="sr-only">
              ì¹´í…Œê³ ë¦¬
            </label>
            <select
              id="category"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              className="w-full border rounded-md px-3 py-2 bg-white"
            >
              {CATEGORIES.map((c) => (
                <option key={c} value={c}>
                  {c}
                </option>
              ))}
            </select>
          </div>

          <form onSubmit={onSubmit} className="flex-1">
            <div className="relative">
              <input
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="í–‰ì‚¬ëª…, ì¥ì†Œë¡œ ê²€ìƒ‰"
                className="w-full border rounded-md pl-3 pr-10 py-2"
              />
              <button
                type="submit"
                aria-label="ê²€ìƒ‰"
                className="absolute right-2 top-1/2 -translate-y-1/2 p-1 rounded hover:bg-gray-50"
              >
                <img src="/images/search.png" alt="" className="w-4 h-4" />
              </button>
            </div>
          </form>
        </div>

        {/* ë³¸ë¬¸ */}
        <div className="mt-6">
          <div className="border rounded-lg p-4">
            {loading && (
              <div className="h-56 grid place-items-center text-gray-500">
                ë¶ˆëŸ¬ì˜¤ëŠ” ì¤‘â€¦
              </div>
            )}
            {error && (
              <div className="h-56 grid place-items-center text-red-600">
                ë°ì´í„°ë¥¼ ë¶ˆëŸ¬ì˜¤ì§€ ëª»í–ˆì–´ìš”.{" "}
                {String(error.message || error)}
              </div>
            )}

            {!loading && !error && filtered.length === 0 && (
              <div className="h-56 grid place-items-center text-gray-500">
                ì¡°ê±´ì— ë§ëŠ” í–‰ì‚¬ê°€ ì—†ìŠµë‹ˆë‹¤.
              </div>
            )}

            {!loading && !error && filtered.length > 0 && (
              <>
                {/* ê²°ê³¼ ê·¸ë¦¬ë“œ */}
                <div className="grid sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
                  {filtered.map((ev) => (
                    <EventCard
                      key={ev.id}
                      ev={ev}
                      isFav={favSet.has(ev.id)}
                      onHeartToggle={onHeartToggle}
                    />
                  ))}
