// src/pages/EventDetail.jsx
import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

/**
 * Î¶¨Î∑∞ Í∏∞Îä• Ï†úÍ±∞ Î≤ÑÏ†ÑÏùò ÏÉÅÏÑ∏ ÌéòÏù¥ÏßÄ
 * - MyPage / Map Îì±ÏóêÏÑú navigate Ìï† Îïå stateÎ°ú ÎÑòÍ∏¥ eventÎßå ÏÇ¨Ïö©
 * - Î¶¨Î∑∞, Î≥ÑÏ†ê, Î°úÏª¨Ïä§ÌÜ†Î¶¨ÏßÄ Î¶¨Î∑∞ Ï†ÑÎ∂Ä ÏóÜÏùå
 */

export default function EventDetail() {
  const navigate = useNavigate();
  const location = useLocation();

  // navigate(..., { state: { event } }) Î°ú ÎÑòÍ∏¥ Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö©
  const ev = location.state?.event;

  if (!ev) {
    // state ÏóÜÏù¥ /events/:id Î°ú ÏßÅÏ†ë Îì§Ïñ¥Ïò® Í≤ΩÏö∞
    return (
      <div className="min-h-screen bg-white px-6 py-8 max-w-4xl mx-auto">
        <button
          onClick={() => navigate(-1)}
          className="mb-4 text-sm underline text-gray-600"
        >
          ‚Üê Îí§Î°úÍ∞ÄÍ∏∞
        </button>
        <div className="border rounded-lg p-6 text-sm text-gray-600">
          Ïù¥ ÌñâÏÇ¨Ïùò ÏÉÅÏÑ∏ Ï†ïÎ≥¥Í∞Ä Ï†ÑÎã¨ÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§.
          <br />
          Ïù¥Ï†Ñ ÌôîÎ©¥ÏóêÏÑú Îã§Ïãú Ï†ëÍ∑ºÌï¥ Ï£ºÏÑ∏Ïöî.
        </div>
      </div>
    );
  }

  const {
    title,
    category,
    date,
    dateLabel,
    place,
    homepage,
    fee,
    thumb,
    gu,
  } = ev;

  const displayDate = dateLabel || date || "ÏùºÏ†ï ÎØ∏Ï†ï";
  const displayPlace = place || gu || "Ïû•ÏÜå ÎØ∏Ï†ï";

  return (
    <div className="min-h-screen bg-white px-6 py-8 max-w-4xl mx-auto">
      {/* ÏÉÅÎã® Ìó§Îçî */}
      <button
        onClick={() => navigate(-1)}
        className="mb-4 text-sm underline text-gray-600"
      >
        ‚Üê Îí§Î°úÍ∞ÄÍ∏∞
      </button>

      <div className="border rounded-lg overflow-hidden bg-white shadow-sm">
        {/* Ïù¥ÎØ∏ÏßÄ ÏòÅÏó≠ */}
        {thumb && (
          <div className="w-full h-56 bg-gray-100 overflow-hidden">
            <img
              src={thumb}
              alt={title}
              className="w-full h-full object-cover"
            />
          </div>
        )}

        {/* ÌÖçÏä§Ìä∏ Ï†ïÎ≥¥ */}
        <div className="p-5 space-y-3">
          <div className="flex items-center justify-between gap-2">
            <div className="text-xs inline-flex items-center rounded-full bg-gray-100 px-3 py-1">
              {category || "ÌñâÏÇ¨"}
              {gu ? <span className="ml-1 text-gray-500">¬∑ {gu}</span> : null}
            </div>
          </div>

          <h1 className="text-2xl font-bold leading-snug">{title}</h1>

          <div className="space-y-1 text-sm text-gray-700">
            <p>üìÖ {displayDate}</p>
            <p>üìç {displayPlace}</p>
            {fee && <p>üí≥ ÏöîÍ∏à: {fee}</p>}
          </div>

          {homepage && (
            <div className="pt-3">
              <a
                href={homepage}
                target="_blank"
                rel="noreferrer"
                className="inline-flex items-center text-sm px-4 py-2 rounded-full border hover:bg-gray-50"
              >
                ÏÉÅÏÑ∏ ÌéòÏù¥ÏßÄ Î∞îÎ°úÍ∞ÄÍ∏∞
              </a>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
