// src/App.js
import React from "react";
import { BrowserRouter, Routes, Route, NavLink } from "react-router-dom";

import BrowseEvents from "./pages/BrowseEvents.jsx";
import MapPage from "./pages/Map.jsx";
import CalendarPage from "./pages/Calendar.jsx";
import Favorites from "./pages/Favorites.jsx";
import MyPage from "./pages/MyPage.jsx";
import EventDetail from "./pages/EventDetail.jsx";

/* 간단한 레이아웃 */
function Layout({ children }) {
  const items = [
    { to: "/browse", label: "Browse" },
    { to: "/map", label: "Map" },
    { to: "/calendar", label: "Calendar" },
    { to: "/favorites", label: "Favorites" },
    { to: "/mypage", label: "My Page" },
  ];
  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-white border-b">
        <div className="max-w-6xl mx-auto px-6 h-14 flex items-center gap-4">
          <div className="font-bold">SEOUL NOW</div>
          <nav className="flex items-center gap-3 text-sm">
            {items.map((m) => (
              <NavLink
                key={m.to}
                to={m.to}
                className={({ isActive }) =>
                  `px-2 py-1 rounded ${isActive ? "bg-gray-900 text-white" : "hover:bg-gray-100"}`
                }
              >
                {m.label}
              </NavLink>
            ))}
          </nav>
        </div>
      </header>
      <main className="max-w-6xl mx-auto px-6 py-6">{children}</main>
    </div>
  );
}

function Home() {
  return (
    <div className="py-24 text-center">
      <h1 className="text-4xl font-extrabold">SEOUL NOW</h1>
      <p className="text-gray-600 mt-3">실시간 서울 문화행사 통합 안내</p>
    </div>
  );
}

export default function App() {
  return (
    <BrowserRouter>
      <Layout>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/browse" element={<BrowseEvents />} />
          <Route path="/map" element={<MapPage />} />
          <Route path="/calendar" element={<CalendarPage />} />
          <Route path="/favorites" element={<Favorites />} />
          <Route path="/mypage" element={<MyPage />} />
          {/* 상세 페이지 라우트 필수 */}
          <Route path="/events/:id" element={<EventDetail />} />
        </Routes>
      </Layout>
    </BrowserRouter>
  );
}
