// src/pages/EventDetail.jsx
import React, { useEffect } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";

/**
 * 리뷰/내부 상세 화면 제거 버전
 * - MyPage / Map / 기타 곳에서 navigate("/events/:id", { state: { event } }) 로 올 때
 * - event.homepage 로 곧바로 이동시켜 주는 역할만 함
 */

export default function EventDetail() {
  const navigate = useNavigate();
  const location = useLocation();
  const { id } = useParams();

  // 다른 페이지에서 넘겨준 이벤트 정보
  const ev = location.state?.event;

  useEffect(() => {
    if (ev?.homepage) {
      // 외부 상세 페이지로 바로 이동
      window.location.href = ev.homepage;
    }
  }, [ev]);

  // state 없거나 homepage 없는 경우 — 안내만 표시
  if (!ev || !ev.homepage) {
    return (
      <div className="min-h-screen bg-white px-6 py-8 max-w-4xl mx-auto">
        <button
          onClick={() => navigate(-1)}
          className="mb-4 text-sm underline text-gray-600"
        >
          ← 뒤로가기
        </button>
        <div className="border rounded-lg p-6 text-sm text-gray-700">
          이 행사의 외부 상세 링크 정보가 없습니다.
          <br />
          이전 화면으로 돌아가 다시 시도해 주세요.
          <br />
          (event id: {id})
        </div>
      </div>
    );
  }

  // homepage가 있는 정상 케이스: useEffect에서 이동 중
  return (
    <div className="min-h-screen bg-white px-6 py-8 max-w-4xl mx-auto">
      <button
        onClick={() => navigate(-1)}
        className="mb-4 text-sm underline text-gray-600"
      >
        ← 뒤로가기
      </button>
      <div className="border rounded-lg p-6 text-sm text-gray-700">
        <p className="mb-2 font-semibold">{ev.title}</p>
        <p>외부 상세 페이지로 이동 중입니다…</p>
        <p className="mt-2 text-xs text-gray-500 break-all">
          {ev.homepage}
        </p>
        <p className="mt-3 text-xs text-gray-500">
          자동으로 이동하지 않는다면{" "}
          <a
            href={ev.homepage}
            className="underline"
            target="_blank"
            rel="noreferrer"
          >
            여기를 클릭
          </a>
          해 주세요.
        </p>
      </div>
    </div>
  );
}
