// src/App.js
// - HOMEÏóêÏÑú Ï†ÑÏ≤¥ Îç∞Ïù¥ÌÑ∞ Î°úÎî© ÎåÄÏã†,
//   ‚ë† Ï∂îÏ≤ú ÌñâÏÇ¨Îäî Î≥ÑÎèÑ API(START_INDEX=0, END_INDEX=4)
//   ‚ë° ÌñâÏÇ¨ Ï∫òÎ¶∞ÎçîÎäî ÌòÑÏû¨ Îã¨Î†•Ïóê Î≥¥Ïù¥Îäî ÎÇ†Ïßú(ÏµúÎåÄ 42Ïùº)Ïóê ÎåÄÌï¥ÏÑúÎßå ÏùºÎ≥Ñ API Ìò∏Ï∂ú
// - seoulApi.js Ïóê fetchSeoulRecommendedEvents, fetchSeoulDailyEvents ÏÇ¨Ïö©

import React, { useEffect, useMemo, useState } from "react";
import BrowseEvents from "./pages/BrowseEvents.jsx";
import MapPage from "./pages/Map.jsx";
import CalendarPage from "./pages/Calendar.jsx";
import Favorites from "./pages/Favorites.jsx";
import MyPage from "./pages/MyPage.jsx";
import EventDetail from "./pages/EventDetail";
import {
  BrowserRouter,
  Routes,
  Route,
  NavLink,
  useNavigate,
} from "react-router-dom";
import {
  FaTheaterMasks,
  FaPalette,
  FaGraduationCap,
  FaQuestion,
} from "react-icons/fa";

// üîó Ïú†Ìã∏
import {
  fetchSeoulRecommendedEvents, // Ï∂îÏ≤ú ÌñâÏÇ¨Ïö© (START_INDEX=0, END_INDEX=4)
  fetchSeoulDailyEvents, // ÌäπÏ†ï ÎÇ†Ïßú ÌñâÏÇ¨Ïö© (DATE + START_INDEX=0, END_INDEX=4)
} from "./lib/seoulApi";

/* === .env ÌÇ§ === */
const SEOUL_KEY = (process.env.REACT_APP_SEOUL_KEY || "").trim();

/* --- Ïπ¥ÌÖåÍ≥†Î¶¨/ÏÉâÏÉÅ --- */
const categories = [
  { id: 1, name: "Í≥µÏó∞", icon: FaTheaterMasks },
  { id: 2, name: "Ï†ÑÏãú", icon: FaPalette },
  { id: 3, name: "ÍµêÏú°/Ï≤¥Ìóò", icon: FaGraduationCap },
  { id: 4, name: "Í∏∞ÌÉÄ", icon: FaQuestion },
];

const CATEGORY_COLORS = {
  Í≥µÏó∞: "bg-indigo-500",
  Ï†ÑÏãú: "bg-emerald-500",
  "ÍµêÏú°/Ï≤¥Ìóò": "bg-amber-500",
  Í∏∞ÌÉÄ: "bg-rose-500",
};

/* --- Îã¨Î†• Ïú†Ìã∏ --- */
function formatDateKey(date) {
  const y = date.getFullYear();
  const m = String(date.getMonth() + 1).padStart(2, "0");
  const d = String(date.getDate()).padStart(2, "0");
  return `${y}-${m}-${d}`;
}

function formatDateLabel(dateKey) {
  try {
    const d = new Date(`${dateKey}T00:00:00`);
    if (isNaN(d)) return dateKey;
    return d.toLocaleDateString("ko-KR", {
      year: "numeric",
      month: "long",
      day: "numeric",
      weekday: "short",
    });
  } catch {
    return dateKey;
  }
}

function getMonthMatrix(year, monthIndex, weekStartsOn = 0) {
  const first = new Date(year, monthIndex, 1);
  const last = new Date(year, monthIndex + 1, 0);
  const daysInMonth = last.getDate();
  const firstDay = (first.getDay() - weekStartsOn + 7) % 7;
  const totalCells = Math.ceil((firstDay + daysInMonth) / 7) * 7;
  const cells = [];
  for (let i = 0; i < totalCells; i++) {
    const dayOffset = i - firstDay + 1;
    const d = new Date(year, monthIndex, dayOffset);
    const inCurrentMonth = dayOffset >= 1 && dayOffset <= daysInMonth;
    cells.push({ date: d, inCurrentMonth });
  }
  return cells;
}

/** APIÏùò ÎÇ†Ïßú Î¨∏ÏûêÏó¥(YYYY-MM-DD, YYYY.MM.DD, YYYYMMDD‚Ä¶)ÏùÑ DateÎ°ú ÏïàÏ†Ñ Î≥ÄÌôò */
function toISODate(dateStr = "") {
  if (!dateStr) return null;
  const raw = String(dateStr).trim();
  if (/^\d{8}$/.test(raw)) {
    const y = raw.slice(0, 4);
    const m = raw.slice(4, 6);
    const d = raw.slice(6, 8);
    return new Date(`${y}-${m}-${d}T00:00:00`);
  }
  const s = raw.replaceAll(".", "-").split(" ")[0];
  const d = new Date(`${s}T00:00:00`);
  return isNaN(d) ? null : d;
}

/** Ïπ¥ÌÖåÍ≥†Î¶¨ ÏÉÅÏúÑÎß§Ìïë */
function toHighLevelCategory(codename = "", themecode = "") {
  const c = String(codename || "");
  const t = String(themecode || "");

  // 1) Í≥µÏó∞ Í¥ÄÎ†® ÌÇ§ÏõåÎìú
  if (
    [
      "Í≥µÏó∞",
      "ÏΩòÏÑúÌä∏",
      "ÌÅ¥ÎûòÏãù",
      "Íµ≠ÏïÖ",
      "Î¨¥Ïö©",
      "Ïó∞Í∑π",
      "ÎÆ§ÏßÄÏª¨",
      "Ïò§ÌéòÎùº",
      "ÏùåÏïÖÌöå",
      "ÌéòÏä§Ìã∞Î≤å",
      "Ï∂ïÏ†ú",
    ].some((k) => c.includes(k))
  ) {
    return "Í≥µÏó∞";
  }

  // 2) Ï†ÑÏãú
  if (["Ï†ÑÏãú", "ÎØ∏Ïà†", "Í∞§Îü¨Î¶¨", "ÏïÑÌä∏", "ÏÇ¨ÏßÑÏ†Ñ"].some((k) => c.includes(k))) {
    return "Ï†ÑÏãú";
  }

  // 3) ÍµêÏú°/Ï≤¥Ìóò
  if (
    ["ÍµêÏú°", "Ï≤¥Ìóò", "ÏõåÌÅ¨Ïàç", "ÏõåÌÅ¨ÏÉµ", "Í∞ïÏ¢å", "Í∞ïÏùò", "ÏÑ∏ÎØ∏ÎÇò", "Í∞ïÏó∞"].some(
      (k) => c.includes(k)
    ) ||
    t.includes("ÍµêÏú°")
  ) {
    return "ÍµêÏú°/Ï≤¥Ìóò";
  }

  // 4) ÎÇòÎ®∏ÏßÄÎäî Í∏∞ÌÉÄ
  return "Í∏∞ÌÉÄ";
}

/** OpenAPI row Î∞∞Ïó¥ÏùÑ ÌîÑÎ°†Ìä∏ÏóêÏÑú Ïì∞Í∏∞ Ìé∏Ìïú Í∞ùÏ≤¥Î∞∞Ïó¥Î°ú Î≥ÄÌôò */
function normalizeEvents(jsonOrRows) {
  const rows = Array.isArray(jsonOrRows)
    ? jsonOrRows
    : jsonOrRows?.culturalEventInfo?.row || [];

  return rows.map((r, idx) => {
    const start = toISODate(r.STRTDATE || r.DATE);
    const end = toISODate(r.END_DATE || r.ENDDATE || r.END);
    const cat = toHighLevelCategory(r.CODENAME, r.THEMECODE);

    return {
      id: r.SVCID || `${Date.now()}_${idx}`,
      title: r.TITLE || r.SVCNM || "Î¨¥Ï†ú",
      category: cat,
      codename: r.CODENAME,
      gu: r.GUNAME,
      place: r.PLACE,
      org: r.ORG_NAME,
      fee: r.USE_FEE,
      target: r.USE_TRGT,
      time: r.TIME,
      homepage: r.ORG_LINK || r.HMPG_ADDR,
      img: r.MAIN_IMG,
      startDate: start ? formatDateKey(start) : null,
      endDate: end && !isNaN(end) ? formatDateKey(end) : null,
    };
  });
}

/* --- 1) Ï∂îÏ≤ú ÌñâÏÇ¨Îßå Î≥ÑÎèÑ APIÎ°ú Î°úÎî©ÌïòÎäî ÌõÖ --- */
function useRecommendedEvents(limit = 4) {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState(null);

  useEffect(() => {
    const ctrl = new AbortController();
    let mounted = true;

    (async () => {
      try {
        if (!SEOUL_KEY) {
          throw new Error(
            "REACT_APP_SEOUL_KEYÍ∞Ä ÏÑ§Ï†ïÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§ (.env ÌôïÏù∏)."
          );
        }
        setLoading(true);
        setErr(null);

        const json = await fetchSeoulRecommendedEvents({
          seoulKey: SEOUL_KEY,
          startIndex: 0,
          endIndex: limit,
          signal: ctrl.signal,
        });

        if (!mounted) return;

        const rows = json?.culturalEventInfo?.row || [];
        const items = normalizeEvents(rows);
        setEvents(items);
      } catch (e) {
        if (!mounted) return;
        setErr(e);
      } finally {
        if (!mounted) return;
        setLoading(false);
      }
    })();

    return () => {
      mounted = false;
      ctrl.abort();
    };
  }, [limit]);

  return { events, loading, error: err };
}

/* --- 2) ÌòÑÏû¨ Îã¨Î†•Ïóê Î≥¥Ïù¥Îäî ÎÇ†Ïßú(ÏµúÎåÄ 42Ïùº)Ïóê ÎåÄÌï¥ÏÑúÎßå ÏùºÎ≥Ñ API 42Í∞ú Ìò∏Ï∂úÌïòÎäî ÌõÖ --- */
function useCalendarMonth(year, month, weekStartsOn = 0, perDayLimit = 4) {
  const [dataByDay, setDataByDay] = useState({});
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState(null);

  useEffect(() => {
    const ctrl = new AbortController();
    let mounted = true;

    (async () => {
      try {
        if (!SEOUL_KEY) {
          throw new Error(
            "REACT_APP_SEOUL_KEYÍ∞Ä ÏÑ§Ï†ïÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§ (.env ÌôïÏù∏)."
          );
        }
        setLoading(true);
        setErr(null);
        setDataByDay({});

        const matrix = getMonthMatrix(year, month, weekStartsOn);
        const dates = matrix.map((cell) => formatDateKey(cell.date)); // ÏµúÎåÄ 42Í∞ú

        const results = await Promise.all(
          dates.map(async (dateKey) => {
            try {
              const json = await fetchSeoulDailyEvents({
                seoulKey: SEOUL_KEY,
                date: dateKey,
                startIndex: 0,
                endIndex: perDayLimit,
                signal: ctrl.signal,
              });

              const info = json?.culturalEventInfo;
              const rows = info?.row || [];
              const totalCount = info?.list_total_count ?? rows.length;
              const events = normalizeEvents(rows);

              return { dateKey, events, totalCount };
            } catch (e) {
              console.error("Calendar daily fetch error:", dateKey, e);
              return { dateKey, events: [], totalCount: 0 };
            }
          })
        );

        if (!mounted) return;

        const map = {};
        results.forEach(({ dateKey, events, totalCount }) => {
          map[dateKey] = { events, totalCount };
        });
        setDataByDay(map);
      } catch (e) {
        if (!mounted) return;
        setErr(e);
      } finally {
        if (!mounted) return;
        setLoading(false);
      }
    })();

    return () => {
      mounted = false;
      ctrl.abort();
    };
  }, [year, month, weekStartsOn, perDayLimit]);

  return { dataByDay, loading, error: err };
}

/* --- Calendar Ïª¥Ìè¨ÎÑåÌä∏ (Ïπ¥Îìú ÌÅ¥Î¶≠ Ïù¥Îèô X, ÎÇ†Ïßú ÌÅ¥Î¶≠ ‚Üí ÌåùÏóÖÏö© ÏΩúÎ∞±) --- */
function Calendar({
  year,
  month,
  dataByDay = {},
  weekStartsOn = 0,
  title = "Ï∫òÎ¶∞Îçî",
  onPrevMonth,
  onNextMonth,
  onDayClick,
  selectedDateKey,
}) {
  const todayKey = formatDateKey(new Date());
  const matrix = useMemo(
    () => getMonthMatrix(year, month, weekStartsOn),
    [year, month, weekStartsOn]
  );

  const weekLabels =
    weekStartsOn === 1
      ? ["Ïõî", "Ìôî", "Ïàò", "Î™©", "Í∏à", "ÌÜ†", "Ïùº"]
      : ["Ïùº", "Ïõî", "Ìôî", "Ïàò", "Î™©", "Í∏à", "ÌÜ†"];

  return (
    <div className="bg-white shadow-md rounded-lg p-4 hover:shadow-lg transition">
      <div className="flex items-center justify-between mb-3">
        <h3 className="font-semibold text-lg">{title}</h3>
        <div className="flex items-center gap-2">
          <button
            onClick={(e) => {
              e.stopPropagation();
              onPrevMonth?.();
            }}
            className="px-2 py-1 rounded border text-sm hover:bg-gray-50"
            aria-label="Ïù¥Ï†Ñ Îã¨"
          >
            ‚Üê
          </button>
          <div className="text-sm font-medium">
            {year}.{String(month + 1).padStart(2, "0")}
          </div>
          <button
            onClick={(e) => {
              e.stopPropagation();
              onNextMonth?.();
            }}
            className="px-2 py-1 rounded border text-sm hover:bg-gray-50"
            aria-label="Îã§Ïùå Îã¨"
          >
            ‚Üí
          </button>
        </div>
      </div>

      <div className="grid grid-cols-7 text-center text-xs text-gray-600 mb-1">
        {weekLabels.map((w) => (
          <div key={w} className="py-1">
            {w}
          </div>
        ))}
      </div>

      <div className="grid grid-cols-7 gap-1">
        {matrix.map(({ date, inCurrentMonth }, idx) => {
          const key = formatDateKey(date);
          const day = date.getDate();
          const dayData = inCurrentMonth
            ? dataByDay[key] || { events: [], totalCount: 0 }
            : null;
          const todaysEvents = dayData?.events || [];
          const totalCount = dayData?.totalCount ?? todaysEvents.length;

          const maxDots = 4;
          const dotEvents = todaysEvents.slice(0, maxDots);
          const extra = totalCount - dotEvents.length;

          const isPast = inCurrentMonth && key < todayKey;
          const isSelected = selectedDateKey && selectedDateKey === key;

          const baseClasses = [
            "aspect-square rounded-md border p-1 flex flex-col",
            !inCurrentMonth
              ? "bg-gray-50 text-gray-300"
              : isPast
              ? "bg-gray-50 text-gray-400"
              : "bg-white",
          ];

          if (inCurrentMonth) {
            baseClasses.push(
              "cursor-pointer hover:ring-1 hover:ring-indigo-300 hover:border-indigo-300"
            );
          }

          if (isSelected) {
            baseClasses.push("ring-2 ring-indigo-500 border-indigo-500");
          }

          return (
            <div
              key={idx}
              className={baseClasses.join(" ")}
              title={todaysEvents
                .map((e) => `${e.category} - ${e.title}`)
                .join(", ")}
              onClick={() => {
                if (!inCurrentMonth) return;
                onDayClick?.(key);
              }}
            >
              <div className="text-right text-xs">{day}</div>
              <div className="mt-auto flex flex-wrap gap-1 items-end">
                {dotEvents.map((ev, i) => (
                  <span
                    key={i}
                    className={[
                      "inline-block w-2 h-2 rounded-full",
                      CATEGORY_COLORS[ev.category] || "bg-gray-400",
                      isPast ? "opacity-50" : "",
                    ].join(" ")}
                  />
                ))}
                {extra > 0 && (
                  <span className="text-[10px] text-gray-500 ml-auto">
                    +{extra}
                  </span>
                )}
              </div>
            </div>
          );
        })}
      </div>

      <div className="mt-3 grid grid-cols-2 gap-x-4 gap-y-1 text-xs">
        {Object.entries(CATEGORY_COLORS).map(([cat, color]) => (
          <div key={cat} className="flex items-center gap-2">
            <span className={`inline-block w-2 h-2 rounded-full ${color}`} />
            <span className="text-gray-700">{cat}</span>
          </div>
        ))}
      </div>
    </div>
  );
}

/* --- HOME ÏΩòÌÖêÏ∏† (Ï∂îÏ≤ú API + Îã¨Î†•Ïö© ÏùºÎ≥Ñ API Î∂ÑÎ¶¨) --- */
function HomeContent() {
  const navigate = useNavigate();

  // 1) Ï∂îÏ≤ú ÌñâÏÇ¨
  const {
    events: recommended,
    loading: loadingRec,
    error: errorRec,
  } = useRecommendedEvents(4);

  // 2) ÌñâÏÇ¨ Ï∫òÎ¶∞Îçî
  const [cursor, setCursor] = useState(() => new Date());
  const year = cursor.getFullYear();
  const month = cursor.getMonth();

  const {
    dataByDay,
    loading: loadingCal,
    error: errorCal,
  } = useCalendarMonth(year, month, 0, 4);

  // 3) ÎÇ†Ïßú ÌÅ¥Î¶≠ Ïãú Î†àÏù¥Ïñ¥ ÌåùÏóÖ
  const [popupDateKey, setPopupDateKey] = useState(null);

  const handleDayClick = (dateKey) => {
    setPopupDateKey(dateKey);
  };

  const closePopup = () => setPopupDateKey(null);

  const popupDayData = popupDateKey ? dataByDay[popupDateKey] || {} : {};
  const popupEvents = popupDayData.events || [];
  const popupTotalCount =
    popupDayData.totalCount ?? popupEvents.length ?? 0;

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero */}
      <div className="relative h-96 w-full overflow-hidden">
        <img
          src="/images/hero.jpg"
          alt="hero"
          className="absolute inset-0 h-full w-full object-cover"
        />
      </div>

      {/* Ïπ¥ÌÖåÍ≥†Î¶¨ */}
      <section className="p-6">
        <h2 className="text-xl font-semibold mb-4">Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ ÌñâÏÇ¨ Î≥¥Í∏∞</h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {categories.map((cat) => {
            const IconComponent = cat.icon;
            return (
              <button
                key={cat.id}
                type="button"
                onClick={() =>
                  navigate(`/browse?category=${encodeURIComponent(cat.name)}`)
                }
                className="group cursor-pointer rounded-lg shadow-md hover:shadow-lg transition bg-white p-4 flex flex-col items-center justify-center focus:outline-none focus:ring-2 focus:ring-gray-200"
                aria-label={`${cat.name} Ïπ¥ÌÖåÍ≥†Î¶¨Î°ú Ïù¥Îèô`}
                title={`${cat.name}Îßå Î≥¥Í∏∞`}
              >
                <div className="flex items-center justify-center w-full h-24 mb-2">
                  <IconComponent className="w-12 h-12 text-gray-800 group-hover:scale-110 transition-transform" />
                </div>
                <div className="text-lg font-bold text-gray-800">
                  {cat.name}
                </div>
              </button>
            );
          })}
        </div>
      </section>

      {/* Ï∂îÏ≤ú + Ï∫òÎ¶∞Îçî */}
