/* ‚úÖ Ìó§Îçî Í≤ÄÏÉâÎ∞î: Í≤ÄÏÉâ ‚Üí BrowseEventsÎ°ú Ïù¥Îèô */
function SearchBar() {
  const navigate = useNavigate();
  const [query, setQuery] = useState("");

  const goSearch = () => {
    const q = query.trim();
    if (!q) return;
    navigate(`/browse?q=${encodeURIComponent(q)}`);
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") goSearch();
  };

  return (
    <div className="relative">
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        onKeyDown={handleKeyDown}
        placeholder="Í≤ÄÏÉâ"
        className="h-7 w-44 md:w-56 border rounded pl-8 pr-2 text-sm focus:outline-none focus:ring-2 focus:ring-gray-200"
        aria-label="Í≤ÄÏÉâÏñ¥ ÏûÖÎ†•"
      />
      <button
        type="button"
        onClick={goSearch}
        className="absolute right-2 top-1.5"
        aria-label="Í≤ÄÏÉâ"
        title="Í≤ÄÏÉâ"
      >
        <img
          src="/images/search.png"
          alt=""
          className="w-4 h-4 pointer-events-none select-none"
        />
      </button>
    </div>
  );
}

