const PER_PAGE = 64;

function mapRowToEvent(r, globalIndex) {
  const id = r.SVCID || `evt_${globalIndex}`;
  const title = r.TITLE || r.SVCNM || "Î¨¥Ï†ú";
  const codename = r.CODENAME;
  const themecode = r.THEMECODE;
  const gu = r.GUNAME;
  const place = r.PLACE;
  const start = r.STRTDATE || r.DATE;
  const end = r.END_DATE || r.ENDDATE || r.END;
  const homepage = r.ORG_LINK || r.HMPG_ADDR;
  const img = r.MAIN_IMG;
  const fee = r.USE_FEE;
  const category = toHighLevelCategory(codename, themecode);

  return {
    id,
    title,
    category,
    date: normalizeDateRange(start, end),
    place: place || gu || "Ïû•ÏÜå ÎØ∏ÏÉÅ",
    thumb: img || "/images/sample-event.jpg",
    homepage,
    gu,
    fee,
  };
}

function useSeoulEventsPage(page) {
  const [pages, setPages] = useState(() => ({})); // { [page]: Event[] }
  const [totalCount, setTotalCount] = useState(0);
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState(null);

  useEffect(() => {
    const ctrl = new AbortController();
    let cancelled = false;

    async function run() {
      if (!SEOUL_KEY) {
        setErr(
          new Error(
            "ÌôòÍ≤ΩÎ≥ÄÏàò REACT_APP_SEOUL_KEY Í∞Ä ÏÑ§Ï†ïÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§ (.env ÌôïÏù∏)."
          )
        );
        return;
      }

      // Ïù¥ÎØ∏ Í∞ÄÏ†∏Ïò® ÌéòÏù¥ÏßÄÎ©¥ Îã§Ïãú Ìò∏Ï∂ú Ïïà Ìï®
      if (pages[page]) return;

      setLoading(true);
      setErr(null);
      try {
        const start1 = (page - 1) * PER_PAGE + 1;
        const end1 = start1 + PER_PAGE - 1;

        const json = await fetchSeoulRecommendedEvents({
          seoulKey: SEOUL_KEY,
          startIndex: start1,
          endIndex: end1,
          signal: ctrl.signal,
        });

        if (cancelled) return;

        const info = json?.culturalEventInfo;
        const rows = info?.row || [];
        const total = info?.list_total_count ?? 0;

        const mapped = rows.map((r, idx) =>
          mapRowToEvent(r, start1 - 1 + idx)
        );

        setPages((prev) => ({ ...prev, [page]: mapped }));
        setTotalCount(total);
      } catch (e) {
        if (!cancelled) setErr(e);
      } finally {
        if (!cancelled) setLoading(false);
      }
    }

    run();

    return () => {
      cancelled = true;
      ctrl.abort();
    };
  }, [page, pages]);

  const events = pages[page] || [];
  const totalPages = Math.max(
    1,
    Math.ceil((totalCount || events.length || 1) / PER_PAGE)
  );

  return { events, loading, error: err, totalCount, totalPages };
}

