export default function BrowseEvents() {
  const [sp, setSp] = useSearchParams();

  // URL ÏøºÎ¶¨ ÎèôÍ∏∞Ìôî
  const initialCategory = decodeURIComponent(sp.get("category") || "Ï†ÑÏ≤¥");
  const initialQuery = sp.get("q") || "";

  const [category, setCategory] = useState(
    CATEGORIES.includes(initialCategory) ? initialCategory : "Ï†ÑÏ≤¥"
  );
  const [input, setInput] = useState(initialQuery);
  const [query, setQuery] = useState(initialQuery);
  const [page, setPage] = useState(1);

  // Í≤ÄÏÉâ ÏûÖÎ†• ÎîîÎ∞îÏö¥Ïä§
  useEffect(() => {
    const t = setTimeout(() => setQuery(input), 300);
    return () => clearTimeout(t);
  }, [input]);

  // Ïπ¥ÌÖåÍ≥†Î¶¨/Í≤ÄÏÉâ Î∞îÎÄåÎ©¥ 1ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
  useEffect(() => {
    setPage(1);
  }, [category, query]);

  // ÌòÑÏû¨ ÌéòÏù¥ÏßÄ Îç∞Ïù¥ÌÑ∞Îßå APIÎ°ú Î°úÎî©
  const { events: pageEvents, loading, error, totalCount, totalPages } =
    useSeoulEventsPage(page);

  // Ï¶êÍ≤®Ï∞æÍ∏∞
  const [favSet, setFavSet] = useState(
    () => new Set(loadFavs().map((x) => x.id))
  );

  // URL Ïú†ÏßÄ
  useEffect(() => {
    const next = new URLSearchParams(sp);
    if (category && category !== "Ï†ÑÏ≤¥")
      next.set("category", encodeURIComponent(category));
    else next.delete("category");
    if (query) next.set("q", query);
    else next.delete("q");
    setSp(next, { replace: true });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [category, query]);

  // ÌïÑÌÑ∞ + Í≤ÄÏÉâ (ÌòÑÏû¨ ÌéòÏù¥ÏßÄ Îç∞Ïù¥ÌÑ∞ ÏïàÏóêÏÑúÎßå)
  const deferredQuery = useDeferredValue(query);
  const filtered = useMemo(() => {
    const pool =
      category === "Ï†ÑÏ≤¥"
        ? pageEvents
        : pageEvents.filter((e) => e.category === category);
    const q = (deferredQuery || "").trim().toLowerCase();
    if (!q) return pool;
    return pool.filter(
      (e) =>
        e.title.toLowerCase().includes(q) ||
        (e.place || "").toLowerCase().includes(q)
