    }
    saveFavs(next);
    setFavSet(new Set(next.map((x) => x.id)));
  };

  return (
    <div className="min-h-screen bg-white">
      <div className="max-w-6xl mx-auto px-4 md:px-6">
        {/* ===== ÏÉÅÎã® Ïª®Ìä∏Î°§ Î∞î (sticky) ===== */}
        <div
          className="
            sticky 
            top-[48px]   /* Ìó§Îçî ÎÜíÏù¥Ïóê ÎßûÍ≤å Ï°∞Ï†ï, ÌïÑÏöîÌïòÎ©¥ 56px/72px Îì±ÏúºÎ°ú Î∞îÍøî */
            z-20
            bg-white
            pt-4 pb-3
          "
        >
          <div className="flex flex-col md:flex-row md:items-center gap-3 md:gap-4 border-b pb-3">
            <div className="w-full md:w-56">
              <label htmlFor="category" className="sr-only">
                Ïπ¥ÌÖåÍ≥†Î¶¨
              </label>
              <select
                id="category"
                value={category}
                onChange={(e) => setCategory(e.target.value)}
                className="w-full border rounded-md px-3 py-2 bg-white"
              >
                {CATEGORIES.map((c) => (
                  <option key={c} value={c}>
                    {c}
                  </option>
                ))}
              </select>
            </div>

            <form onSubmit={onSubmit} className="flex-1">
              <div className="relative">
                <input
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  placeholder="ÌñâÏÇ¨Î™Ö, Ïû•ÏÜåÎ°ú Í≤ÄÏÉâ"
                  className="w-full border rounded-md pl-3 pr-10 py-2"
                />
                <button
                  type="submit"
                  aria-label="Í≤ÄÏÉâ"
                  className="absolute right-2 top-1/2 -translate-y-1/2 p-1 rounded hover:bg-gray-50"
                >
                  <img src="/images/search.png" alt="" className="w-4 h-4" />
                </button>
              </div>
            </form>
          </div>
        </div>

        {/* ===== Î≥∏Î¨∏ Î¶¨Ïä§Ìä∏ ===== */}
        <div className="mt-4">
          <div className="border rounded-lg p-4">
            {loading && (
              <div className="h-56 grid place-items-center text-gray-500">
                Î∂àÎü¨Ïò§Îäî Ï§ë‚Ä¶
              </div>
            )}
            {error && (
              <div className="h-56 grid place-items-center text-red-600">
                Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§ÏßÄ Î™ªÌñàÏñ¥Ïöî. {String(error.message || error)}
              </div>
            )}

            {!loading && !error && filtered.length === 0 && (
              <div className="h-56 grid place-items-center text-gray-500">
                Ï°∞Í±¥Ïóê ÎßûÎäî ÌñâÏÇ¨Í∞Ä ÏóÜÏäµÎãàÎã§.
              </div>
            )}

            {!loading && !error && filtered.length > 0 && (
              <>
                <div className="grid sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
                  {filtered.map((ev) => (
                    <EventCard
                      key={ev.id}
                      ev={ev}
                      isFav={favSet.has(ev.id)}
                      onHeartToggle={onHeartToggle}
                    />
                  ))}
                </div>

                <div className="mt-6 flex items-center justify-between text-sm">
                  <div className="text-gray-600">
                    Ï¥ù {totalCount || filtered.length}Í±¥ ¬∑ {page}/{totalPages}
                    ÌéòÏù¥ÏßÄ
                  </div>
                  <div className="flex items-center gap-2">
                    <button
                      onClick={() => setPage((p) => Math.max(1, p - 1))}
                      disabled={page === 1}
                      className="px-3 py-1.5 rounded border disabled:opacity-50"
                    >
                      Ïù¥Ï†Ñ
                    </button>
                    <button
                      onClick={() =>
                        setPage((p) => Math.min(totalPages, p + 1))
                      }
                      disabled={page === totalPages}
                      className="px-3 py-1.5 rounded border disabled:opacity-50"
                    >
                      Îã§Ïùå
                    </button>
                  </div>
                </div>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

