// src/pages/MyPage.jsx
import React, { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";

/* ===== LocalStorage ===== */
const LS_FAV = "sn_favorites"; // [{id,title,place,date,category,homepage?,gu?,lat?,lng?}]
const LS_RECENT = "sn_recent";

const loadLS = (k, fb = []) => { try { return JSON.parse(localStorage.getItem(k) || JSON.stringify(fb)); } catch { return fb; } };
const saveLS = (k, v) => localStorage.setItem(k, JSON.stringify(v));

/* ÏµúÍ∑º Î≥∏ Ìï≠Î™© Ï†ÄÏû•(ÌÉÄÏûÑÏä§ÌÉ¨ÌîÑ Ìè¨Ìï®, Ï§ëÎ≥µ Ï†úÍ±∞) */
export function addRecent(item) {
  const now = new Date().toISOString();
  const base = {
    id: item.id ?? String(item.id ?? ""),
    title: item.title ?? "",
    date: item.date ?? "",
    place: item.place ?? "",
    category: item.category ?? "",
    seenAt: now,
  };
  const arr = loadLS(LS_RECENT, []);
  const withoutDup = arr.filter((x) => x.id !== base.id);
  const next = [base, ...withoutDup].slice(0, 10);
  saveLS(LS_RECENT, next);
  return next;
}

/* ===== Kakao Maps loader ===== */
const FALLBACK_APPKEY = "2ee5022c1da6fc178bd51ad4042556fb";
const loadKakao = () =>
  new Promise((resolve, reject) => {
    if (window.kakao && window.kakao.maps) { resolve(window.kakao); return; }
    const key = process.env.REACT_APP_KAKAO_MAP_KEY || FALLBACK_APPKEY;
    const ID = "kakao-maps-sdk";
    const exist = document.getElementById(ID);
    const onLoaded = () => {
      try { window.kakao.maps.load(() => resolve(window.kakao)); }
      catch (e) { reject(e); }
    };
    if (exist) {
      exist.addEventListener("load", onLoaded, { once: true });
      exist.addEventListener("error", reject, { once: true });
      return;
    }
    const s = document.createElement("script");
    s.id = ID;
    s.async = true;
    s.src = `https://dapi.kakao.com/v2/maps/sdk.js?appkey=${key}&libraries=services,clusterer&autoload=false`;
    s.onload = onLoaded;
    s.onerror = reject;
    document.head.appendChild(s);
  });

/* ===== Ï¢åÌëú Ï∫êÏãú ===== */
const LS_GEO = "sn_geo_cache_v1";
const loadGeoCache = () => { try { return JSON.parse(localStorage.getItem(LS_GEO) || "{}"); } catch { return {}; } };
const saveGeoCache = (obj) => localStorage.setItem(LS_GEO, JSON.stringify(obj));

/* ===== Íµ¨ Ï§ëÏã¨(Î∞±ÏóÖ) ===== */
const GU_CENTER = {
  Ï¢ÖÎ°úÍµ¨:[37.5730,126.9794], Ï§ëÍµ¨:[37.5636,126.9976], Ïö©ÏÇ∞Íµ¨:[37.5326,126.9905], ÏÑ±ÎèôÍµ¨:[37.5636,127.0364],
  Í¥ëÏßÑÍµ¨:[37.5386,127.0822], ÎèôÎåÄÎ¨∏Íµ¨:[37.5744,127.0396], Ï§ëÎûëÍµ¨:[37.6060,127.0929], ÏÑ±Î∂ÅÍµ¨:[37.5894,127.0167],
  Í∞ïÎ∂ÅÍµ¨:[37.6396,127.0257], ÎèÑÎ¥âÍµ¨:[37.6688,127.0471], ÎÖ∏ÏõêÍµ¨:[37.6542,127.0568], ÏùÄÌèâÍµ¨:[37.6176,126.9227],
  ÏÑúÎåÄÎ¨∏Íµ¨:[37.5791,126.9368], ÎßàÌè¨Íµ¨:[37.5665,126.9018], ÏñëÏ≤úÍµ¨:[37.5169,126.8665], Í∞ïÏÑúÍµ¨:[37.5510,126.8495],
  Íµ¨Î°úÍµ¨:[37.4954,126.8874], Í∏àÏ≤úÍµ¨:[37.4599,126.9001], ÏòÅÎì±Ìè¨Íµ¨:[37.5263,126.8963], ÎèôÏûëÍµ¨:[37.5124,126.9393],
  Í¥ÄÏïÖÍµ¨:[37.4784,126.9516], ÏÑúÏ¥àÍµ¨:[37.4836,127.0326], Í∞ïÎÇ®Íµ¨:[37.5173,127.0473], ÏÜ°ÌååÍµ¨:[37.5112,127.0980],
  Í∞ïÎèôÍµ¨:[37.5301,127.1238],
};

/* ===== Ïú†Ìã∏: ÏµúÍ∑º Î≥∏ Îç∞Ïù¥ÌÑ∞ Ï†ïÎ¶¨(ÎàÑÎùΩ Î∞©ÏßÄ) ===== */
function normalizeRecent(rawArr) {
  const arr = Array.isArray(rawArr) ? rawArr : [];
  // ÏµúÏÜå ÌïÑÎìú Î≥¥Ï†ï + seenAt ÏóÜÏúºÎ©¥ ÏµúÍ∑º ÏãúÍ∞ÅÏúºÎ°ú Î≥¥Ï†ï
  const fixed = arr.map((x) => ({
    id: String(x.id ?? ""),
    title: x.title ?? "",
    date: x.date ?? "",
    place: x.place ?? "",
    category: x.category ?? "",
    seenAt: x.seenAt ?? new Date().toISOString(),
  }));
  // Îπà id/title Ï†úÍ±∞
  const valid = fixed.filter((x) => x.id && x.title);
  // Ï§ëÎ≥µ Ï†úÍ±∞ (id Í∏∞Ï§Ä)
  const dedup = [];
  const vis = new Set();
  for (const it of valid) {
    if (vis.has(it.id)) continue;
    vis.add(it.id);
    dedup.push(it);
  }
  // ÏµúÏã†Ïàú Ï†ïÎ†¨
  dedup.sort((a, b) => new Date(b.seenAt) - new Date(a.seenAt));
  return dedup.slice(0, 50);
}

export default function MyPage() {
  const navigate = useNavigate();

  const [recent, setRecent] = useState(() => normalizeRecent(loadLS(LS_RECENT, [])));
  const [favs, setFavs]     = useState(() => loadLS(LS_FAV, []));
  const [geoFavs, setGeoFavs] = useState([]);

  // Kakao map refs
  const mapEl = useRef(null);
  const mapRef = useRef(null);
  const kakaoRef = useRef(null);
  const clusterRef = useRef(null);
  const markersRef = useRef([]);
  const infoRef = useRef([]);

  /* ÏßÄÎèÑ Ï¥àÍ∏∞Ìôî */
  useEffect(() => {
    let off = false;
    (async () => {
      const kakao = await loadKakao();
      if (off) return;
      kakaoRef.current = kakao;
      const center = new kakao.maps.LatLng(37.5665, 126.9780);
      mapRef.current = new kakao.maps.Map(mapEl.current, { center, level: 7 });
    })();
    return () => { off = true; };
  }, []);

  /* Ï¶êÍ≤®Ï∞æÍ∏∞ ‚Üí Ï¢åÌëú Ï±ÑÏö∞Í∏∞ (Î≥¥Í∞ï Î≤ÑÏ†Ñ) */
  useEffect(() => {
    const kakao = kakaoRef.current;
    if (!kakao) return;
    const SEOUL_CENTER = new kakao.maps.LatLng(37.5665,126.9780);
    const ps = new kakao.maps.services.Places();
    const geocoder = new kakao.maps.services.Geocoder();
    const cache = loadGeoCache();

    let cancelled = false;

    const pickSeoul = (data, gu) => {
      const seoul = data.filter(d => (d.address_name || "").startsWith("ÏÑúÏö∏"));
      if (gu) {
        const inGu = seoul.filter(d => d.address_name.includes(gu));
        return inGu[0] || seoul[0] || null;
      }
      return seoul[0] || null;
    };

    const isBad = (t="") => /Ïò®ÎùºÏù∏|ÎπÑÎåÄÎ©¥|ÎØ∏Ï†ï|ÏóÜÏùå/i.test(String(t));

    const kwSearch = (keyword, gu) => new Promise(res => {
      if (!keyword) return res(null);
      ps.keywordSearch(
        keyword,
        (data, status) => {
          if (status === kakao.maps.services.Status.OK && data.length) {
            const best = pickSeoul(data, gu);
            if (best) return res({ lat: Number(best.y), lng: Number(best.x) });
          }
          res(null);
        },
        { location: SEOUL_CENTER, radius: 60000 }
      );
    });

    const addrSearch = (addr) => new Promise(res => {
      if (!addr) return res(null);
      geocoder.addressSearch(addr, (result, status) => {
        if (status === kakao.maps.services.Status.OK && result[0]) {
          const r = result.find(r => r.address_name.startsWith("ÏÑúÏö∏")) || result[0];
          if (r) return res({ lat: Number(r.y), lng: Number(r.x) });
        }
        res(null);
      });
    });

    (async () => {
      const out = [];
      for (const f of favs) {
        if (typeof f.lat === "number" && typeof f.lng === "number") { out.push(f); continue; }

        const cacheKey = `${f.place || ""}|${f.title || ""}`;
        if (cache[cacheKey]) { out.push({ ...f, ...cache[cacheKey] }); continue; }

        let coords = null;

        // 1) placeÎ°ú ÌÇ§ÏõåÎìú/Ï£ºÏÜå Í≤ÄÏÉâ
        if (f.place && !isBad(f.place)) {
          coords = await kwSearch(f.place, f.gu);
          if (!coords) coords = await addrSearch(f.place);
        }

        // 2) place Ïã§Ìå® Ïãú titleÎ°ú ÌÇ§ÏõåÎìú Í≤ÄÏÉâ
        if (!coords && f.title) {
          coords = await kwSearch(f.title, f.gu);
        }

        // 3) Íµ¨ Ï§ëÏã¨ ‚Üí ÏÑúÏö∏ÏãúÏ≤≠
        if (!coords && f.gu && GU_CENTER[f.gu]) {
          const [lat, lng] = GU_CENTER[f.gu];
          coords = { lat, lng };
        }
        if (!coords) coords = { lat: 37.5665, lng: 126.9780 };

        cache[cacheKey] = coords;
        out.push({ ...f, ...coords });

        await new Promise(r => setTimeout(r, 20));
        if (cancelled) return;
      }
      saveGeoCache(cache);
      if (!cancelled) setGeoFavs(out);
    })();

    return () => { cancelled = true; };
  }, [favs]);

  /* ÎßàÏª§ + ÌÅ¥Îü¨Ïä§ÌÑ∞ + Ìà¥ÌåÅ + ÌÅ¥Î¶≠ Ïù¥Îèô (ÌÅ¥Îü¨Ïä§ÌÑ∞Îü¨ Ïò¨Î∞îÎ•∏ Ìå®ÌÑ¥) */
  useEffect(() => {
    const kakao = kakaoRef.current;
    const map = mapRef.current;
    if (!kakao || !map) return;

    // cleanup
    markersRef.current.forEach(m => m.setMap(null));
    infoRef.current.forEach(i => i.close());
    markersRef.current = [];
    infoRef.current = [];
    if (clusterRef.current) {
      clusterRef.current.clear();
      clusterRef.current = null;
    }

    const list = geoFavs.filter(f => f.lat != null && f.lng != null);
    if (list.length === 0) return;

    const markers = [];
    const infos = [];
    const bounds = new kakao.maps.LatLngBounds();

    list.forEach((f) => {
      const pos = new kakao.maps.LatLng(f.lat, f.lng);
      bounds.extend(pos);

      const marker = new kakao.maps.Marker({ position: pos, title: f.title });
      // ÌÅ¥Îü¨Ïä§ÌÑ∞Îü¨Í∞Ä Í¥ÄÎ¶¨Ìï† Í≤ÉÏù¥ÎØÄÎ°ú marker.setMap(map) Ìò∏Ï∂úÌïòÏßÄ ÏïäÏùå

      const iwHtml = `
        <div style="padding:8px 10px; font-size:12px; max-width:220px;">
          <div style="font-weight:600; margin-bottom:4px;">${f.title}</div>
          <div style="color:#666;">${f.place || ""}</div>
          <div style="color:#888; margin-top:2px;">${f.date || ""} ¬∑ ${f.category || ""}</div>
        </div>
      `;
      const iw = new kakao.maps.InfoWindow({ content: iwHtml });

      kakao.maps.event.addListener(marker, "mouseover", () => {
        infos.forEach((i) => i.close());
        iw.open(map, marker);
      });
      kakao.maps.event.addListener(marker, "mouseout", () => iw.close());

      kakao.maps.event.addListener(marker, "click", () => {
        if (f.id) navigate(`/events/${encodeURIComponent(f.id)}`);
      });

      markers.push(marker);
      infos.push(iw);
    });

    markersRef.current = markers;
    infoRef.current = infos;

    // Ïò¨Î∞îÎ•∏ ÌÅ¥Îü¨Ïä§ÌÑ∞Îü¨ ÏÇ¨Ïö©
    const clusterer = new kakao.maps.MarkerClusterer({
      map,
      averageCenter: true,
      minLevel: 6,
      disableClickZoom: false,
    });
    clusterer.addMarkers(markers);
    clusterRef.current = clusterer;

    map.setBounds(bounds, 40, 40, 40, 40);
  }, [geoFavs, navigate]);

  // Ïä§ÌÜ†Î¶¨ÏßÄ Î≥ÄÌôî Î∞òÏòÅ(ÏµúÍ∑º/Ï¶êÍ≤®Ï∞æÍ∏∞ ÎèôÍ∏∞Ìôî)
  useEffect(() => {
    const onStorage = (e) => {
      if (e.key === LS_RECENT) setRecent(normalizeRecent(loadLS(LS_RECENT, [])));
      if (e.key === LS_FAV)    setFavs(loadLS(LS_FAV, []));
    };
    window.addEventListener("storage", onStorage);
    return () => window.removeEventListener("storage", onStorage);
  }, []);

  /* ===== Ï¶êÍ≤®Ï∞æÍ∏∞ ÌÜ†Í∏Ä(ÌïòÌä∏ ÌÅ¥Î¶≠ Ïãú Ï†úÍ±∞) ===== */
  const toggleFav = (item) => {
    const list = loadLS(LS_FAV, []);
    const exists = list.some((x) => x.id === item.id);
    const next = exists ? list.filter((x) => x.id !== item.id) : [...list, item];
    saveLS(LS_FAV, next);
    setFavs(next); // ÏÉÅÌÉú Í∞±Ïã† -> ÏßÄÎèÑ/Î¶¨Ïä§Ìä∏ ÏûêÎèô Î∞òÏòÅ
  };

  /* ÏµúÍ∑º Î™©Î°ù ÏàòÎèô ÏÉàÎ°úÍ≥†Ïπ® Î≤ÑÌäº(ÎîîÎ≤ÑÍ∑∏/ÏïàÏ†ÑÏö©) */
  const refreshRecent = () => setRecent(normalizeRecent(loadLS(LS_RECENT, [])));

  return (
    <div className="min-h-screen bg-white px-6 py-8 max-w-7xl mx-auto">
      <div className="grid grid-cols-1 lg:grid-cols-12 gap-8">
        {/* ÏµúÍ∑º Î≥∏ Ìï≠Î™© */}
        <section className="lg:col-span-6">
          <div className="flex items-center justify-between mb-3">
            <h2 className="text-lg font-semibold">ÏµúÍ∑º Î≥∏ Ìï≠Î™©</h2>
            <div className="flex items-center gap-3">
              <button
                className="text-xs underline text-gray-500"
                onClick={refreshRecent}
                title="ÏÉàÎ°úÍ≥†Ïπ®"
              >
                ÏÉàÎ°úÍ≥†Ïπ®
              </button>
              {recent.length > 0 && (
                <button
                  className="text-xs underline text-gray-500"
                  onClick={() => { saveLS(LS_RECENT, []); setRecent([]); }}
                >
                  ÎπÑÏö∞Í∏∞
                </button>
              )}
            </div>
          </div>
          {recent.length === 0 ? (
            <div className="h-[420px] grid place-items-center bg-gray-100 border rounded text-gray-500">
              ÏµúÍ∑º Î≥∏ Ìï≠Î™©Ïù¥ ÏóÜÏäµÎãàÎã§.
            </div>
          ) : (
            <div className="border rounded p-3 h-[420px] overflow-auto">
              <ul className="space-y-2">
                {recent.map((it) => (
                  <li key={it.id}>
                    <button
                      onClick={() => navigate(`/events/${encodeURIComponent(it.id)}`)}
                      className="w-full text-left px-3 py-2 rounded hover:bg-gray-50 border"
                    >
                      <div className="font-medium truncate">{it.title}</div>
                      <div className="text-xs text-gray-600 truncate">
                        üìÖ {it.date || "ÏùºÏ†ï ÎØ∏Ï†ï"} ¬∑ üìç {it.place || "Ïû•ÏÜå ÎØ∏Ï†ï"} ¬∑ {it.category || ""}
                      </div>
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </section>

        {/* My Favorite (ÏßÄÎèÑ) */}
        <section className="lg:col-span-6">
          <div className="flex items-center justify-between mb-3">
            <h2 className="text-lg font-semibold">My Favorite</h2>
            {favs.length > 0 && (
              <button
                className="text-xs underline text-gray-500"
                onClick={() => { saveLS(LS_FAV, []); setFavs([]); }}
              >
                Ï†ÑÏ≤¥ Ìï¥Ï†ú
              </button>
            )}
          </div>

          <div
            ref={mapEl}
            className="relative border rounded-lg bg-gray-100 h-[420px] overflow-hidden"
            aria-label="Ï¶êÍ≤®Ï∞æÍ∏∞ ÏßÄÎèÑ"
            role="region"
          >
            {!kakaoRef.current && (
              <div className="absolute inset-0 grid place-items-center text-gray-500 text-sm">
                ÏßÄÎèÑÎ•º Î∂àÎü¨Ïò§Îäî Ï§ë‚Ä¶
              </div>
            )}
          </div>

          {/* Ï¶êÍ≤®Ï∞æÍ∏∞ Í∞ÑÎã® Î¶¨Ïä§Ìä∏ (ÌïòÌä∏ ÌÜ†Í∏Ä ÏßÄÏõê) */}
          <ul className="mt-3 text-sm text-gray-700 space-y-1">
            {favs.map((f) => (
              <li key={f.id} className="flex items-center gap-2">
                <button
                  onClick={() => toggleFav(f)}
                  title="Ï¶êÍ≤®Ï∞æÍ∏∞ Ìï¥Ï†ú"
                  aria-label="Ï¶êÍ≤®Ï∞æÍ∏∞ Ìï¥Ï†ú"
                  className="leading-none"
                >
                  ‚ù§Ô∏è
                </button>
                <button
                  className="truncate text-left hover:underline"
                  onClick={() => navigate(`/events/${encodeURIComponent(f.id)}`)}
                >
                  <b>{f.title}</b> ¬∑ {f.date || "ÏùºÏ†ï ÎØ∏Ï†ï"} ¬∑ {f.place || "Ïû•ÏÜå ÎØ∏Ï†ï"} ({f.category || ""})
                </button>
              </li>
            ))}
            {favs.length === 0 && <li className="text-gray-500">Ï¶êÍ≤®Ï∞æÍ∏∞Ìïú ÌñâÏÇ¨Í∞Ä ÏóÜÏäµÎãàÎã§.</li>}
          </ul>
        </section>
      </div>
    </div>
  );
}
