// src/pages/BrowseEvents.jsx
import React, {
  useEffect,
  useMemo,
  useState,
  useDeferredValue,
} from "react";
import { useSearchParams } from "react-router-dom";
import { fetchSeoulRecommendedEvents } from "../lib/seoulApi";
import { addRecent } from "./MyPage";

/* === .env ÌÇ§ === */
const SEOUL_KEY = (process.env.NEXT_PUBLIC_SEOUL_KEY || "").trim();

/* === Ïπ¥ÌÖåÍ≥†Î¶¨ === */
const CATEGORIES = ["Ï†ÑÏ≤¥", "Í≥µÏó∞", "Ï†ÑÏãú", "ÍµêÏú°/Ï≤¥Ìóò", "Í∏∞ÌÉÄ"];

/* === ÏÉÅÏúÑ Ïπ¥ÌÖåÍ≥†Î¶¨ Îß§Ìïë (App.js ÏôÄ ÎèôÏùº Í∑úÏπô) === */
function toHighLevelCategory(codename = "", themecode = "") {
  const c = String(codename || "");
  const t = String(themecode || "");

  // Í≥µÏó∞
  if (
    [
      "Í≥µÏó∞",
      "ÏΩòÏÑúÌä∏",
      "ÌÅ¥ÎûòÏãù",
      "Íµ≠ÏïÖ",
      "Î¨¥Ïö©",
      "Ïó∞Í∑π",
      "ÎÆ§ÏßÄÏª¨",
      "Ïò§ÌéòÎùº",
      "ÏùåÏïÖÌöå",
      "ÌéòÏä§Ìã∞Î≤å",
      "Ï∂ïÏ†ú",
    ].some((k) => c.includes(k))
  ) {
    return "Í≥µÏó∞";
  }

  // Ï†ÑÏãú
  if (["Ï†ÑÏãú", "ÎØ∏Ïà†", "Í∞§Îü¨Î¶¨", "ÏïÑÌä∏", "ÏÇ¨ÏßÑÏ†Ñ"].some((k) => c.includes(k))) {
    return "Ï†ÑÏãú";
  }

  // ÍµêÏú°/Ï≤¥Ìóò
  if (
    ["ÍµêÏú°", "Ï≤¥Ìóò", "ÏõåÌÅ¨Ïàç", "ÏõåÌÅ¨ÏÉµ", "Í∞ïÏ¢å", "Í∞ïÏùò", "ÏÑ∏ÎØ∏ÎÇò", "Í∞ïÏó∞"].some(
      (k) => c.includes(k)
    ) ||
    t.includes("ÍµêÏú°")
  ) {
    return "ÍµêÏú°/Ï≤¥Ìóò";
  }

  return "Í∏∞ÌÉÄ";
}

/* === ÎÇ†Ïßú ÌëúÍ∏∞ Ïú†Ìã∏ === */
function normalizeDateRange(startStr = "", endStr = "") {
  const s = (startStr || "").replaceAll(".", "-");
  const e = (endStr || "").replaceAll(".", "-");
  if (!s && !e) return "ÏùºÏ†ï ÎØ∏Ï†ï";
  if (s && e) return `${s} ~ ${e}`;
  return s || e;
}

/* === Ï¶êÍ≤®Ï∞æÍ∏∞ Î°úÏª¨Ïä§ÌÜ†Î¶¨ÏßÄ === */
const LS_KEY_FAV = "sn_favorites";
function loadFavs() {
  try {
    const raw = localStorage.getItem(LS_KEY_FAV);
    return raw ? JSON.parse(raw) : [];
  } catch {
    return [];
  }
}
function saveFavs(list) {
  localStorage.setItem(LS_KEY_FAV, JSON.stringify(list));
}

/* =========================
   ÌéòÏù¥ÏßÄ Îã®ÏúÑ API Î°úÎî© (Î¨¥Ìïú "Îçî Î≥¥Í∏∞" Î∞©Ïãù)
========================= */
const PER_PAGE = 64;

function mapRowToEvent(r, globalIndex) {
  const id = r.SVCID || `evt_${globalIndex}`;
  const title = r.TITLE || r.SVCNM || "Î¨¥Ï†ú";
  const codename = r.CODENAME;
  const themecode = r.THEMECODE;
  const gu = r.GUNAME;
  const place = r.PLACE;
  const start = r.STRTDATE || r.DATE;
  const end = r.END_DATE || r.ENDDATE || r.END;
  const homepage = r.ORG_LINK || r.HMPG_ADDR;
  const img = r.MAIN_IMG;
  const fee = r.USE_FEE;
  const category = toHighLevelCategory(codename, themecode);

  return {
    id,
    title,
    category,
    date: normalizeDateRange(start, end),
    place: place || gu || "Ïû•ÏÜå ÎØ∏ÏÉÅ",
    thumb: img || "/images/sample-event.jpg",
    homepage,
    gu,
    fee,
  };
}

/**
 * Ï†ÑÏ≤¥ Îç∞Ïù¥ÌÑ∞Î•º "ÌéòÏù¥ÏßÄ Îã®ÏúÑÎ°ú Ï†êÏ†ê Îçî ÎßéÏù¥" Î∂àÎü¨ÏôÄÏÑú
 * ÎàÑÏ†Å(allEvents) + Í≤ÄÏÉâ/ÌïÑÌÑ∞Ïóê ÏÇ¨Ïö©.
 */
function useSeoulEventsInfinite() {
  const [pages, setPages] = useState(() => ({})); // { [page]: Event[] }
  const [currentPage, setCurrentPage] = useState(1);
  const [totalCount, setTotalCount] = useState(0);
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState(null);

  useEffect(() => {
    const page = currentPage;
    const ctrl = new AbortController();
    let cancelled = false;

    async function run() {
      if (!SEOUL_KEY) {
        setErr(
          new Error(
            "ÌôòÍ≤ΩÎ≥ÄÏàò REACT_APP_SEOUL_KEY Í∞Ä ÏÑ§Ï†ïÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§ (.env ÌôïÏù∏)."
          )
        );
        return;
      }

      // Ïù¥ÎØ∏ Í∞ÄÏ†∏Ïò® ÌéòÏù¥ÏßÄÎ©¥ Îã§Ïãú Ìò∏Ï∂ú Ïïà Ìï®
      if (pages[page]) return;

      setLoading(true);
      setErr(null);
      try {
        const start1 = (page - 1) * PER_PAGE + 1;
        const end1 = start1 + PER_PAGE - 1;

        const json = await fetchSeoulRecommendedEvents({
          seoulKey: SEOUL_KEY,
          startIndex: start1,
          endIndex: end1,
          signal: ctrl.signal,
        });

        if (cancelled) return;

        const info = json?.culturalEventInfo;
        const rows = info?.row || [];
        const total = info?.list_total_count ?? 0;

        const mapped = rows.map((r, idx) =>
          mapRowToEvent(r, start1 - 1 + idx)
        );

        setPages((prev) => ({ ...prev, [page]: mapped }));
        setTotalCount(total);
      } catch (e) {
        if (!cancelled) setErr(e);
      } finally {
        if (!cancelled) setLoading(false);
      }
    }

    run();

    return () => {
      cancelled = true;
      ctrl.abort();
    };
  }, [currentPage, pages]);

  // ÏßÄÍ∏àÍπåÏßÄ Î°úÎî©Îêú Î™®Îì† ÌéòÏù¥ÏßÄÎ•º Ìï©Ïπú Î∞∞Ïó¥
  const allEvents = useMemo(() => {
    const arr = [];
    for (let p = 1; p <= currentPage; p++) {
      if (pages[p]) arr.push(...pages[p]);
    }
    return arr;
  }, [pages, currentPage]);

  const totalPages = totalCount
    ? Math.max(1, Math.ceil(totalCount / PER_PAGE))
    : null;

  const hasMore =
    totalPages == null // ÏïÑÏßÅ totalCount Î™®Î•¥Î©¥ Îçî ÏûàÎäî Í±∏Î°ú Í∞ÄÏ†ï
      ? true
      : currentPage < totalPages;

  const loadMore = () => {
    if (loading) return;
    if (!hasMore) return;
    setCurrentPage((p) => p + 1);
  };

  return {
    events: allEvents,
    loading,
    error: err,
    totalCount,
    totalPages,
    currentPage,
    hasMore,
    loadMore,
  };
}

/* =========================
   ÌéòÏù¥ÏßÄ Ïª¥Ìè¨ÎÑåÌä∏
========================= */
export default function BrowseEvents() {
  const [sp, setSp] = useSearchParams();

  // --- URL ÏøºÎ¶¨ÏóêÏÑú Ï¥àÍ∏∞Í∞í Ï∂îÏ∂ú ---
  const categoryParam = sp.get("category");
  const qParam = sp.get("q") || "";

  const [category, setCategory] = useState(
    categoryParam && CATEGORIES.includes(categoryParam)
      ? categoryParam
      : "Ï†ÑÏ≤¥"
  );
  const [input, setInput] = useState(qParam); // ÏûÖÎ†•Ï∞Ω Í∞í
  const [query, setQuery] = useState(qParam); // Ïã§Ï†ú Í≤ÄÏÉâÏóê Ïì∞Îäî Í∞í

  // ‚úÖ URL(searchParams)Ïù¥ Î∞îÎÄî ÎïåÎßàÎã§ ÏÉÅÌÉú Ïû¨ÎèôÍ∏∞Ìôî
  // (HOME/Ìó§Îçî Í≤ÄÏÉâÏóêÏÑú /browse?q=... Î°ú Îì§Ïñ¥Ïò§Îäî Í≤ΩÏö∞ Ìè¨Ìï®)
  useEffect(() => {
    const urlCategory = sp.get("category");
    const urlQ = sp.get("q") || "";

    const nextCategory =
      urlCategory && CATEGORIES.includes(urlCategory)
        ? urlCategory
        : "Ï†ÑÏ≤¥";

    setCategory(nextCategory);
    setInput(urlQ);
    setQuery(urlQ);
  }, [sp]);

  // Í≤ÄÏÉâ ÏûÖÎ†• ÎîîÎ∞îÏö¥Ïä§ ‚Üí queryÎ°ú Î∞òÏòÅ
  useEffect(() => {
    const t = setTimeout(() => setQuery(input), 300);
    return () => clearTimeout(t);
  }, [input]);

  // URL Ïú†ÏßÄ (Ïπ¥ÌÖåÍ≥†Î¶¨ / Í≤ÄÏÉâÏñ¥ -> ÏøºÎ¶¨Ïä§Ìä∏ÎßÅ Î∞òÏòÅ)
  useEffect(() => {
    const next = new URLSearchParams(sp);
    if (category && category !== "Ï†ÑÏ≤¥") next.set("category", category);
    else next.delete("category");

    if (query) next.set("q", query);
    else next.delete("q");

    setSp(next, { replace: true });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [category, query]);

  // Ï†ÑÏ≤¥ Îç∞Ïù¥ÌÑ∞ Î¨¥Ìïú Î°úÎî© ÌõÖ
  const {
    events: allEvents,
    loading,
    error,
    totalCount,
    hasMore,
    loadMore,
  } = useSeoulEventsInfinite();

  const deferredQuery = useDeferredValue(query);

  // Ïπ¥ÌÖåÍ≥†Î¶¨ + Í≤ÄÏÉâ ÌïÑÌÑ∞ (ÌòÑÏû¨ÍπåÏßÄ Î°úÎî©Îêú Ï†ÑÏ≤¥ Îç∞Ïù¥ÌÑ∞ÏóêÏÑú ÏàòÌñâ)
  const filtered = useMemo(() => {
    const pool =
      category === "Ï†ÑÏ≤¥"
        ? allEvents
        : allEvents.filter((e) => e.category === category);

    const q = (deferredQuery || "").trim().toLowerCase();
    if (!q) return pool;

    return pool.filter(
      (e) =>
        e.title.toLowerCase().includes(q) ||
        (e.place || "").toLowerCase().includes(q)
    );
  }, [allEvents, category, deferredQuery]);

  const initialLoading = loading && allEvents.length === 0;

  const onSubmit = (e) => {
    e.preventDefault();
    setQuery(input);
  };

  // Ï¶êÍ≤®Ï∞æÍ∏∞
  const [favSet, setFavSet] = useState(
    () => new Set(loadFavs().map((x) => x.id))
  );

  const onHeartToggle = (ev) => {
    const list = loadFavs();
    const exists = list.some((x) => x.id === ev.id);
    let next;
    if (exists) {
      next = list.filter((x) => x.id !== ev.id);
    } else {
      next = [
        ...list,
        {
          id: ev.id,
          title: ev.title,
          category: ev.category,
          date: ev.date,
          place: ev.place,
          thumb: ev.thumb,
          homepage: ev.homepage,
          gu: ev.gu,
          lat: ev.lat ?? null,
          lng: ev.lng ?? null,
        },
      ];
    }
    saveFavs(next);
    setFavSet(new Set(next.map((x) => x.id)));
  };

  const isFiltering =
    category !== "Ï†ÑÏ≤¥" || (deferredQuery || "").trim().length > 0;

  return (
    <div className="min-h-screen bg-white">
      <div className="max-w-6xl mx-auto px-4 md:px-6">
        {/* ===== ÏÉÅÎã® Ïª®Ìä∏Î°§ Î∞î (sticky) ===== */}
        <div
          className="
            sticky 
            top-[48px]   /* Ìó§Îçî ÎÜíÏù¥Ïóê ÎßûÍ≤å Ï°∞Ï†ï */
            z-20
            bg-white
            pt-4 pb-3
          "
        >
          <div className="flex flex-col md:flex-row md:items-center gap-3 md:gap-4 border-b pb-3">
            <div className="w-full md:w-56">
              <label htmlFor="category" className="sr-only">
                Ïπ¥ÌÖåÍ≥†Î¶¨
              </label>
              <select
                id="category"
                value={category}
                onChange={(e) => setCategory(e.target.value)}
                className="w-full border rounded-md px-3 py-2 bg-white"
              >
                {CATEGORIES.map((c) => (
                  <option key={c} value={c}>
                    {c}
                  </option>
                ))}
              </select>
            </div>

            <form onSubmit={onSubmit} className="flex-1">
              <div className="relative">
                <input
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  placeholder="ÌñâÏÇ¨Î™Ö, Ïû•ÏÜåÎ°ú Í≤ÄÏÉâ"
                  className="w-full border rounded-md pl-3 pr-10 py-2"
                />
                <button
                  type="submit"
                  aria-label="Í≤ÄÏÉâ"
                  className="absolute right-2 top-1/2 -translate-y-1/2 p-1 rounded hover:bg-gray-50"
                >
                  <img src="/images/search.png" alt="" className="w-4 h-4" />
                </button>
              </div>
            </form>
          </div>
        </div>

        {/* ===== Î≥∏Î¨∏ Î¶¨Ïä§Ìä∏ ===== */}
        <div className="mt-4">
          <div className="border rounded-lg p-4">
            {initialLoading && (
              <div className="h-56 grid place-items-center text-gray-500">
                Î∂àÎü¨Ïò§Îäî Ï§ë‚Ä¶
              </div>
            )}

            {error && !initialLoading && (
              <div className="h-56 grid place-items-center text-red-600">
                Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§ÏßÄ Î™ªÌñàÏñ¥Ïöî. {String(error.message || error)}
              </div>
            )}

            {!initialLoading && !error && filtered.length === 0 && (
              <div className="h-56 grid place-items-center text-gray-500">
                Ï°∞Í±¥Ïóê ÎßûÎäî ÌñâÏÇ¨Í∞Ä ÏóÜÏäµÎãàÎã§.
              </div>
            )}

            {!initialLoading && !error && filtered.length > 0 && (
              <>
                <div className="grid sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
                  {filtered.map((ev) => (
                    <EventCard
                      key={ev.id}
                      ev={ev}
                      isFav={favSet.has(ev.id)}
                      onHeartToggle={onHeartToggle}
                    />
                  ))}
                </div>

                <div className="mt-6 flex flex-col md:flex-row md:items-center md:justify-between gap-3 text-sm">
                  <div className="text-gray-600">
                    {isFiltering ? (
                      <>
                        Í≤ÄÏÉâ Í≤∞Í≥º{" "}
                        <strong className="font-semibold">
                          {filtered.length}
                        </strong>
                        Í±¥
                        <span className="text-xs text-gray-500 ml-1">
                          (ÌòÑÏû¨ Î°úÎî©Îêú Îç∞Ïù¥ÌÑ∞ {allEvents.length}Í±¥ Í∏∞Ï§Ä)
                        </span>
                      </>
                    ) : totalCount ? (
                      <>
                        Ï¥ù{" "}
                        <strong className="font-semibold">
                          {totalCount}
                        </strong>
                        Í±¥ Ï§ë{" "}
                        <span className="font-semibold">
                          {allEvents.length}
                        </span>
                        Í±¥ÏùÑ Î∂àÎü¨ÏôîÏäµÎãàÎã§.
                      </>
                    ) : (
                      <>
                        ÌòÑÏû¨{" "}
                        <span className="font-semibold">
                          {allEvents.length}
                        </span>
                        Í±¥ÏùÑ Î∂àÎü¨ÏôîÏäµÎãàÎã§.
                      </>
                    )}
                  </div>
                  <div className="flex items-center gap-2 justify-end">
                    {hasMore ? (
                      <button
                        onClick={loadMore}
                        disabled={loading}
                        className="px-4 py-1.5 rounded border bg-white hover:bg-gray-50 disabled:opacity-50"
                      >
                        {loading ? "Îçî Î∂àÎü¨Ïò§Îäî Ï§ë‚Ä¶" : "Îçî Î≥¥Í∏∞"}
                      </button>
                    ) : (
                      <span className="text-gray-400">
                        ÎßàÏßÄÎßâÍπåÏßÄ Î™®Îëê Î∂àÎü¨ÏôîÏäµÎãàÎã§.
                      </span>
                    )}
                  </div>
                </div>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

/* Î©îÎ™®ized Ïπ¥Îìú ÏïÑÏù¥ÌÖú */
const EventCard = React.memo(function EventCard({
  ev,
  isFav,
  onHeartToggle,
}) {
  return (
    <div className="relative text-left bg-white border rounded-lg overflow-hidden hover:shadow transition">
      <button
        onClick={(e) => {
          e.preventDefault();
          onHeartToggle(ev);
        }}
        className="absolute right-2 top-2 z-10 text-2xl leading-none select-none"
        title={isFav ? "Ï¶êÍ≤®Ï∞æÍ∏∞ Ìï¥Ï†ú" : "Ï¶êÍ≤®Ï∞æÍ∏∞ Ï∂îÍ∞Ä"}
        aria-label={isFav ? "Ï¶êÍ≤®Ï∞æÍ∏∞ Ìï¥Ï†ú" : "Ï¶êÍ≤®Ï∞æÍ∏∞ Ï∂îÍ∞Ä"}
      >
        {isFav ? "‚ù§Ô∏è" : "ü§ç"}
      </button>

      <a
        href={ev.homepage || "#"}
        target={ev.homepage ? "_blank" : "_self"}
        rel="noreferrer"
        className="block"
        title={ev.homepage ? "ÏÉÅÏÑ∏Î≥¥Í∏∞(ÏÉàÏ∞Ω)" : undefined}
        onClick={() => addRecent(ev)}
      >
        <img
          src={ev.thumb}
          alt={ev.title}
          className="w-full h-40 object-cover"
          loading="lazy"
        />
        <div className="p-3">
          <div className="flex items-center justify-between gap-2">
            <h3 className="font-semibold line-clamp-2">{ev.title}</h3>
            <span className="text-xs px-2 py-0.5 rounded-full bg-gray-100 whitespace-nowrap">
              {ev.category}
            </span>
          </div>
          <p className="text-sm text-gray-600 mt-1">üìÖ {ev.date}</p>
          <p className="text-sm text-gray-600">üìç {ev.place}</p>
          {ev.fee && (
            <p className="text-xs text-gray-500 mt-1">ÏöîÍ∏à: {ev.fee}</p>
          )}
        </div>
      </a>
    </div>
  );
});
