// src/pages/MyPage.jsx
import React, { useEffect, useMemo, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";

/* =========================
   LocalStorage keys
   ========================= */
const LS_FAV = "sn_favorites"; // [{id,title,place,date,category,homepage?,lat?,lng?}]
const LS_RECENT = "sn_recent";  // ÏµúÍ∑º Î≥∏ Ìï≠Î™©, ÏµúÎåÄ 10Í∞ú

const loadLS = (key, fallback = []) => {
  try { return JSON.parse(localStorage.getItem(key) || JSON.stringify(fallback)); }
  catch { return fallback; }
};
const saveLS = (key, value) => localStorage.setItem(key, JSON.stringify(value));

/** Îã§Î•∏ ÌéòÏù¥ÏßÄ(ÏÉÅÏÑ∏ Îì±)ÏóêÏÑú Ìò∏Ï∂úÌï¥ÏÑú ÏµúÍ∑º Î≥∏ Ìï≠Î™©ÏùÑ Í∏∞Î°ùÌï† Îïå Ïì∞Î©¥ Îê®
 *  addRecent({ id, title, place, date, category })
 */
export function addRecent(item) {
  const arr = loadLS(LS_RECENT, []);
  const withoutDup = arr.filter((x) => x.id !== item.id);
  const next = [item, ...withoutDup].slice(0, 10); // ÏµúÎåÄ 10Í∞ú
  saveLS(LS_RECENT, next);
  return next;
}

/* =========================
   Kakao Maps Loader + Geo Cache
   ========================= */
const FALLBACK_APPKEY = "2ee5022c1da6fc178bd51ad4042556fb";
const loadKakao = () =>
  new Promise((resolve, reject) => {
    if (window.kakao && window.kakao.maps) { resolve(window.kakao); return; }
    const key = process.env.REACT_APP_KAKAO_MAP_KEY || FALLBACK_APPKEY;
    const ID = "kakao-maps-sdk";
    const exist = document.getElementById(ID);
    const onLoaded = () => {
      try { window.kakao.maps.load(() => resolve(window.kakao)); }
      catch (e) { reject(e); }
    };
    if (exist) {
      exist.addEventListener("load", onLoaded, { once: true });
      exist.addEventListener("error", reject, { once: true });
      return;
    }
    const s = document.createElement("script");
    s.id = ID;
    s.async = true;
    s.src = `https://dapi.kakao.com/v2/maps/sdk.js?appkey=${key}&libraries=services,clusterer&autoload=false`;
    s.onload = onLoaded;
    s.onerror = reject;
    document.head.appendChild(s);
  });

const LS_GEO = "sn_geo_cache_v1";
const loadGeoCache = () => {
  try { return JSON.parse(localStorage.getItem(LS_GEO) || "{}"); } catch { return {}; }
};
const saveGeoCache = (obj) => localStorage.setItem(LS_GEO, JSON.stringify(obj));

/* =========================
   Ïª¥Ìè¨ÎÑåÌä∏
   ========================= */
export default function MyPage() {
  const navigate = useNavigate();

  // Ï¢åÏ∏°: ÏµúÍ∑º Î≥∏ Ìï≠Î™© / Ïö∞Ï∏°: Ï¶êÍ≤®Ï∞æÍ∏∞
  const [recent, setRecent]   = useState(() => loadLS(LS_RECENT, []));
  const [favs, setFavs]       = useState(() => loadLS(LS_FAV, []));
  const [favsReady, setFavsReady] = useState([]); // Ï¢åÌëú Ï±ÑÏö¥ Ï¶êÍ≤®Ï∞æÍ∏∞

  // Kakao map refs
  const mapEl = useRef(null);
  const mapRef = useRef(null);
  const kakaoRef = useRef(null);
  const clusterRef = useRef(null);
  const markersRef = useRef([]);
  const infoRef = useRef([]);

  /* Kakao ÏßÄÎèÑ Ï¥àÍ∏∞Ìôî (ÏµúÏ¥à 1Ìöå) */
  useEffect(() => {
    let disposed = false;
    (async () => {
      try {
        const kakao = await loadKakao();
        if (disposed) return;
        kakaoRef.current = kakao;

        const center = new kakao.maps.LatLng(37.5665, 126.9780); // ÏÑúÏö∏ÏãúÏ≤≠
        const map = new kakao.maps.Map(mapEl.current, { center, level: 7 });
        mapRef.current = map;
      } catch (e) {
        // ÏßÄÎèÑ Î°úÎî© Ïã§Ìå® Ïãú Ï°∞Ïö©Ìûà Î¨¥Ïãú (Ï¢åÏ∏° Í∏∞Îä•Îßå ÏÇ¨Ïö© Í∞ÄÎä•)
        console.error("Kakao map load failed:", e);
      }
    })();
    return () => { disposed = true; };
  }, []);

  /* Ï¶êÍ≤®Ï∞æÍ∏∞ Î≥ÄÍ≤Ω Ïãú Ï¢åÌëú Ï±ÑÏö∞Í∏∞ (Ï∫êÏãú ÏÇ¨Ïö©) */
  useEffect(() => {
    const kakao = kakaoRef.current;
    if (!kakao) return; // ÏßÄÎèÑ ÏïÑÏßÅÏù¥Î©¥ ÎÇòÏ§ëÏóê Îã§Ïãú Ïã§ÌñâÎê®

    const cache = loadGeoCache();
    const ps = new kakao.maps.services.Places();
    const geocoder = new kakao.maps.services.Geocoder();
    const SEOUL_CENTER = new kakao.maps.LatLng(37.5665, 126.9780);

    let cancelled = false;
    const isBadPlace = (txt = "") =>
      !String(txt).trim() || /Ïò®ÎùºÏù∏|ÎπÑÎåÄÎ©¥|Î¨¥Í¥Ä|ÎØ∏Ï†ï|ÏóÜÏùå/i.test(txt);

    const pickSeoulHit = (data, gu) => {
      const inSeoul = data.filter(d => (d.address_name || "").startsWith("ÏÑúÏö∏"));
      const inGu = gu ? inSeoul.filter(d => d.address_name.includes(gu)) : inSeoul;
      return (inGu[0] || inSeoul[0] || null);
    };

    const fill = async () => {
      const out = [];
      for (const f of favs) {
        // Ïù¥ÎØ∏ lat/lngÍ∞Ä ÏûàÏúºÎ©¥ Í∑∏ÎåÄÎ°ú ÏÇ¨Ïö©
        if (typeof f.lat === "number" && typeof f.lng === "number") {
          out.push(f); continue;
        }
        const key = `${f.place || ""}|${f.title}`;
        if (cache[key]) { out.push({ ...f, ...cache[key] }); continue; }

        let coords = null;

        // 1) PLACEÍ∞Ä Ïì∏Î™® ÏóÜÏúºÎ©¥ Ìå®Ïä§
        if (!isBadPlace(f.place)) {
          // 1-a) ÌÇ§ÏõåÎìú Í≤ÄÏÉâ (ÏÑúÏö∏ Ï§ëÏã¨ Î∞îÏù¥Ïñ¥Ïä§)
          coords = await new Promise(resolve => {
            ps.keywordSearch(
              f.place,
              (data, status) => {
                if (status === kakao.maps.services.Status.OK && data.length) {
                  const best = pickSeoulHit(data, /*gu*/ undefined);
                  if (best) return resolve({ lat: Number(best.y), lng: Number(best.x) });
                }
                resolve(null);
              },
              { location: SEOUL_CENTER, radius: 60000 }
            );
          });

          // 1-b) Ï£ºÏÜå ÏßÄÏò§ÏΩîÎî©
          if (!coords) {
            coords = await new Promise(resolve => {
              geocoder.addressSearch(f.place, (result, status) => {
                if (status === kakao.maps.services.Status.OK && result[0]) {
                  const r = result.find(r => r.address_name.startsWith("ÏÑúÏö∏")) || result[0];
                  if (r.address_name.startsWith("ÏÑúÏö∏")) {
                    return resolve({ lat: Number(r.y), lng: Number(r.x) });
                  }
                }
                resolve(null);
              });
            });
          }
        }

        if (coords) {
          cache[key] = coords;
          out.push({ ...f, ...coords });
        } else {
          // Ï¢åÌëú ÏñªÏßÄ Î™ªÌïú Ìï≠Î™©ÏùÄ ÏßÄÎèÑÏóêÏÑú Ï†úÏô∏(Î™©Î°ùÏóêÎäî ÌëúÏãúÎê®)
          out.push({ ...f, lat: null, lng: null });
        }

        await new Promise(r => setTimeout(r, 20));
        if (cancelled) return;
      }
      saveGeoCache(cache);
      if (!cancelled) setFavsReady(out);
    };

    fill();
    return () => { cancelled = true; };
  }, [favs]);

  /* ÏßÄÎèÑÏóê ÎßàÏª§/ÌÅ¥Îü¨Ïä§ÌÑ∞ ÌëúÏãú */
  useEffect(() => {
    const kakao = kakaoRef.current;
    const map = mapRef.current;
    if (!kakao || !map) return;

    // Ï†ïÎ¶¨
    markersRef.current.forEach((m) => m.setMap(null));
    infoRef.current.forEach((i) => i.close());
    markersRef.current = [];
    infoRef.current = [];
    if (clusterRef.current) clusterRef.current.clear();

    const filtered = favsReady.filter((f) => f.lat != null && f.lng != null);
    if (filtered.length === 0) return;

    const markers = [];
    const infos = [];
    const bounds = new kakao.maps.LatLngBounds();

    filtered.forEach((f) => {
      const pos = new kakao.maps.LatLng(f.lat, f.lng);
      bounds.extend(pos);

      const marker = new kakao.maps.Marker({ position: pos, title: f.title });
      // ÎßàÏö∞Ïä§ Ïò§Î≤Ñ Ìà¥ÌåÅ(InfoWindow ÏÇ¨Ïö©)
      const iwHtml = `
        <div style="padding:8px 10px; font-size:12px; max-width:220px;">
          <div style="font-weight:600; margin-bottom:4px;">${f.title}</div>
          <div style="color:#666;">${f.place || ""}</div>
          <div style="color:#888; margin-top:2px;">${f.date || ""} ¬∑ ${f.category || ""}</div>
        </div>
      `;
      const iw = new kakao.maps.InfoWindow({ content: iwHtml });

      kakao.maps.event.addListener(marker, "mouseover", () => {
        infos.forEach((i) => i.close());
        iw.open(map, marker);
      });
      kakao.maps.event.addListener(marker, "mouseout", () => {
        iw.close();
      });
      kakao.maps.event.addListener(marker, "click", () => {
        // ÏÉÅÏÑ∏ ÌéòÏù¥ÏßÄÍ∞Ä ÏûàÎã§Î©¥ Ïó∞Í≤∞
        if (f.id) navigate(`/detail/${f.id}`);
      });

      markers.push(marker);
      infos.push(iw);
    });

    markersRef.current = markers;
    infoRef.current = infos;

    const clusterer = new kakao.maps.MarkerClusterer({
      map,
      markers,
      averageCenter: true,
      minLevel: 6,
      disableClickZoom: false,
    });
    clusterRef.current = clusterer;

    map.setBounds(bounds, 40, 40, 40, 40);
  }, [favsReady, navigate]);

  /* ÏµúÍ∑º/Ï¶êÍ≤®Ï∞æÍ∏∞ Ïã§ÏãúÍ∞Ñ Î∞òÏòÅÏùÑ ÏúÑÌïú storage Ïù¥Î≤§Ìä∏ (Îã§Î•∏ ÌÉ≠ÏóêÏÑú ÏàòÏ†ï Ïãú) */
  useEffect(() => {
    const onStorage = (e) => {
      if (e.key === LS_RECENT) setRecent(loadLS(LS_RECENT, []));
      if (e.key === LS_FAV)    setFavs(loadLS(LS_FAV, []));
    };
    window.addEventListener("storage", onStorage);
    return () => window.removeEventListener("storage", onStorage);
  }, []);

  // ÏµúÍ∑º Ìï≠Î™©Ïù¥ ÏóÜÏùÑ Îïå ÏïàÎÇ¥ Íµ¨Î¨∏
  const recentEmpty = useMemo(
    () => recent.length === 0,
    [recent]
  );

  return (
    <div className="min-h-screen bg-white px-6 py-8 max-w-7xl mx-auto">
      <div className="grid grid-cols-1 lg:grid-cols-12 gap-8">
        {/* ‚ë† ÏµúÍ∑º Î≥∏ Ìï≠Î™© */}
        <section className="lg:col-span-6">
          <div className="flex items-center justify-between mb-3">
            <h2 className="text-lg font-semibold">ÏµúÍ∑º Î≥∏ Ìï≠Î™©</h2>
            {recent.length > 0 && (
              <button
                className="text-xs underline text-gray-500"
                onClick={() => { saveLS(LS_RECENT, []); setRecent([]); }}
                title="ÏµúÍ∑º Î™©Î°ù ÎπÑÏö∞Í∏∞"
              >
                ÎπÑÏö∞Í∏∞
              </button>
            )}
          </div>

          {recentEmpty ? (
            <div className="h-[420px] grid place-items-center bg-gray-100 border rounded text-gray-500">
              ÏµúÍ∑º Î≥∏ Ìï≠Î™©Ïù¥ ÏóÜÏäµÎãàÎã§.
            </div>
          ) : (
            <div className="border rounded p-3 h-[420px] overflow-auto">
              <ul className="space-y-2">
                {recent.map((it) => (
                  <li key={it.id}>
                    <button
                      onClick={() => navigate(`/detail/${it.id}`)}
                      className="w-full text-left px-3 py-2 rounded hover:bg-gray-50 border"
                      title="ÏÉÅÏÑ∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô"
                    >
                      <div className="flex items-center justify-between gap-2">
                        <div className="min-w-0">
                          <div className="font-medium truncate">{it.title}</div>
                          <div className="text-xs text-gray-600 truncate">
                            üìÖ {it.date || "ÏùºÏ†ï ÎØ∏Ï†ï"} ¬∑ üìç {it.place || "Ïû•ÏÜå ÎØ∏Ï†ï"} ¬∑ {it.category || ""}
                          </div>
                        </div>
                        <span className="text-xs text-gray-500">ÏûêÏÑ∏Ìûà ‚Üí</span>
                      </div>
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </section>

        {/* ‚ë° My Favorite (Ïã§Ï†ú Ïπ¥Ïπ¥Ïò§ ÏßÄÎèÑ + ÎßàÏª§/Ìà¥ÌåÅ) */}
        <section className="lg:col-span-6">
          <div className="flex items-center justify-between mb-3">
            <h2 className="text-lg font-semibold">My Favorite</h2>
            {favs.length > 0 && (
              <button
                className="text-xs underline text-gray-500"
                onClick={() => { saveLS(LS_FAV, []); setFavs([]); }}
                title="Ï¶êÍ≤®Ï∞æÍ∏∞ ÎπÑÏö∞Í∏∞"
              >
                Ï†ÑÏ≤¥ Ìï¥Ï†ú
              </button>
            )}
          </div>

          <div
            ref={mapEl}
            className="relative border rounded-lg bg-gray-100 h-[420px] overflow-hidden"
            aria-label="Ï¶êÍ≤®Ï∞æÍ∏∞ ÏßÄÎèÑ"
            role="region"
          >
            {!kakaoRef.current && (
              <div className="absolute inset-0 grid place-items-center text-gray-500 text-sm">
                ÏßÄÎèÑÎ•º Î∂àÎü¨Ïò§Îäî Ï§ë‚Ä¶
              </div>
            )}
            {kakaoRef.current && favsReady.filter(f => f.lat != null && f.lng != null).length === 0 && (
              <div className="absolute inset-0 grid place-items-center text-gray-500 text-sm">
                ÏßÄÎèÑÏóê ÌëúÏãúÌï† Ï¶êÍ≤®Ï∞æÍ∏∞Í∞Ä ÏóÜÏäµÎãàÎã§.
              </div>
            )}
          </div>

          {/* Ï¶êÍ≤®Ï∞æÍ∏∞ Í∞ÑÎã® Î¶¨Ïä§Ìä∏ (ÏßÄÎèÑÏùò ÏïÑÎûò) */}
          <ul className="mt-3 text-sm text-gray-700 space-y-1">
            {favs.map((f) => (
              <li key={f.id} className="flex items-center gap-2">
                <span>{/* ÌïòÌä∏ ÏÉÅÌÉúÎäî Favorites/MapÏóêÏÑú ÌÜ†Í∏ÄÎê® */}‚ù§Ô∏è</span>
                <button
                  className="truncate text-left hover:underline"
                  onClick={() => navigate(`/detail/${f.id}`)}
                  title="ÏÉÅÏÑ∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô"
                >
                  <b>{f.title}</b> ¬∑ {f.date || "ÏùºÏ†ï ÎØ∏Ï†ï"} ¬∑ {f.place || "Ïû•ÏÜå ÎØ∏Ï†ï"} ({f.category || ""})
                </button>
              </li>
            ))}
            {favs.length === 0 && (
              <li className="text-gray-500">Ï¶êÍ≤®Ï∞æÍ∏∞Ìïú ÌñâÏÇ¨Í∞Ä ÏóÜÏäµÎãàÎã§.</li>
            )}
          </ul>
        </section>
      </div>
    </div>
  );
}
