import React, { useEffect, useMemo, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { addRecent } from "./MyPage";

export default function EventDetail() {
  const { id } = useParams();
  const location = useLocation();
  const navigate = useNavigate();

  // Browse/Favorites/MyPage ÏóêÏÑú stateÎ°ú ÎÑòÏñ¥Ïò® event(ÏóÜÏùÑ ÏàòÎèÑ ÏûàÏùå)
  const evt = location.state?.event;

  // mount Ïãú 'ÏµúÍ∑º Î≥∏' Í∏∞Î°ù
  useEffect(() => {
    if (evt) addRecent(evt);
  }, [evt]);

  // (ÏòµÏÖò) idÎßå ÏûàÎäî Í≤ΩÏö∞ ÏÑúÎ≤Ñ/APIÏóêÏÑú evtÎ•º Î°úÎìúÌïòÎäî Î°úÏßÅÏùÑ Ïó¨Í∏∞Ïóê Ï∂îÍ∞Ä Í∞ÄÎä•
  // useEffect(() => { if (!evt) fetchEventById(id).then(addRecent) }, [id, evt]);

  return (
    <div className="min-h-screen bg-white px-6 py-8 max-w-3xl mx-auto">
      <button
        onClick={() => navigate(-1)}
        className="px-3 py-1.5 text-sm rounded-lg border hover:bg-gray-50 mb-4"
      >
        ‚Üê Îí§Î°ú
      </button>

      <h2 className="text-2xl font-semibold mb-2">{evt?.title || `ÌñâÏÇ¨ #${id}`}</h2>
      <div className="text-sm text-gray-600 mb-4">
        üìÖ {evt?.date || "-"} ¬∑ üìç {evt?.place || "-"} {evt?.gu ? `(${evt.gu})` : ""}
      </div>

      {evt?.thumb ? (
        <img src={evt.thumb} alt={evt.title} className="w-full h-64 object-cover rounded-xl mb-4" />
      ) : (
        <div className="w-full h-64 bg-gray-100 rounded-xl mb-4" />
      )}

      <div className="prose max-w-none">
        {/* ÏÉÅÏÑ∏ ÎÇ¥Ïö©ÏùÄ Ïã§Îç∞Ïù¥ÌÑ∞Ïóê ÎßûÍ≤å */}
        <p>Ïπ¥ÌÖåÍ≥†Î¶¨: {evt?.category || "-"}</p>
        {evt?.homepage && (
          <p>
            ÌôàÌéòÏù¥ÏßÄ:{" "}
            <a className="text-blue-600 underline" href={evt.homepage} target="_blank" rel="noreferrer">
              Î∞îÎ°úÍ∞ÄÍ∏∞
            </a>
          </p>
        )}
        {/* ÌïÑÏöî Ïãú Î¶¨Î∑∞ ÏÑπÏÖò/ÏóêÎîîÌÑ∞ Ïú†ÏßÄ Í∞ÄÎä• */}
      </div>
    </div>
  );
}
