import React, { useMemo, useState } from "react";

const categories = [
  { id: 1, name: "Í≥µÏó∞", img: "/images/concert.jpg" },
  { id: 2, name: "Ï†ÑÏãú", img: "/images/exhibition.jpg" },
  { id: 3, name: "ÍµêÏú°/Ï≤¥Ìóò", img: "/images/edu.jpg" },
  { id: 4, name: "Í∏∞ÌÉÄ", img: "/images/etc.jpg" },
];

// Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ ÏÉâÏÉÅ Îß§Ìïë
const CATEGORY_COLORS = {
  Í≥µÏó∞: "bg-indigo-500",
  Ï†ÑÏãú: "bg-emerald-500",
  "ÍµêÏú°/Ï≤¥Ìóò": "bg-amber-500",
  Í∏∞ÌÉÄ: "bg-rose-500",
};

// Îç∞Î™®Ïö© Ïù¥Î≤§Ìä∏ (Ïã§Ï†úÏóêÏÑ† API Îç∞Ïù¥ÌÑ∞Î°ú ÍµêÏ≤¥)
// dateÎäî 'YYYY-MM-DD' ÌòïÏãù
const demoEvents = [
  { title: "ÏÑúÏö∏Ïû¨Ï¶àÌéòÏä§Ìã∞Î≤å 2025", date: "2025-06-02", category: "Í≥µÏó∞" },
  { title: "ÏãúÎ¶ΩÎØ∏Ïà†Í¥Ä Í∏∞ÌöçÏ†Ñ", date: "2025-06-10", category: "Ï†ÑÏãú" },
  { title: "ÌïúÍ∞ï ÎèóÏûêÎ¶¨ Ï≤¥Ìóò ÌÅ¥ÎûòÏä§", date: "2025-06-15", category: "ÍµêÏú°/Ï≤¥Ìóò" },
  { title: "Ï≤≠ÎÖÑ Î¨∏ÌôîÎßàÏºì", date: "2025-06-22", category: "Í∏∞ÌÉÄ" },
];

// ÎÇ†Ïßú Ïú†Ìã∏
function formatDateKey(date) {
  const y = date.getFullYear();
  const m = String(date.getMonth() + 1).padStart(2, "0");
  const d = String(date.getDate()).padStart(2, "0");
  return `${y}-${m}-${d}`;
}

function getMonthMatrix(year, monthIndex, weekStartsOn = 0) {
  // weekStartsOn: 0=Sun, 1=Mon
  const first = new Date(year, monthIndex, 1);
  const last = new Date(year, monthIndex + 1, 0);

  const daysInMonth = last.getDate();
  const firstDay = (first.getDay() - weekStartsOn + 7) % 7;

  const totalCells = Math.ceil((firstDay + daysInMonth) / 7) * 7;
  const cells = [];
  for (let i = 0; i < totalCells; i++) {
    const dayOffset = i - firstDay + 1;
    const d = new Date(year, monthIndex, dayOffset);
    const inCurrentMonth = dayOffset >= 1 && dayOffset <= daysInMonth;
    cells.push({ date: d, inCurrentMonth });
  }
  return cells;
}

function Calendar({ events = [], weekStartsOn = 0, title = "Ï∫òÎ¶∞Îçî" }) {
  const [cursor, setCursor] = useState(() => {
    // Îç∞Î™® Ïù¥Î≤§Ìä∏Ïóê ÎßûÏ∂∞ 2025-06ÏúºÎ°ú ÏãúÏûë (ÏõêÌïòÎ©¥ new Date()Î°ú Î∞îÍøîÎèÑ Îê®)
    return new Date(2025, 5, 1); // 2025ÎÖÑ 6Ïõî (0-indexed)
  });

  const y = cursor.getFullYear();
  const m = cursor.getMonth();

  const matrix = useMemo(() => getMonthMatrix(y, m, weekStartsOn), [y, m, weekStartsOn]);

  const eventsByDay = useMemo(() => {
    const map = {};
    events.forEach((e) => {
      (map[e.date] ||= []).push(e);
    });
    return map;
  }, [events]);

  const weekLabels = weekStartsOn === 1
    ? ["Ïõî", "Ìôî", "Ïàò", "Î™©", "Í∏à", "ÌÜ†", "Ïùº"]
    : ["Ïùº", "Ïõî", "Ìôî", "Ïàò", "Î™©", "Í∏à", "ÌÜ†"];

  return (
    <div className="bg-white shadow-md rounded-lg p-4 hover:shadow-lg transition">
      <div className="flex items-center justify-between mb-3">
        <h3 className="font-semibold text-lg">{title}</h3>
        <div className="flex items-center gap-2">
          <button
            onClick={() =>
              setCursor(new Date(y, m - 1, 1))
            }
            className="px-2 py-1 rounded border text-sm hover:bg-gray-50"
            aria-label="Ïù¥Ï†Ñ Îã¨"
          >
            ‚Üê
          </button>
          <div className="text-sm font-medium">
            {y}.{String(m + 1).padStart(2, "0")}
          </div>
          <button
            onClick={() =>
              setCursor(new Date(y, m + 1, 1))
            }
            className="px-2 py-1 rounded border text-sm hover:bg-gray-50"
            aria-label="Îã§Ïùå Îã¨"
          >
            ‚Üí
          </button>
        </div>
      </div>

      {/* ÏöîÏùº Ìó§Îçî */}
      <div className="grid grid-cols-7 text-center text-xs text-gray-600 mb-1">
        {weekLabels.map((w) => (
          <div key={w} className="py-1">{w}</div>
        ))}
      </div>

      {/* ÎÇ†Ïßú Í∑∏Î¶¨Îìú */}
      <div className="grid grid-cols-7 gap-1">
        {matrix.map(({ date, inCurrentMonth }, idx) => {
          const key = formatDateKey(date);
          const day = date.getDate();
          const todaysEvents = inCurrentMonth ? (eventsByDay[key] || []) : [];
          return (
            <div
              key={idx}
              className={[
                "aspect-square rounded-md border p-1 flex flex-col",
                inCurrentMonth ? "bg-white" : "bg-gray-50 text-gray-400",
              ].join(" ")}
            >
              {/* ÎÇ†Ïßú Ïà´Ïûê */}
              <div className="text-right text-xs">
                {day}
              </div>
              {/* Ïù¥Î≤§Ìä∏ Ï†êÎì§ (Ïπ¥ÌÖåÍ≥†Î¶¨ ÏÉâÏÉÅ) */}
              <div className="mt-auto flex flex-wrap gap-1">
                {todaysEvents.slice(0, 4).map((ev, i) => (
                  <span
                    key={i}
                    className={[
                      "inline-block w-2 h-2 rounded-full",
                      CATEGORY_COLORS[ev.category] || "bg-gray-400",
                    ].join(" ")}
                    title={`${ev.title} ¬∑ ${ev.category}`}
                  />
                ))}
                {todaysEvents.length > 4 && (
                  <span className="text-[10px] text-gray-500">+{todaysEvents.length - 4}</span>
                )}
              </div>
            </div>
          );
        })}
      </div>

      {/* Î≤îÎ°Ä */}
      <div className="mt-3 grid grid-cols-2 gap-x-4 gap-y-1 text-xs">
        {Object.entries(CATEGORY_COLORS).map(([cat, color]) => (
