// src/pages/BrowseEvents.jsx
import React, {
  useEffect,
  useMemo,
  useState,
  useDeferredValue,
} from "react";
import { useSearchParams } from "react-router-dom";
import { fetchSeoulRecommendedEvents } from "../lib/seoulApi";
import { addRecent } from "./MyPage";

/* === .env í‚¤ === */
const SEOUL_KEY = (process.env.REACT_APP_SEOUL_KEY || "").trim();

/* === ì¹´í…Œê³ ë¦¬ === */
const CATEGORIES = ["ì „ì²´", "ê³µì—°", "ì „ì‹œ", "êµìœ¡/ì²´í—˜", "ê¸°íƒ€"];

/* === ìƒìœ„ ì¹´í…Œê³ ë¦¬ ë§¤í•‘ (App.js ì™€ ë™ì¼ ê·œì¹™) === */
function toHighLevelCategory(codename = "", themecode = "") {
  const c = String(codename || "");
  const t = String(themecode || "");

  // ê³µì—°
  if (
    [
      "ê³µì—°",
      "ì½˜ì„œíŠ¸",
      "í´ë˜ì‹",
      "êµ­ì•…",
      "ë¬´ìš©",
      "ì—°ê·¹",
      "ë®¤ì§€ì»¬",
      "ì˜¤í˜ë¼",
      "ìŒì•…íšŒ",
      "í˜ìŠ¤í‹°ë²Œ",
      "ì¶•ì œ",
    ].some((k) => c.includes(k))
  ) {
    return "ê³µì—°";
  }

  // ì „ì‹œ
  if (["ì „ì‹œ", "ë¯¸ìˆ ", "ê°¤ëŸ¬ë¦¬", "ì•„íŠ¸", "ì‚¬ì§„ì „"].some((k) => c.includes(k))) {
    return "ì „ì‹œ";
  }

  // êµìœ¡/ì²´í—˜
  if (
    ["êµìœ¡", "ì²´í—˜", "ì›Œí¬ìˆ", "ì›Œí¬ìƒµ", "ê°•ì¢Œ", "ê°•ì˜", "ì„¸ë¯¸ë‚˜", "ê°•ì—°"].some(
      (k) => c.includes(k)
    ) ||
    t.includes("êµìœ¡")
  ) {
    return "êµìœ¡/ì²´í—˜";
  }

  return "ê¸°íƒ€";
}

/* === ë‚ ì§œ í‘œê¸° ìœ í‹¸ === */
function normalizeDateRange(startStr = "", endStr = "") {
  const s = (startStr || "").replaceAll(".", "-");
  const e = (endStr || "").replaceAll(".", "-");
  if (!s && !e) return "ì¼ì • ë¯¸ì •";
  if (s && e) return `${s} ~ ${e}`;
  return s || e;
}

/* === ì¦ê²¨ì°¾ê¸° ë¡œì»¬ìŠ¤í† ë¦¬ì§€ === */
const LS_KEY_FAV = "sn_favorites";
function loadFavs() {
  try {
    const raw = localStorage.getItem(LS_KEY_FAV);
    return raw ? JSON.parse(raw) : [];
  } catch {
    return [];
  }
}
function saveFavs(list) {
  localStorage.setItem(LS_KEY_FAV, JSON.stringify(list));
}

/* =========================
   ì„œìš¸ì‹œ API í˜ì´ì§€ ë‹¨ìœ„ ë¡œë”© (ë¬´í•œ ë¡œë”)
========================= */
const API_PER_PAGE = 64; // ì„œìš¸ì‹œ ì˜¤í”ˆAPIì—ì„œ í•œ ë²ˆì— ê°€ì ¸ì˜¤ëŠ” ê°œìˆ˜
const UI_PAGE_SIZE = 32; // í™”ë©´ì—ì„œ í•œ í˜ì´ì§€ì— ë³´ì—¬ì¤„ ì¹´ë“œ ê°œìˆ˜

function mapRowToEvent(r, globalIndex) {
  const id = r.SVCID || `evt_${globalIndex}`;
  const title = r.TITLE || r.SVCNM || "ë¬´ì œ";
  const codename = r.CODENAME;
  const themecode = r.THEMECODE;
  const gu = r.GUNAME;
  const place = r.PLACE;
  const start = r.STRTDATE || r.DATE;
  const end = r.END_DATE || r.ENDDATE || r.END;
  const homepage = r.ORG_LINK || r.HMPG_ADDR;
  const img = r.MAIN_IMG;
  const fee = r.USE_FEE;
  const category = toHighLevelCategory(codename, themecode);

  return {
    id,
    title,
    category,
    date: normalizeDateRange(start, end),
    place: place || gu || "ì¥ì†Œ ë¯¸ìƒ",
    thumb: img || "/images/sample-event.jpg",
    homepage,
    gu,
    fee,
  };
}

/**
 * API í˜ì´ì§€ë“¤ì„ í•„ìš”í•  ë•Œë§ˆë‹¤ ì¡°ê¸ˆì”© ë¶ˆëŸ¬ì™€ì„œ
 * allEvents ë°°ì—´ë¡œ ëˆ„ì í•´ ì£¼ëŠ” í›….
 */
function useSeoulEventsLoader() {
  const [pages, setPages] = useState(() => ({})); // { [apiPage]: Event[] }
  const [apiPage, setApiPage] = useState(1); // í˜„ì¬ê¹Œì§€ ìš”ì²­í•œ ë§ˆì§€ë§‰ API í˜ì´ì§€ ë²ˆí˜¸
  const [totalCount, setTotalCount] = useState(0);
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState(null);

  useEffect(() => {
    const page = apiPage;
    const ctrl = new AbortController();
    let cancelled = false;

    async function run() {
      if (!SEOUL_KEY) {
        setErr(
          new Error(
            "í™˜ê²½ë³€ìˆ˜ REACT_APP_SEOUL_KEY ê°€ ì„¤ì •ë˜ì§€ ì•Šì•˜ìŠµë‹ˆë‹¤ (.env í™•ì¸)."
          )
        );
        return;
      }

      if (pages[page]) return; // ì´ë¯¸ ë¶ˆëŸ¬ì˜¨ í˜ì´ì§€ë©´ ì¬í˜¸ì¶œ X

      setLoading(true);
      setErr(null);
      try {
        const start1 = (page - 1) * API_PER_PAGE + 1;
        const end1 = start1 + API_PER_PAGE - 1;

        const json = await fetchSeoulRecommendedEvents({
          seoulKey: SEOUL_KEY,
          startIndex: start1,
          endIndex: end1,
          signal: ctrl.signal,
        });

        if (cancelled) return;

        const info = json?.culturalEventInfo;
        const rows = info?.row || [];
        const total = info?.list_total_count ?? 0;

        const mapped = rows.map((r, idx) =>
          mapRowToEvent(r, start1 - 1 + idx)
        );

        setPages((prev) => ({ ...prev, [page]: mapped }));
        setTotalCount(total);
      } catch (e) {
        if (!cancelled) setErr(e);
      } finally {
        if (!cancelled) setLoading(false);
      }
    }

    run();

    return () => {
      cancelled = true;
      ctrl.abort();
    };
  }, [apiPage, pages]);

  // ì§€ê¸ˆê¹Œì§€ ë¶ˆëŸ¬ì˜¨ ëª¨ë“  API í˜ì´ì§€ë¥¼ í•©ì¹œ ë°°ì—´
  const allEvents = useMemo(() => {
    const arr = [];
    const maxPage = Math.max(
      0,
      ...Object.keys(pages).map((k) => Number(k) || 0)
    );
    for (let p = 1; p <= maxPage; p++) {
      if (pages[p]) arr.push(...pages[p]);
    }
    return arr;
  }, [pages]);

  const totalApiPages = totalCount
    ? Math.max(1, Math.ceil(totalCount / API_PER_PAGE))
    : null;

  const hasMore =
    totalApiPages == null ? true : apiPage < totalApiPages;

  const ensureMore = () => {
    if (!hasMore) return;
    setApiPage((p) => p + 1);
  };

  return {
    allEvents,
    totalCount,
    loading,
    error: err,
    hasMore,
    ensureMore, // ë” í•„ìš”í•œ ê²½ìš°(ë‹¤ìŒ UI í˜ì´ì§€ë¡œ ê°ˆ ë•Œ) ë‚´ë¶€ì ìœ¼ë¡œë§Œ í˜¸ì¶œ
  };
}

/* =========================
   í˜ì´ì§€ ì»´í¬ë„ŒíŠ¸
========================= */
export default function BrowseEvents() {
  const [sp, setSp] = useSearchParams();

  // --- URL ì¿¼ë¦¬ì—ì„œ ì´ˆê¸°ê°’ ì¶”ì¶œ ---
  const categoryParam = sp.get("category");
  const qParam = sp.get("q") || "";

  const [category, setCategory] = useState(
    categoryParam && CATEGORIES.includes(categoryParam)
      ? categoryParam
      : "ì „ì²´"
  );
  const [input, setInput] = useState(qParam); // ì…ë ¥ì°½ ê°’
  const [query, setQuery] = useState(qParam); // ì‹¤ì œ ê²€ìƒ‰ì— ì“°ëŠ” ê°’

  // âœ… URL(searchParams)ì´ ë°”ë€” ë•Œë§ˆë‹¤ ìƒíƒœ ì¬ë™ê¸°í™”
  // (HOME/í—¤ë” ê²€ìƒ‰ì—ì„œ /browse?q=... ë¡œ ë“¤ì–´ì˜¤ëŠ” ê²½ìš° í¬í•¨)
  useEffect(() => {
    const urlCategory = sp.get("category");
    const urlQ = sp.get("q") || "";

    const nextCategory =
      urlCategory && CATEGORIES.includes(urlCategory)
        ? urlCategory
        : "ì „ì²´";

    setCategory(nextCategory);
    setInput(urlQ);
    setQuery(urlQ);
  }, [sp]);

  // ê²€ìƒ‰ ì…ë ¥ ë””ë°”ìš´ìŠ¤ â†’ queryë¡œ ë°˜ì˜
  useEffect(() => {
    const t = setTimeout(() => setQuery(input), 300);
    return () => clearTimeout(t);
  }, [input]);

  // URL ìœ ì§€ (ì¹´í…Œê³ ë¦¬ / ê²€ìƒ‰ì–´ -> ì¿¼ë¦¬ìŠ¤íŠ¸ë§ ë°˜ì˜)
  useEffect(() => {
    const next = new URLSearchParams(sp);
    if (category && category !== "ì „ì²´") next.set("category", category);
    else next.delete("category");

    if (query) next.set("q", query);
    else next.delete("q");

    setSp(next, { replace: true });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [category, query]);

  // ì „ì²´ ë°ì´í„° ë¡œë” (ë°±ê·¸ë¼ìš´ë“œë¡œ API ë‹¤ì¤‘í˜ì´ì§€ ë¡œë”©)
  const { allEvents, totalCount, loading, error, hasMore, ensureMore } =
    useSeoulEventsLoader();

  const deferredQuery = useDeferredValue(query);

  // ì¹´í…Œê³ ë¦¬ + ê²€ìƒ‰ í•„í„° (í˜„ì¬ê¹Œì§€ ë¡œë”©ëœ ì „ì²´ ë°ì´í„°ì—ì„œ ìˆ˜í–‰)
  const filtered = useMemo(() => {
    const pool =
      category === "ì „ì²´"
        ? allEvents
        : allEvents.filter((e) => e.category === category);

    const q = (deferredQuery || "").trim().toLowerCase();
    if (!q) return pool;

    return pool.filter(
      (e) =>
        e.title.toLowerCase().includes(q) ||
        (e.place || "").toLowerCase().includes(q)
    );
  }, [allEvents, category, deferredQuery]);

  // ğŸ“„ UIìš© í˜ì´ì§€ë„¤ì´ì…˜ (í´ë¼ì´ì–¸íŠ¸ì—ì„œ ì˜ë¼ ì“°ê¸°)
  const [uiPage, setUiPage] = useState(1);
  const uiTotalPages = Math.max(
    1,
    Math.ceil(filtered.length / UI_PAGE_SIZE) || 1
  );

  // ì¹´í…Œê³ ë¦¬/ê²€ìƒ‰ì´ ë°”ë€Œë©´ 1í˜ì´ì§€ë¡œ ì´ë™
  useEffect(() => {
    setUiPage(1);
  }, [category, query]);

  // ì‚¬ìš©ìê°€ ë‹¤ìŒ/íŠ¹ì • í˜ì´ì§€ë¡œ ì´ë™í•  ë•Œ,
  // í•„ìš”í•œ ë°ì´í„°ê°€ ë¶€ì¡±í•˜ë©´ ìë™ìœ¼ë¡œ ì¶”ê°€ ë¡œë“œ ìš”ì²­
  const goToPage = (nextPage) => {
    if (nextPage < 1 || nextPage > uiTotalPages) return;

    const neededCount = nextPage * UI_PAGE_SIZE;
    if (neededCount > allEvents.length && hasMore && !loading) {
      ensureMore();
    }
    setUiPage(nextPage);
  };

  const uiStart = (uiPage - 1) * UI_PAGE_SIZE;
  const uiEnd = uiStart + UI_PAGE_SIZE;
  const visibleEvents = filtered.slice(uiStart, uiEnd);

  const initialLoading = loading && allEvents.length === 0;

  const onSubmit = (e) => {
    e.preventDefault();
    setQuery(input);
  };

  // ì¦ê²¨ì°¾ê¸°
  const [favSet, setFavSet] = useState(
    () => new Set(loadFavs().map((x) => x.id))
  );

  const onHeartToggle = (ev) => {
    const list = loadFavs();
    const exists = list.some((x) => x.id === ev.id);
    let next;
    if (exists) {
      next = list.filter((x) => x.id !== ev.id);
    } else {
      next = [
        ...list,
        {
          id: ev.id,
          title: ev.title,
          category: ev.category,
          date: ev.date,
          place: ev.place,
          thumb: ev.thumb,
          homepage: ev.homepage,
          gu: ev.gu,
          lat: ev.lat ?? null,
          lng: ev.lng ?? null,
        },
      ];
    }
    saveFavs(next);
    setFavSet(new Set(next.map((x) => x.id)));
  };

  const isFiltering =
    category !== "ì „ì²´" || (deferredQuery || "").trim().length > 0;

  // í˜ì´ì§€ ë²ˆí˜¸ ëª©ë¡(í˜„ì¬ í˜ì´ì§€ ê¸°ì¤€ìœ¼ë¡œ ì£¼ë³€ë§Œ í‘œì‹œ)
  const buildPageList = () => {
    const pages = [];
    const maxButtons = 7;
    let start = Math.max(1, uiPage - 3);
    let end = Math.min(uiTotalPages, start + maxButtons - 1);
    if (end - start + 1 < maxButtons) {
      start = Math.max(1, end - maxButtons + 1);
    }
    for (let p = start; p <= end; p++) {
      pages.push(p);
    }
    return pages;
  };

  const pageList = buildPageList();

  return (
    <div className="min-h-screen bg-white">
      <div className="max-w-6xl mx-auto px-4 md:px-6">
        {/* ===== ìƒë‹¨ ì»¨íŠ¸ë¡¤ ë°” (sticky) ===== */}
        <div
          className="
            sticky 
            top-[48px]   /* í—¤ë” ë†’ì´ì— ë§ê²Œ ì¡°ì • */
            z-20
            bg-white
            pt-4 pb-3
          "
        >
          <div className="flex flex-col md:flex-row md:items-center gap-3 md:gap-4 border-b pb-3">
            <div className="w-full md:w-56">
              <label htmlFor="category" className="sr-only">
                ì¹´í…Œê³ ë¦¬
              </label>
              <select
                id="category"
                value={category}
                onChange={(e) => setCategory(e.target.value)}
                className="w-full border rounded-md px-3 py-2 bg-white"
              >
                {CATEGORIES.map((c) => (
                  <option key={c} value={c}>
                    {c}
                  </option>
                ))}
              </select>
            </div>

            <form onSubmit={onSubmit} className="flex-1">
              <div className="relative">
                <input
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  placeholder="í–‰ì‚¬ëª…, ì¥ì†Œë¡œ ê²€ìƒ‰"
                  className="w-full border rounded-md pl-3 pr-10 py-2"
                />
                <button
                  type="submit"
                  aria-label="ê²€ìƒ‰"
                  className="absolute right-2 top-1/2 -translate-y-1/2 p-1 rounded hover:bg-gray-50"
                >
                  <img src="/images/search.png" alt="" className="w-4 h-4" />
                </button>
              </div>
            </form>
          </div>
        </div>

        {/* ===== ë³¸ë¬¸ ë¦¬ìŠ¤íŠ¸ ===== */}
        <div className="mt-4">
          <div className="border rounded-lg p-4">
            {initialLoading && (
              <div className="h-56 grid place-items-center text-gray-500">
                ë¶ˆëŸ¬ì˜¤ëŠ” ì¤‘â€¦
              </div>
            )}

            {error && !initialLoading && (
              <div className="h-56 grid place-items-center text-red-600">
                ë°ì´í„°ë¥¼ ë¶ˆëŸ¬ì˜¤ì§€ ëª»í–ˆì–´ìš”. {String(error.message || error)}
              </div>
